tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",5,1,"後來摩西亞倫去對法老說、耶和華以色列的　神這樣說、容我的百姓去、在曠野向我守節。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",5,2,"法老說、耶和華是誰、使我聽他的話、容以色列人去呢、我不認識耶和華、也不容以色列人去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",5,3,"他們說、希伯來人的　神遇見了我們、求你容我們往曠野去、走三天的路程、祭祀耶和華我們的　神、免得他用瘟疫、刀兵、攻擊我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",5,4,"埃及王對他們說、摩西亞倫你們為甚麼叫百姓曠工呢．你們去擔你們的擔子罷。")');
