tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",5,14,"法老督工的責打他所派以色列人的官長說、你們昨天今天為甚麼沒有照向來的數目作磚、完你們的工作呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",5,15,"以色列人的官長就來哀求法老說、為甚麼這樣待你的僕人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",5,16,"督工的不把草給僕人、並且對我們說、作磚罷．看哪、你僕人挨了打、其實是你百姓的錯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",5,17,"但法老說、你們是懶惰的、你們是懶惰的、所以說、容我們去祭祀耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",5,18,"現在你們去作工罷、草是不給你們的、磚卻要如數交納。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",5,19,"以色列人的官長聽說、你們每天作磚的工作一點不可減少、就知道是遭遇禍患了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",5,20,"他們離了法老出來、正遇見摩西亞倫站在對面、")');
