tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",5,22,"摩西回到耶和華那裡說、主阿、你為甚麼苦待這百姓呢、為甚麼打發我去呢、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",5,23,"自從我去見法老奉你的名說話、他就苦待這百姓、你一點也沒有拯救他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,1,"耶和華對摩西說、現在你必看見我向法老所行的事、使他因我大能的手容以色列人去、且把他們趕出他的地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,2,"　神曉諭摩西說、我是耶和華．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,3,"我從前向亞伯拉罕、以撒、雅各、顯現為全能的　神、至於我名耶和華、他們未曾知道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,4,"我與他們堅定所立的約、要把他們寄居的迦南地賜給他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,5,"我也聽見以色列人被埃及人苦待的哀聲、我也記念我的約。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,6,"所以你要對以色列人說、我是耶和華、我要用伸出來的膀臂重重地刑罰埃及人、救贖你們脫離他們的重擔、不作他們的苦工。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,7,"我要以你們為我的百姓．我也要作你們的　神、你們要知道我是耶和華你們的　神、是救你們脫離埃及人之重擔的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,8,"我起誓應許給亞伯拉罕、以撒、雅各的那地、我要把你們領進去將那地賜給你們為業、我是耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,9,"摩西將這話告訴以色列人、只是他們因苦工愁煩、不肯聽他的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,10,"耶和華曉諭摩西說、")');
