tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,13,"耶和華吩咐摩西亞倫往以色列人和埃及王法老那裡去、把以色列人從埃及地領出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,14,"以色列人家長的名字記在下面．以色列長子流便的兒子是哈諾、法路、希斯崙、迦米．這是流便的各家。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,15,"西緬的兒子是耶母利、雅憫、阿轄、雅斤、瑣轄、和迦南女子的兒子掃羅．這是西緬的各家。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,16,"利未眾子的名字、按著他們的後代記在下面．就是革順、哥轄、米拉利．利未一生的歲數是一百三十七歲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,17,"革順的兒子按著家室、是立尼、示每。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,18,"哥轄的兒子是暗蘭、以斯哈、希伯倫、烏薛．哥轄一生的歲數是一百三十三歲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,19,"米拉利的兒子是抹利、和母示．這是利未的家、都按著他們的後代。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,20,"暗蘭娶了他父親的妹妹約基別為妻、他給他生了亞倫和摩西．暗蘭一生的歲數是一百三十七歲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,21,"以斯哈的兒子是可拉、尼斐、細基利。")');
