tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,23,"亞倫娶了亞米拿達的女兒拿順的妹妹以利沙巴為妻、他給他生了拿答、亞比戶、以利亞撒、以他瑪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,24,"可拉的兒子是亞惜、以利加拿、亞比亞撒．這是可拉的各家。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,25,"亞倫的兒子以利亞撒、娶了普鐵的一個女兒為妻、他給他生了非尼哈．這是利未人的家長、都按著他們的家。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,26,"耶和華說、將以色列人按著他們的軍隊從埃及地領出來、這是對那亞倫摩西說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,27,"對埃及王法老說、要將以色列人從埃及領出來的、就是這摩西亞倫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,28,"當耶和華在埃及地對摩西說話的日子、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,29,"他向摩西說、我是耶和華、我對你說的一切話、你都要告訴埃及王法老。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",6,30,"摩西在耶和華面前說、看哪、我是拙口笨舌的人、法老怎肯聽我呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",7,1,"耶和華對摩西說、我使你在法老面前代替　神、你的哥哥亞倫是替你說話的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",7,2,"凡我所吩咐你的、你都要說、你的哥哥亞倫要對法老說、容以色列人出他的地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",7,3,"我要使法老的心剛硬、也要在埃及地多行神蹟奇事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",7,4,"但法老必不聽你們、我要伸手重重地刑罰埃及、將我的軍隊以色列民從埃及地領出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",7,5,"我伸手攻擊埃及、將以色列人從他們中間領出來的時候、埃及人就要知道我是耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",7,6,"摩西亞倫這樣行、耶和華怎樣吩咐他們、他們就照樣行了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",7,7,"摩西亞倫與法老說話的時候、摩西八十歲、亞倫八十三歲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",7,8,"耶和華曉諭摩西亞倫說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",7,9,"法老若對你們說、你們行件奇事罷、你就吩咐亞倫說、把杖丟在法老面前、使杖變作蛇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",7,10,"摩西亞倫進去見法老、就照耶和華所吩咐的行、亞倫把杖丟在法老和臣僕面前、杖就變作蛇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",7,11,"於是法老召了博士和術士來、他們是埃及行法術的、也用邪術照樣而行。")');
