tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",7,25,"耶和華擊打河以後滿了七天。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",8,1,"耶和華吩咐摩西說、你進去見法老、對他說、耶和華這樣說、容我的百姓去、好事奉我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",8,2,"你若不肯容他們去、我必使青蛙糟蹋你的四境。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",8,3,"河裡要滋生青蛙、這青蛙要上來進你的宮殿、和你的臥房、上你的床榻、進你臣僕的房屋、上你百姓的身上、進你的爐、和你的摶麵盆．")');
