tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",8,5,"耶和華曉諭摩西說、你對亞倫說、把你的杖伸在江、河、池以上、使青蛙到埃及地上來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",8,6,"亞倫便伸杖在埃及的諸水以上、青蛙就上來遮滿了埃及地．")');
