tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",8,8,"法老召了摩西亞倫來說、請你們求耶和華使這青蛙離開我和我的民、我就容百姓去祭祀耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",8,9,"摩西對法老說、任憑你罷、我要何時為你和你的臣僕、並你的百姓、祈求除滅青蛙離開你和你的宮殿、只留在河裡呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",8,10,"他說、明天、摩西說、可以照你的話罷、好叫你知道沒有像耶和華我們　神的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",8,11,"青蛙要離開你和你的宮殿、並你的臣僕與你的百姓、只留在河裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",8,12,"於是摩西亞倫離開法老出去．摩西為擾害法老的青蛙呼求耶和華。")');
