tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,1,"你在百姓面前所要立的典章是這樣．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,2,"你若買希伯來人作奴僕、他必服事你六年、第七年他可以自由、白白地出去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,3,"他若孤身來、就可以孤身去．他若有妻、他的妻就可以同他出去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,4,"他主人若給他妻子、妻子給他生了兒子、或女兒、妻子和兒女要歸主人、他要獨自出去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,5,"倘或奴僕明說、我愛我的主人、和我的妻子兒女、不願意自由出去．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,6,"他的主人就要帶他到審判官那裡、〔審判官或作　神下同〕又要帶他到門前、靠近門框、用錐子穿他的耳朵、他就永遠服事主人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,7,"人若賣女兒作婢女、婢女不可像男僕那樣出去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,8,"主人選定他歸自己、若不喜歡他、就要許他贖身．主人既然用詭詐待他、就沒有權柄賣給外邦人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,9,"主人若選定他給自己的兒子、就當待他如同女兒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,10,"若另娶一個、那女子的喫食、衣服、並好合的事、仍不可減少。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,11,"若不向他行這三樣、他就可以不用錢贖、白白的出去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,12,"打人以致打死的、必要把他治死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,13,"人若不是埋伏著殺人、乃是　神交在他手中、我就設下一個地方、他可以往那裡逃跑。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,14,"人若任意用詭計殺了他的鄰舍、就是逃到我的壇那裡、也當捉去把他治死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,15,"打父母的、必要把他治死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,16,"拐帶人口、或是把人賣了、或是留在他手下、必要把他治死。")');
