tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,18,"人若彼此相爭、這個用石頭或是拳頭打那個、尚且不至於死、不過躺臥在床、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,19,"若再能起來扶杖而出、那打他的可算無罪、但要將他耽誤的工夫用錢賠補、並要將他全然醫好。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,20,"人若用棍子打奴僕或婢女、立時死在他的手下、他必要受刑。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,21,"若過一兩天纔死、就可以不受刑、因為是用錢買的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,22,"人若彼此爭鬥、傷害有孕的婦人、甚至墜胎、隨後卻無別害、那傷害他的總要按婦人的丈夫所要的、照審判官所斷的受罰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,23,"若有別害、就要以命償命、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,24,"以眼還眼、以牙還牙、以手還手、以腳還腳、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,25,"以烙還烙、以傷還傷、以打還打。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,26,"人若打壞了他奴僕或是婢女的一隻眼、就要因他的眼放他去得以自由。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,27,"若打掉了他奴僕或是婢女的一個牙、就要因他的牙放他去得以自由。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,28,"牛若觸死男人、或是女人、總要用石頭打死那牛、卻不可喫他的肉．牛的主人可算無罪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,29,"倘若那牛素來是觸人的、有人報告了牛主、他竟不把牛拴著、以致把男人或是女人觸死、就要用石頭打死那牛、牛主也必治死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,30,"若罰他贖命的價銀、他必照所罰的、贖他的命。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,31,"牛無論觸了人的兒子、或是女兒、必照這例辦理。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,32,"牛若觸了奴僕、或是婢女、必將銀子三十舍客勒給他們的主人、也要用石頭把牛打死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,33,"人若敞著井口、或挖井不遮蓋、有牛、或驢掉在裡頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,34,"井主要拿錢賠還本主人．死牲畜要歸自己。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,35,"這人的牛若傷了那人的牛、以致於死、他們要賣了活牛、平分價值、也要平分死牛。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",21,36,"人若知道這牛素來是觸人的、主人竟不把牛拴著、他必要以牛還牛、死牛要歸自己。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,1,"人若偷牛、或羊、無論是宰了、是賣了、他就要以五牛賠一牛、四羊賠一羊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,2,"人若遇見賊挖窟窿、把賊打了、以致於死、就不能為他有流血的罪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,3,"若太陽已經出來、就為他有流血的罪．賊若被拿、總要賠還、若他一無所有、就要被賣、頂他所偷的物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,4,"若他所偷的、或牛、或驢、或羊、仍在他手下存活、他就要加倍賠還。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,5,"人若在田間、或在葡萄園裡放牲畜、任憑牲畜上別人的田裡去喫、就必拿自己田間上好的、和葡萄園上好的賠還。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,6,"若點火焚燒荊棘、以致將別人堆積的禾捆、站著的禾稼、或是田園、都燒盡了、那點火的必要賠還。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,7,"人若將銀錢、或家具、交付鄰舍看守、這物從那人的家被偷去、若把賊找到了、賊要加倍賠還．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,8,"若找不到賊、那家主必就近審判官、要看看他拿了原主的物件沒有。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,9,"兩個人的案件、無論是為甚麼過犯、或是為牛、為驢、為羊、為衣裳、或是為甚麼失掉之物、有一人說、這是我的、兩造就要將案件稟告審判官、審判官定誰有罪、誰就要加倍賠還。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,10,"人若將驢、或牛、或羊、或別的牲畜、交付鄰舍看守、牲畜或死、或受傷、或被趕去、無人看見、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,11,"那看守的人、要憑著耶和華起誓、手裡未曾拿鄰舍的物、本主就要罷休、看守的人不必賠還。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,12,"牲畜若從看守的那裡被偷去、他就要賠還本主．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,13,"若被野獸撕碎、看守的要帶來當作證據、所撕的不必賠還。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,14,"人若向鄰舍借甚麼、所借的或受傷、或死、本主沒有同在一處、借的人總要賠還。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,15,"若本主同在一處、他就不必賠還．若是雇的、也不必賠還、本是為雇價來的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,16,"人若引誘沒有受聘的處女、與他行淫、他總要交出聘禮娶他為妻。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,17,"若女子的父親決不肯將女子給他、他就要按處女的聘禮、交出錢來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,18,"行邪術的女人、不可容他存活。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,19,"凡與獸淫合的、總要把他治死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,20,"祭祀別神、不單單祭祀耶和華的、那人必要滅絕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,21,"不可虧負寄居的、也不可欺壓他、因為你們在埃及地也作過寄居的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,22,"不可苦待寡婦和孤兒．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,23,"若是苦待他們一點、他們向我一哀求、我總要聽他們的哀聲．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,24,"並要發烈怒、用刀殺你們、使你們的妻子為寡婦、兒女為孤兒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,25,"我民中有貧窮人與你同住、你若借錢給他、不可如放債的向他取利。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,26,"你即或拿鄰舍的衣服作當頭、必在日落以先歸還他．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,27,"因他只有這一件當蓋頭、是他蓋身的衣服、若是沒有、他拿甚麼睡覺呢．他哀求我、我就應允、因為我是有恩惠的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,28,"不可毀謗　神、也不可毀謗你百姓的官長。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,29,"你要從你莊稼中的穀、和酒醡中滴出來的酒、拿來獻上、不可遲延。你要將頭生的兒子歸給我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,30,"你牛羊頭生的、也要這樣．七天當跟著母、第八天要歸給我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",22,31,"你要在我面前為聖潔的人、因此田間被野獸撕裂牲畜的肉、你們不可喫、要丟給狗喫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,1,"不可隨夥佈散謠言、不可與惡人連手妄作見證。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,2,"不可隨眾行惡．不可在爭訟的事上、隨眾偏行、作見證屈枉正直．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,3,"也不可在爭訟的事上、偏護窮人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,4,"若遇見你仇敵的牛、或驢、失迷了路、總要牽回來交給他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,5,"若看見恨你人的驢壓臥在重馱之下、不可走開、務要和驢主一同抬開重馱。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,6,"不可在窮人爭訟的事上屈枉正直。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,7,"當遠離虛假的事．不可殺無辜和有義的人、因我必不以惡人為義。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,8,"不可受賄賂、因為賄賂能叫明眼人變瞎了、又能顛倒義人的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,9,"不可欺壓寄居的．因為你們在埃及地作過寄居的、知道寄居的心。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,10,"六年你要耕種田地、收藏土產、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,11,"只是第七年、要叫地歇息、不耕不種、使你民中的窮人有喫的、他們所剩下的、野獸可以喫．你的葡萄園和橄欖園、也要照樣辦理。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,12,"六日你要作工、第七日要安息、使牛、驢可以歇息、並使你婢女的兒子和寄居的、都可以舒暢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,13,"凡我對你們說的話、你們要謹守．別神的名、你不可提、也不可從你口中傳說。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,14,"一年三次、你要向我守節。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,15,"你要守除酵節、照我所吩咐你的、在亞筆月內所定的日期、喫無酵餅七天．誰也不可空手朝見我、因為你是這月出了埃及。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,16,"又要守收割節、所收的是你田間所種勞碌得來初熟之物．並在年底收藏、要守收藏節。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,17,"一切的男丁、要一年三次朝見主耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,18,"不可將我祭牲的血、和有酵的餅、一同獻上、也不可將我節上祭牲的脂油留到早晨。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,19,"地裡首先初熟之物、要送到耶和華你　神的殿。不可用山羊羔母的奶煮山羊羔。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,20,"看哪、我差遣使者在你前面、在路上保護你、領你到我所預備的地方去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,21,"他是奉我名來的、你們要在他面前謹慎、聽從他的話、不可惹他、〔惹或作違背〕因為他必不赦免你們的過犯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,22,"你若實在聽從他的話、照著我一切所說的去行、我就向你的仇敵作仇敵、向你的敵人作敵人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,23,"我的使者要在你前面行、領你到亞摩利人、赫人、比利洗人、迦南人、希未人、耶布斯人那裡去．我必將他們剪除。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,24,"你不可跪拜他們的神、不可事奉他、也不可效法他們的行為、卻要把神像盡行拆毀、打碎他們的柱像。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,25,"你們要事奉耶和華你們的　神、他必賜福與你的糧、與你的水、也必從你們中間除去疾病。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,26,"你境內必沒有墜胎的、不生產的、我要使你滿了你年日的數目。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,27,"凡你所到的地方、我要使那裡的眾民、在你面前驚駭、擾亂、又要使你一切仇敵、轉背逃跑。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,28,"我要打發黃蜂飛在你前面、把希未人、迦南人、赫人、攆出去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,29,"我不在一年之內、將他們從你面前攆出去、恐怕地成為荒涼、野地的獸多起來害你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,30,"我要漸漸的將他們從你面前攆出去、等到你的人數加多、承受那地為業。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,31,"我要定你的境界、從紅海直到非利士海、又從曠野直到大河．我要將那地的居民交在你手中、你要將他們從你面前攆出去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,32,"不可和他們並他們的神立約。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",23,33,"他們不可住在你的地上、恐怕他們使你得罪我．你若事奉他們的神、這必成為你的網羅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,1,"耶和華對摩西說、你和亞倫、拿答、亞比戶、並以色列長老中的七十人、都要上到我這裡來、遠遠的下拜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,2,"惟獨你可以親近耶和華．他們卻不可親近．百姓也不可和你一同上來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,3,"摩西下山、將耶和華的命令、典章、都述說與百姓聽．眾百姓齊聲說、耶和華所吩咐的、我們都必遵行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,4,"摩西將耶和華的命令都寫上、清早起來、在山下築一座壇、按以色列十二支派、立十二根柱子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,5,"又打發以色列人中的少年人去獻燔祭、又向耶和華獻牛為平安祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,6,"摩西將血一半盛在盆中、一半灑在壇上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,7,"又將約書念給百姓聽、他們說、耶和華所吩咐的、我們都必遵行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,8,"摩西將血灑在百姓身上、說、你看、這是立約的血、是耶和華按這一切話與你們立約的憑據。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,9,"摩西、亞倫、拿答、亞比戶、並以色列長老中的七十人、都上了山。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,10,"他們看見以色列的　神、他腳下彷彿有平鋪的藍寶石、如同天色明淨。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,11,"他的手不加害在以色列的尊者身上．他們觀看　神、他們又喫又喝。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,12,"耶和華對摩西說、你上山到我這裡來住在這裡、我要將石版、並我所寫的律法、和誡命、賜給你、使你可以教訓百姓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,13,"摩西和他的幫手約書亞起來、上了　神的山、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,14,"摩西對長老說、你們在這裡等著、等到我們再回來、有亞倫、戶珥與你們同在、凡有爭訟的、都可以就近他們去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,15,"摩西上山、有雲彩把山遮蓋。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,16,"耶和華的榮耀停於西乃山、雲彩遮蓋山六天．第七天他從雲中召摩西。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,17,"耶和華的榮耀在山頂上、在以色列人眼前、形狀如烈火。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",24,18,"摩西進入雲中上山、在山上四十晝夜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,1,"耶和華曉諭摩西說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,2,"你告訴以色列人當為我送禮物來、凡甘心樂意的、你們就可以收下歸我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,3,"所要收的禮物、就是金、銀、銅、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,4,"藍色紫色朱紅色線、細麻、山羊毛、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,5,"染紅的公羊皮、海狗皮、皂莢木、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,6,"點燈的油、並作膏油和香的香料、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,7,"紅瑪瑙、與別樣的寶石、可以鑲嵌在以弗得和胸牌上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,8,"又當為我造聖所、使我可以住在他們中間。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,9,"製造帳幕和其中的一切器具、都要照我所指示你的樣式。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,10,"要用皂莢木作一櫃、長二肘半、寬一肘半、高一肘半。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,11,"要裡外包上精金、四圍鑲上金牙邊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,12,"也要鑄四個金環安在櫃的四腳上、這邊兩環、那邊兩環。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,13,"要用皂莢木作兩根杠、用金包裹。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,14,"要把杠穿在櫃旁的環內、以便抬櫃。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,15,"這杠要常在櫃的環內、不可抽出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,16,"必將我所要賜給你的法版、放在櫃裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,17,"要用精金作施恩座、〔施恩或作蔽罪下同〕長二肘半、寬一肘半。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,18,"要用金子錘出兩個基路伯來、安在施恩座的兩頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,19,"這頭作一個基路伯、那頭作一個基路伯、二基路伯要接連一塊、在施恩座的兩頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,20,"二基路伯要高張翅膀、遮掩施恩座．基路伯要臉對臉、朝著施恩座。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,21,"要將施恩座安在櫃的上邊、又將我所要賜給你的法版放在櫃裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,22,"我要在那裡與你相會、又要從法櫃施恩座上二基路伯中間、和你說我所要吩咐你傳給以色列人的一切事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,23,"要用皂莢木作一張桌子、長二肘、寬一肘、高一肘半。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,24,"要包上精金、四圍鑲上金牙邊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,25,"桌子的四圍各作一掌寬的橫梁、橫梁上鑲著金牙邊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,26,"要作四個金環、安在桌子的四角上、就是桌子四腳上的四角。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,27,"安環子的地方要挨近橫梁、可以穿杠抬桌子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,28,"要用皂莢木作兩根杠、用金包裹、以便抬桌子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,29,"要作桌子上的盤子、調羹、並奠酒的爵、和瓶、這都要用精金製作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,30,"又要在桌子上、在我面前、常擺陳設餅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,31,"要用精金作一個燈臺．燈臺的座、和榦、與杯、球、花、都要接連一塊錘出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,32,"燈臺兩旁要杈出六個枝子、這旁三個、那旁三個。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,33,"這旁每枝上有三個杯、形狀像杏花、有球、有花．那旁每枝上也有三個杯、形狀像杏花、有球、有花．從燈臺杈出來的六個枝子、都是如此。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,34,"燈臺上有四個杯、形狀像杏花、有球、有花．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,35,"燈臺每兩個枝子以下、有球、與枝子接連一塊．燈臺出的六個枝子、都是如此。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,36,"球和枝子要接連一塊、都是一塊精金錘出來的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,37,"要作燈臺的七個燈盞．祭司要點這燈、使燈光對照。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,38,"燈臺的蠟剪、和蠟花盤、也是要精金的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,39,"作燈臺和這一切的器具、要用精金一他連得。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",25,40,"要謹慎作這些物件、都要照著在山上指示你的樣式。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,1,"你要用十幅幔子作帳幕．這些幔子要用撚的細麻、和藍色紫色朱紅色線製造．並用巧匠的手工繡上基路伯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,2,"每幅幔子、要長二十八肘、寬四肘、幔子都要一樣的尺寸。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,3,"這五幅幔子要幅幅相連．那五幅幔子也要幅幅相連。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,4,"在這相連的幔子末幅邊上、要作藍色的鈕扣．在那相連的幔子末幅邊上、也要照樣作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,5,"要在這相連的幔子上作五十個鈕扣、在那相連的幔子上也作五十個鈕扣、都要兩兩相對。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,6,"又要作五十個金鉤、用鉤使幔子相連、這纔成了一個帳幕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,7,"你要用山羊毛、織十一幅幔子、作為帳幕以上的罩棚。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,8,"每幅幔子要長三十肘、寬四肘．十一幅幔子、都要一樣的尺寸。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,9,"要把五幅幔子連成一幅．又把六幅幔子連成一幅、這第六幅幔子、要在罩棚的前面摺上去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,10,"在這相連的幔子末幅邊上、要作五十個鈕扣．在那相連的幔子末幅邊上、也作五十個鈕扣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,11,"又要作五十個銅鉤、鉤在鈕扣中、使罩棚連成一個。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,12,"罩棚的幔子所餘那垂下來的半幅幔子、要垂在帳幕的後頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,13,"罩棚的幔子所餘長的、這邊一肘、那邊一肘、要垂在帳幕的兩旁、遮蓋帳幕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,14,"又要用染紅的公羊皮、作罩棚的蓋．再用海狗皮作一層罩棚上的頂蓋。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,15,"你要用皂莢木作帳幕的豎板。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,16,"每塊要長十肘、寬一肘半。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,17,"每塊必有兩榫相對．帳幕一切的板、都要這樣作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,18,"帳幕的南面要作板二十塊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,19,"在這二十塊板底下、要作四十個帶卯的銀座、兩卯接這塊板上的兩榫、兩卯接那塊板上的兩榫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,20,"帳幕第二面、就是北面、也要作板二十塊、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,21,"和帶卯的銀座四十個．這板底下有兩卯、那板底下也有兩卯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,22,"帳幕的後面、就是西面、要作板六塊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,23,"帳幕後面的拐角、要作板兩塊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,24,"板的下半截要雙的、上半截要整的、直頂到第一個環子、兩塊都要這樣作兩個拐角。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,25,"必有八塊板、和十六個帶卯的銀座、這板底下有兩卯、那板底下也有兩卯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,26,"你要用皂莢木作閂．為帳幕這面的板作五閂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,27,"為帳幕那面的板作五閂、又為帳幕後面的板作五閂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,28,"板腰間的中閂、要從這一頭通到那一頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,29,"板要用金子包裹、又要作板上的金環套閂、閂也要用金子包裹。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,30,"要照著在山上指示你的樣式、立起帳幕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,31,"你要用藍色紫色朱紅色線、和撚的細麻、織幔子．以巧匠的手工繡上基路伯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,32,"要把幔子掛在四根包金的皂莢木柱子上、柱子上當有金鉤、柱子安在四個帶卯的銀座上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,33,"要使幔子垂在鉤子下、把法櫃抬進幔子內、這幔子要將聖所和至聖所隔開。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,34,"又要把施恩座安在至聖所內的法櫃上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,35,"把桌子安在幔子外帳幕的北面．把燈臺安在帳幕的南面、彼此相對。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,36,"你要拿藍色紫色朱紅色線、和撚的細麻、用繡花的手工織帳幕的門簾。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",26,37,"要用皂莢木為簾子作五根柱子、用金子包裹．柱子上當有金鉤、又要為柱子用銅鑄造五個帶卯的座。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,1,"你要用皂莢木作壇．這壇要四方的、長五肘、寬五肘、高三肘。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,2,"要在壇的四拐角上作四個角、與壇接連一塊、用銅把壇包裹。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,3,"要作盆、收去壇上的灰、又作鏟子、盤子、肉鍤子、火鼎、壇上一切的器具、都用銅作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,4,"要為壇作一個銅網．在網的四角上、作四個銅環。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,5,"把網安在壇四面的圍腰板以下、使網從下達到壇的半腰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,6,"又要用皂莢木為壇作杠、用銅包裹。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,7,"這杠要穿在壇兩旁的環子內、用以抬壇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,8,"要用板作壇．壇是空的、都照著在山上指示你的樣式作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,9,"你要作帳幕的院子．院子的南面、要用撚的細麻作帷子、長一百肘。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,10,"帷子的柱子要二十根．帶卯的銅座二十個．柱子上的鉤子、和杆子、都要用銀子作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,11,"北面也當有帷子、長一百肘、帷子的柱子二十根、帶卯的銅座二十個．柱子上的鉤子、和杆子、都要用銀子作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,12,"院子的西面當有帷子、寬五十肘．帷子的柱子十根、帶卯的座十個。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,13,"院子的東面要寬五十肘。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,14,"門這邊的帷子要十五肘．帷子的柱子三根、帶卯的座三個。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,15,"門那邊的帷子也要十五肘．帷子的柱子三根、帶卯的座三個。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,16,"院子的門當有簾子、長二十肘．要拿藍色紫色朱紅色線、和撚的細麻、用繡花的手工織成．柱子四根、帶卯的座四個。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,17,"院子四圍一切的柱子、都要用銀杆連絡．柱子上的鉤子要用銀作．帶卯的座要用銅作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,18,"院子要長一百肘、寬五十肘、高五肘、帷子要用撚的細麻作．帶卯的座要用銅作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,19,"帳幕各樣用處的器具、並帳幕一切的橛子、和院子裡一切的橛子、都要用銅作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,20,"你要吩咐以色列人、把那為點燈搗成的清橄欖油拿來給你、使燈常常點著。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",27,21,"在會幕中法櫃前的幔外、亞倫和他的兒子、從晚上到早晨、要在耶和華面前經理這燈．這要作以色列人世世代代永遠的定例。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,1,"你要從以色列人中使你的哥哥亞倫、和他的兒子拿答、亞比戶、以利亞撒、以他瑪、一同就近你、給我供祭司的職分。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,2,"你要給你哥哥亞倫作聖衣為榮耀、為華美。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,3,"又要吩咐一切心中有智慧的、就是我用智慧的靈所充滿的、給亞倫作衣服、使他分別為聖、可以給我供祭司的職分。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,4,"所要作的就、是胸牌、以弗得、外袍、雜色的內袍、冠冕、腰帶、使你哥哥亞倫和他兒子穿這聖服、可以給我供祭司的職分。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,5,"要用金線和藍色紫色朱紅色線、並細麻去作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,6,"他們要拿金線和藍色紫色朱紅色線、並撚的細麻、用巧匠的手工作以弗得。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,7,"以弗得當有兩條肩帶、接上兩頭、使他相連。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,8,"其上巧工織的帶子、要和以弗得一樣的作法、用以束上、與以弗得接連一塊．要用金線和藍色紫色朱紅色線、並撚的細麻作成。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,9,"要取兩塊紅瑪瑙、在上面刻以色列兒子的名字．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,10,"六個名字在這塊寶石上、六個名字在那塊寶石上、都照他們生來的次序。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,11,"要用刻寶石的手工、彷彿刻圖書、按著以色列兒子的名字、刻這兩塊寶石、要鑲在金槽上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,12,"要將這兩塊寶石、安在以弗得的兩條肩帶上、為以色列人作紀念石．亞倫要在兩肩上、擔他們的名字、在耶和華面前作為紀念。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,13,"要用金子作二槽。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,14,"又拿精金、用擰工彷彿擰繩子、作兩條鍊子、把這擰成的鍊子搭在二槽上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,15,"你要用巧匠的手工、作一個決斷的胸牌．要和以弗得一樣的作法、用金線和藍色紫色朱紅色線、並撚的細麻作成。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,16,"這胸牌要四方的、疊為兩層．長一虎口、寬一虎口。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,17,"要在上面鑲寶石四行．第一行是紅寶石、紅璧璽、紅玉。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,18,"第二行是綠寶石、藍寶石、金鋼石。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,19,"第三行是紫瑪瑙、白瑪瑙、紫晶。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,20,"第四行是水蒼玉、紅瑪瑙、碧玉．這都要鑲在金槽中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,21,"這些寶石、都要按著以色列十二個兒子的名字、彷彿刻圖書、刻十二個支派的名字。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,22,"要在胸牌上、用精金擰成如繩的鍊子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,23,"在胸牌上也要作兩個金環、安在胸牌的兩頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,24,"要把那兩條擰成的金鍊子、穿過胸牌兩頭的環子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,25,"又要把鍊子的那兩頭、接在兩槽上、安在以弗得前面肩帶上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,26,"要作兩個金環、安在胸牌的兩頭、在以弗得裡面的邊上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,27,"又要作兩個金環、安在以弗得前面兩條肩帶的下邊、挨近相接之處、在以弗得巧工織的帶子以上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,28,"要用藍細帶子、把胸牌的環子與以弗得的環子繫住、使胸牌貼在以弗得巧工織的帶子上、不可與以弗得離縫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,29,"亞倫進聖所的時候、要將決斷胸牌、就是刻著以色列兒子名字的、帶在胸前、在耶和華面前常作紀念。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,30,"又要將烏陵、和土明、放在決斷的胸牌裡．亞倫進到耶和華面前的時候、要帶在胸前、在耶和華面前常將以色列人的決斷牌帶在胸前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,31,"你要作以弗得的外袍、顏色全是藍的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,32,"袍上要為頭留一領口、口的周圍織出領邊來、彷彿鎧甲的領口、免得破裂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,33,"袍子周圍底邊上、要用藍色紫色朱紅色線作石榴、在袍子周圍的石榴中間、要有金鈴鐺。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,34,"一個金鈴鐺、一個石榴、一個金鈴鐺、一個石榴、在袍子周圍的底邊上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,35,"亞倫供職的時候、要穿這袍子．他進聖所到耶和華面前、以及出來的時候、袍上的響聲必被聽見、使他不至於死亡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,36,"你要用精金作一面牌、在上面按刻圖書之法、刻著、歸耶和華為聖。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,37,"要用一條藍細帶子、將牌繫在冠冕的前面。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,38,"這牌必在亞倫的額上、亞倫要擔當干犯聖物條例的罪孽．這聖物是以色列人在一切的聖禮物上、所分別為聖的．這牌要常在他的額上、使他們可以在耶和華面前蒙悅納。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,39,"要用雜色細麻線織內袍．用細麻布作冠冕．又用繡花的手工作腰帶。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,40,"你要為亞倫的兒子作內袍、腰帶、裹頭巾、為榮耀、為華美．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,41,"要把這些給你的哥哥亞倫和他的兒子穿戴．又要膏他們、將他們分別為聖、好給我供祭司的職分。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,42,"要給他們作細麻布褲子、遮掩下體．褲子當從腰達到大腿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",28,43,"亞倫和他兒子進入會幕、或就近壇、在聖所供職的時候必穿上、免得擔罪而死．這要為亞倫和他的後裔作永遠的定例。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,1,"你使亞倫和他兒子成聖、給我供祭司的職分、要如此行．取一隻公牛犢、兩隻無殘疾的公綿羊、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,2,"無酵餅、和調油的無酵餅、與抹油的無酵薄餅．這都要用細麥麵作成。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,3,"這餅要裝在一個筐子裡、連筐子帶來、又把公牛和兩隻公綿羊牽來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,4,"要使亞倫和他兒子到會幕門口來、用水洗身。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,5,"要給亞倫穿上內袍、和以弗得的外袍、並以弗得．又帶上胸牌、束上以弗得巧工織的帶子．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,6,"把冠冕戴在他頭上、將聖冠加在冠冕上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,7,"就把膏油倒在他頭上膏他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,8,"要叫他的兒子來、給他們穿上內袍。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,9,"給亞倫和他兒子束上腰帶、包上裹頭巾、他們就憑永遠的定例、得了祭司的職任．又要將亞倫和他兒子分別為聖。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,10,"你要把公牛牽到會幕前、亞倫和他兒子要按手在公牛的頭上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,11,"你要在耶和華面前、在會幕門口、宰這公牛．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,12,"要取些公牛的血、用指頭抹在壇的四角上、把血都倒在壇腳那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,13,"要把一切蓋臟的脂油、與肝上的網子、並兩個腰子、和腰子上的脂油、都燒在壇上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,14,"只是公牛的皮、肉、糞、都要用火燒在營外．這牛是贖罪祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,15,"你要牽一隻公綿羊來、亞倫和他兒子、要按手在這羊的頭上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,16,"要宰這羊、把血灑在壇的周圍。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,17,"要把羊切成塊子、洗淨五臟、和腿、連塊子帶頭、都放在一處。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,18,"要把全羊燒在壇上、是給耶和華獻的燔祭、是獻給耶和華為馨香的火祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,19,"你要將那一隻公綿羊牽來、亞倫和他兒子、要按手在羊的頭上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,20,"你要宰這羊、取點血、抹在亞倫的右耳垂上、和他兒子的右耳垂上．又抹在他們右手的大拇指上、和右腳的大拇指上．並要把血灑在壇的四圍。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,21,"你要取點膏油和壇上的血、彈在亞倫和他的衣服上、並他兒子和他兒子的衣服上、他們和他們的衣服、就一同成聖。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,22,"你要取這羊的脂油、和肥尾巴、並蓋臟的脂油、與肝上的網子、兩個腰子、和腰子上的脂油、並右腿。（這是承接聖職所獻的羊）")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,23,"再從耶和華面前裝無酵餅的筐子中取一個餅、一個調油的餅、和一個薄餅．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,24,"都放在亞倫的手上、和他兒子的手上、作為搖祭、在耶和華面前搖一搖。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,25,"要從他們手中接過來、燒在耶和華面前壇上的燔祭上、是獻給耶和華為馨香的火祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,26,"你要取亞倫承接聖職所獻公羊的胸、作為搖祭、在耶和華面前搖一搖、這就可以作你的分。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,27,"那搖祭的胸、和舉祭的腿、就是承接聖職所搖的、所舉的、是歸亞倫和他兒子的．這些你都要成為聖、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,28,"作亞倫和他子孫從以色列人中永遠所得的分、因為是舉祭．這要從以色列人的平安祭中、作為獻給耶和華的舉祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,29,"亞倫的聖衣要留給他的子孫、可以穿著受膏．又穿著承接聖職。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,30,"他的子孫接續他當祭司的、每逢進會幕在聖所供職的時候、要穿七天。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,31,"你要將承接聖職所獻公羊的肉、煮在聖處。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,32,"亞倫和他兒子要在會幕門口、喫這羊的肉、和筐內的餅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,33,"他們喫那些贖罪之物、好承接聖職、使他們成聖．只是外人不可喫、因為這是聖物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,34,"那承接聖職所獻的肉、或餅、若有一點留到早晨、就要用火燒了、不可喫這物、因為是聖物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,35,"你要這樣照我一切所吩咐的、向亞倫和他兒子行承接聖職的禮七天。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,36,"每天要獻公牛一隻、為贖罪祭．你潔淨壇的時候、壇就潔淨了．且要用膏抹壇、使壇成聖。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,37,"要潔淨壇七天、使壇成聖、壇就成為至聖．凡挨著壇的都成為聖。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,38,"你每天所要獻在壇上的、就是兩隻一歲的羊羔、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,39,"早晨要獻這一隻、黃昏的時候要獻那一隻。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,40,"和這一隻羊羔同獻的、要用細麵伊法十分之一、與搗成的油一欣四分之一調和．又用酒一欣四分之一、作為奠祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,41,"那一隻羊羔要在黃昏的時候獻上、照著早晨的素祭、和奠祭的禮辦理、作為獻給耶和華馨香的火祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,42,"這要在耶和華面前、會幕門口、作你們世世代代常獻的燔祭．我要在那裡與你們相會、和你們說話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,43,"我要在那裡與以色列人相會、會幕就要因我的榮耀成為聖。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,44,"我要使會幕和壇成聖、也要使亞倫和他的兒子成聖、給我供祭司的職分。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,45,"我要住在以色列人中間、作他們的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",29,46,"他們必知道我是耶和華他們的　神、是將他們從埃及地領出來的、為要住在他們中間．我是耶和華他們的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,1,"你要用皂莢木作一座燒香的壇．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,2,"這壇要四方的、長一肘、寬一肘、高二肘．壇的四角、要與壇接連一塊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,3,"要用精金、把壇的上面、與壇的四圍、並壇的四角包裹．又要在壇的四圍鑲上金牙邊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,4,"要作兩個金環、安在牙子邊以下、在壇的兩旁、兩根橫橕上、作為穿杠的用處、以便抬壇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,5,"要用皂莢木作杠、用金包裹。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,6,"要把壇放在法櫃前的幔子外、對著法櫃上的施恩座、就是我要與你相會的地方。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,7,"亞倫在壇上要燒馨香料作的香．每早晨他收拾燈的時候、要燒這香。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,8,"黃昏點燈的時候、他要在耶和華面前燒這香、作為世世代代常燒的香。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,9,"在這壇上不可奉上異樣的香、不可獻燔祭、素祭、也不可澆上奠祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,10,"亞倫一年一次、要在壇的角上行贖罪之禮．他一年一次要用贖罪祭牲的血、在壇上行贖罪之禮、作為世世代代的定例．這壇在耶和華面前為至聖。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,11,"耶和華曉諭摩西說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,12,"你要按以色列人被數的、計算總數、你數的時候、他們各人要為自己的生命、把贖價奉給耶和華、免得數的時候、在他們中間有災殃。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,13,"凡過去歸那些被數之人的、每人要按聖所的平拿銀子半舍客勒、這半舍客勒是奉給耶和華的禮物、一舍客勒是二十季拉．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,14,"凡過去歸那些被數的人、從二十歲以外的、要將這禮物奉給耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,15,"他們為贖生命將禮物奉給耶和華、富足的不可多出、貧窮的也不可少出、各人要出半舍客勒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,16,"你要從以色列人收這贖罪銀、作為會幕的使用、可以在耶和華面前為以色列人作紀念、贖生命。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,17,"耶和華曉諭摩西說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,18,"你要用銅作洗濯盆、和盆座、以便洗濯．要將盆放在會幕和壇的中間、在盆裡盛水。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",30,19,"亞倫和他的兒子、要在這盆裡洗手洗腳。")');
