tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",39,14,"這些寶石、都是按著以色列十二個兒子的名字、彷彿刻圖書、刻十二個支派的名字。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",39,15,"在胸牌上、用精金擰成如繩子的鍊子。")');
