tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",40,10,"又要抹燔祭壇、和一切器具使壇成聖、就都成為至聖。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",40,11,"要抹洗濯盆、和盆座、使盆成聖。")');
