tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",40,17,"第二年正月初一日、帳幕就立起來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",40,18,"摩西立起帳幕、安上帶卯的座、立上板、穿上閂、立起柱子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",40,19,"在帳幕以上搭罩棚、把罩棚的頂蓋、蓋在其上．是照耶和華所吩咐他的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",40,20,"又把法版放在櫃裡、把杠穿在櫃的兩旁、把施恩座安在櫃上．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",40,21,"把櫃抬進帳幕、掛上遮掩櫃的幔子、把法櫃遮掩了．是照耶和華所吩咐他的。")');
