tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",40,28,"又掛上帳幕的門簾．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",40,29,"在會幕的帳幕門前、安設燔祭壇、把燔祭、和素祭、獻在其上．是照耶和華所吩咐他的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",40,30,"把洗濯盆安在會幕和壇的中間、盆中盛水、以便洗濯。")');
