tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",40,32,"他們進會幕、或就近壇的時候、便都洗濯．是照耶和華所吩咐他的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Exo",40,33,"在帳幕和壇的四圍、立了院帷、把院子的門簾掛上．這樣摩西就完了工。")');
