tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",1,4,"他要按手在燔祭牲的頭上、燔祭便蒙悅納、為他贖罪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",1,5,"他要在耶和華面前宰公牛、亞倫子孫作祭司的、要奉上血、把血灑在會幕門口壇的周圍。")');
