tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",1,7,"祭司亞倫的子孫、要把火放在壇上、把柴擺在火上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",1,8,"亞倫子孫作祭司的、要把肉塊和頭、並脂油、擺在壇上火的柴上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",1,9,"但燔祭的臟腑與腿、要用水洗．祭司就要把一切全燒在壇上、當作燔祭、獻與耶和華為馨香的火祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",1,10,"人的供物若以綿羊、或山羊為燔祭、就要獻上沒有殘疾的公羊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",1,11,"要把羊宰於壇的北邊、在耶和華面前．亞倫子孫作祭司的、要把羊血灑在壇的周圍。")');
