tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",1,17,"要拿著鳥的兩個翅膀、把鳥撕開、只是不可撕斷．祭司要在壇上在火的柴上焚燒．這是燔祭、是獻與耶和華為馨香的火祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",2,1,"若有人獻素祭為供物給耶和華、要用細麵澆上油、加上乳香．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",2,2,"帶到亞倫子孫作祭司的那裡、祭司就要從細麵中取出一把來、並取些油、和所有的乳香、然後要把所取的這些作為紀念、燒在壇上、是獻與耶和華為馨香的火祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",2,3,"素祭所剩的、要歸給亞倫和他的子孫、這是獻與耶和華的火祭中為至聖的。")');
