tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",2,8,"要把這些東西作的素祭、帶到耶和華面前、並奉給祭司帶到壇前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",2,9,"祭司要從素祭中取出作為紀念的、燒在壇上、是獻與耶和華為馨香的火祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",2,10,"素祭所剩的、要歸給亞倫和他的子孫．這是獻與耶和華的火祭中為至聖的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",2,11,"凡獻給耶和華的素祭、都不可有酵、因為你們不可燒一點酵一點蜜、當作火祭獻給耶和華。")');
