tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",2,16,"祭司要把其中作為紀念的、就是一些軋了的禾穗子、和一些油並所有的乳香、都焚燒、是向耶和華獻的火祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",3,1,"人獻供物為平安祭、〔平安或作酬恩下同〕若是從牛群中獻、無論是公的、是母的、必用沒有殘疾的獻在耶和華面前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",3,2,"他要按手在供物的頭上、宰於會幕門口．亞倫子孫作祭司的、要把血灑在壇的周圍。")');
