tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",6,26,"為贖罪獻這祭的祭司要喫、要在聖處、就是在會幕的院子裡喫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Lev",6,27,"凡摸這祭肉的、要成為聖．這祭牲的血、若彈在甚麼衣服上、所彈的那一件、要在聖處洗淨。")');
