tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,8,"以色列人所得的地業、從中要把些城邑給利未人、人多的就多給、人少的就少給、各支派要按所承受為業之地把城邑給利未人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,9,"耶和華曉諭摩西說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,10,"你吩咐以色列人說、你們過約但河、進了迦南地、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,11,"就要分出幾座城、為你們作逃城、使誤殺人的可以逃到那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,12,"這些城、可以作逃避報仇人的城、使誤殺人的不至於死、等他站在會眾面前聽審判。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,13,"你們所分出來的城、要作六座逃城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,14,"在約但河東要分出三座城、在迦南地也要分出三座城、都作逃城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,15,"這六座城要給以色列人、和他們中間的外人、並寄居的、作為逃城、使誤殺人的都可以逃到那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,16,"倘若人用鐵器打人、以致打死、他就是故殺人的、故殺人的必被治死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,17,"若用可以打死人的石頭打死了人、他就是故殺人的、故殺人的必被治死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,18,"若用可以打死人的木器打死了人、他就是故殺人的、故殺人的必被治死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,19,"報血仇的、必親自殺那故殺人的、一遇見就殺他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,20,"人若因怨恨把人推倒、或是埋伏往人身上扔物、以致於死、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,21,"或是因仇恨用手打人、以致於死、那打人的、必被治死。他是故殺人的、報血仇的一遇見就殺他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,22,"倘若人沒有仇恨、忽然將人推倒、或是沒有埋伏、把物扔在人身上、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,23,"或是沒有看見的時候用可以打死人的石頭、扔在人身上、以致於死、本來與他無仇、也無意害他、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,24,"會眾就要照典章、在打死人的、和報血仇的中間審判。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,25,"會眾要救這誤殺人的脫離報血仇人的手、也要使他歸入逃城．他要住在其中、直等到受聖膏的大祭司死了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,26,"但誤殺人的、無論甚麼時候、若出了逃城的境外．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,27,"報血仇的在逃城境外遇見他、將他殺了、報血仇的就沒有流血之罪．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,28,"因為誤殺人的該住在逃城裡、等到大祭司死了、大祭司死了以後、誤殺人的纔可以回到他所得為業之地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,29,"這在你們一切的住處、要作你們世世代代的律例、典章。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,30,"無論誰故殺人、要憑幾個見證人的口、把那故殺人的殺了、只是不可憑一個見證的口叫人死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,31,"故殺人犯死罪的、你們不可收贖價代替他的命他必被治死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,32,"那逃到逃城的人、你們不可為他收贖價、使他在大祭司未死以先、再來住在本地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,33,"這樣、你們就不污穢所住之地、因為血是污穢地的、若有在地上流人血的、非流那殺人者的血、那地就不得潔淨〔潔淨原文作贖〕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",35,34,"你們不可玷污所住之地、就是我住在其中之地因為我耶和華住在以色列人中間。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",36,1,"約瑟的後裔、瑪拿西的孫子、瑪吉的兒子基列、他子孫中的諸族長來到摩西、和作首領的以色列人族長面前、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",36,2,"說、耶和華曾吩咐我主、拈鬮分地給以色列人為業、我主也受了耶和華的吩咐、將我們兄弟西羅非哈的產業分給他的眾女兒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",36,3,"他們若嫁以色列別支派的人、就必將我們祖宗所遺留的產業、加在他們丈夫支派的產業中．這樣、我們拈鬮所得的產業就要減少了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",36,4,"到了以色列人的禧年、這女兒的產業就必加在他們丈夫支派的產業上．這樣、我們祖宗支派的產業就減少了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",36,5,"摩西照耶和華的話、吩咐以色列人說、約瑟支派的人說得有理。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",36,6,"論到西羅非哈的眾女兒、耶和華這樣吩咐說．他們可以隨意嫁人、只是要嫁同宗支派的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",36,7,"這樣、以色列人的產業就不從這支派歸到那支派．因為以色列人要各守各祖宗支派的產業。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",36,8,"凡在以色列支派中得了產業的女子、必作同宗支派人的妻、好叫以色列人、各自承受他祖宗的產業。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",36,9,"這樣、他們的產業就不從這支派歸到那支派、因為以色列支派的人、要各守各的產業。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",36,10,"耶和華怎樣吩咐摩西、西羅非哈的眾女兒就怎樣行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",36,11,"西羅非哈的女兒瑪拉、得撒、曷拉、密迦、挪阿都嫁了他們伯叔的兒子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",36,12,"他們嫁入約瑟兒子、瑪拿西子孫的族中、他們的產業仍留在同宗支派中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Num",36,13,"這是耶和華在摩押平原、約但河邊、耶利哥對面、藉著摩西所吩咐以色列人的命令、典章。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,1,"以下所記的、是摩西在約但河東的曠野、疏弗對面的亞拉巴、就是巴蘭、陀弗、拉班、哈洗錄、底撒哈中間、向以色列眾人所說的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,2,"從何烈山經過西珥山、到加低斯巴尼亞、有十一天的路程。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,3,"出埃及第四十年十一月初一日、摩西照耶和華藉著他所吩咐以色列人的話、都曉諭他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,4,"那時、他已經擊殺了住希實本的亞摩利王西宏、和住以得來亞斯他錄的巴珊王噩。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,5,"摩西在約但河東的摩押地、講律法說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,6,"耶和華我們的　神在何烈山曉諭我們說、你們在這山上住的日子夠了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,7,"要起行轉到亞摩利人的山地、和靠近這山地的各處、就是亞拉巴、山地、高原、南地、沿海一帶迦南人的地、並利巴嫩山又到伯拉大河．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,8,"如今我將這地擺在你們面前、你們要進去得這地、就是耶和華向你們列祖亞伯拉罕、以撒、雅各、起誓應許賜給他們和他們後裔為業之地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,9,"那時我對你們說、管理你們的重任、我獨自擔當不起。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,10,"耶和華你們的　神使你們多起來、看哪、你們今日像天上的星那樣多。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,11,"惟願耶和華你們列祖的　神、使你們比如今更多千倍、照他所應許你們的話、賜福與你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,12,"但你們的麻煩、和管理你們的重任、並你們的爭訟、我獨自一人怎能擔當得起呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,13,"你們要按著各支派、選舉有智慧、有見識、為眾人所認識的、我立他們為你們的首領。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,14,"你們回答我說、照你所說的行了為妙。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,15,"我便將你們各支派的首領、有智慧為眾人所認識的、照你們的支派、立他們為官長、千夫長、百夫長、五十夫長、十夫長、管理你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,16,"當時、我囑咐你們的審判官說、你們聽訟、無論是弟兄彼此爭訟、是與同居的外人爭訟、都要按公義判斷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,17,"審判的時候、不可看人的外貌、聽訟不可分貴賤、不可懼怕人、因為審判是屬乎　神的．若有難斷的案件、可以呈到我這裡、我就判斷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,18,"那時、我將你們所當行的事、都吩咐你們了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,19,"我們照著耶和華我們　神所吩咐的、從何烈山起行、經過你們所看見那大而可怕的曠野、往亞摩利人的山地去、到了加低斯巴尼亞。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,20,"我對你們說、你們已經到了耶和華我們　神所賜給我們的亞摩利人之山地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,21,"看哪、耶和華你的　神已將那地擺在你面前、你要照耶和華你列祖的　神所說的上去得那地為業．不要懼怕、也不要驚惶。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,22,"你們都就近我來說、我們要先打發人去、為我們窺探那地、將我們上去該走何道、必進何城、都回報我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,23,"這話我以為美、就從你們中間選了十二個人、每支派一人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,24,"於是他們起身上山地去、到以實各谷、窺探那地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,25,"他們手裡拿著那地的果子下來、到我們那裡、回報說、耶和華我們的　神所賜給我們的是美地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,26,"你們卻不肯上去、竟違背了耶和華你們　神的命令．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,27,"在帳棚內發怨言、說、耶和華因為恨我們、所以將我們從埃及地領出來、要交在亞摩利人手中、除滅我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,28,"我們上那裡去呢．我們的弟兄使我們的心消化、說、那地的民比我們又大又高、城邑又廣大又堅固、高得頂天、並且我們在那裡看見亞衲族的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,29,"我就對你們說、不要驚恐、也不要怕他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,30,"在你們前面行的耶和華你們的　神、必為你們爭戰、正如他在埃及和曠野、在你們眼前所行的一樣．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,31,"你們在曠野所行的路上、也曾見耶和華你們的　神、撫養你們、如同人撫養兒子一般、直等你們來到這地方。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,32,"你們在這事上卻不信耶和華你們的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,33,"他在路上、在你們前面行、為你們找安營的地方、夜間在火柱裡、日間在雲柱裡、指示你們所當行的路。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,34,"耶和華聽見你們這話、就發怒起誓說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,35,"這惡世代的人、連一個也不得見我起誓應許賜給你們列祖的美地．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,36,"惟有耶孚尼的兒子迦勒、必得看見、並且我要將他所踏過的地賜給他、和他的子孫、因為他專心跟從我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,37,"耶和華為你的緣故、也向我發怒、說、你必不得進入那地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,38,"伺候你嫩的兒子約書亞、他必得進入那地、你要勉勵他、因為他要使以色列人承受那地為業。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,39,"並且你們的婦人孩子、就是你們所說必被擄掠的、和今日不知善惡的兒女、必進入那地、我要將那地賜給他們、他們必得為業。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,40,"至於你們、要轉回、從紅海的路往曠野去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,41,"那時、你們回答我說、我們得罪了耶和華、情願照耶和華我們　神一切所吩咐的、上去爭戰、於是你們各人帶著兵器、爭先上山地去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,42,"耶和華吩咐我說、你對他們說、不要上去、也不要爭戰、因我不在你們中間、恐怕你們被仇敵殺敗了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,43,"我就告訴了你們、你們卻不聽從、竟違背耶和華的命令、擅自上山地去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,44,"住那山地的亞摩利人、就出來攻擊你們、追趕你們如蜂擁一般、在西珥殺退你們、直到何珥瑪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,45,"你們便回來、在耶和華面前哭號、耶和華卻不聽你們的聲音、也不向你們側耳。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",1,46,"於是你們在加低斯住了許多日子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,1,"此後、我們轉回、從紅海的路往曠野去、是照耶和華所吩咐我的．我們在西珥山繞行了許多日子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,2,"耶和華對我說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,3,"你們繞行這山的日子夠了、要轉向北去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,4,"你吩咐百姓說、你們弟兄以掃的子孫、住在西珥、你們要經過他們的境界．他們必懼怕你們、所以你們要分外謹慎。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,5,"不可與他們爭戰、他們的地、連腳掌可踏之處、我都不給你們、因我已將西珥山賜給以掃為業。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,6,"你們要用錢向他們買糧喫、也要用錢向他們買水喝。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,7,"因為耶和華你的　神、在你手裡所辦的一切事上、已賜福與你．你走這大曠野他都知道了．這四十年、耶和華你的　神常與你同在、故此你一無所缺。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,8,"於是我們離了我們弟兄以掃子孫所住的西珥、從亞拉巴的路、經過以拉他、以旬迦別、轉向摩押曠野的路去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,9,"耶和華吩咐我說、不可擾害摩押人、也不可與他們爭戰．他們的地我不賜給你為業、因我已將亞珥賜給羅得的子孫為業。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,10,"（先前、有以米人住在那裡、民數眾多、身體高大、像亞衲人一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,11,"這以米人像亞衲人、也算為利乏音人、摩押人稱他們為以米人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,12,"先前、何利人也住在西珥、但以掃的子孫將他們除滅、得了他們的地、接著居住、就如以色列在耶和華賜給他為業之地所行的一樣。）")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,13,"現在起來過撒烈溪．於是我們過了撒烈溪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,14,"自從離開加低斯巴尼亞、到過了撒烈溪的時候、共有三十八年、等那世代的兵丁、都從營中滅盡、正如耶和華向他們所起的誓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,15,"耶和華的手也攻擊他們、將他們從營中除滅、直到滅盡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,16,"兵丁從民中都滅盡死亡以後、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,17,"耶和華吩咐我說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,18,"你今天要從摩押的境界亞珥經過、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,19,"走近亞捫人之地、不可擾害他們、也不可與他們爭戰、亞捫人的地我不賜給你們為業、因我已將那地賜給羅得的子孫為業。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,20,"（那地也算為利乏音人之地、先前、利乏音人住在那裡、亞捫人稱他們為散送冥。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,21,"那民眾多身體高大、像亞衲人一樣、但耶和華從亞捫人面前除滅他們、亞捫人就得了他們的地、接著居住．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,22,"正如耶和華從前為住西珥的以掃子孫、將何利人從他們面前除滅．他們得了何利人的地、接著居住一樣、直到今日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,23,"從迦斐託出來的迦斐託人、將先前住在鄉村直到迦薩的亞衛人除滅、接著居住。）")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,24,"你們起來前往、過亞嫩谷、我已將亞摩利人希實本王西宏、和他的地、交在你手中、你要與他爭戰得他的地為業。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,25,"從今日起我要使天下萬民、聽見你的名聲都驚恐、懼怕、且因你發顫傷慟。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,26,"我從基底莫的曠野、差遣使者去見希實本王西宏、用和睦的話說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,27,"求你容我從你的地經過、只走大道、不偏左右。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,28,"你可以賣糧給我喫、也可以賣水給我喝、只要容我步行過去．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,29,"就如住西珥的以掃子孫、和住亞珥的摩押人待我一樣、等我過了約但河、好進入耶和華我們　神所賜給我們的地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,30,"但希實本王西宏、不容我們從他那裡經過、因為耶和華你的　神使他心中剛硬、性情頑梗、為要將他交在你手中、像今日一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,31,"耶和華對我說、從此起首、我要將西宏和他的地、交給你、你要得他的地為業。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,32,"那時西宏和他的眾民、出來攻擊我們、在雅雜與我們交戰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,33,"耶和華我們的　神、將他交給我們、我們就把他和他的兒子、並他的眾民都擊殺了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,34,"我們奪了他的一切城邑、將有人煙的各城、連女人帶孩子、盡都毀滅、沒有留下一個。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,35,"惟有牲畜、和所奪的各城、並其中的財物、都取為自己的掠物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,36,"從亞嫩谷邊的亞羅珥、和谷中的城、直到基列、耶和華我們的　神都交給我們了、沒有一座城高得使我們不能攻取的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",2,37,"惟有亞捫人之地、凡靠近雅博河的地、並山地的城邑、與耶和華我們　神所禁止我們去的地方、都沒有挨近。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,1,"以後我們轉回向巴珊去、巴珊王噩和他的眾民都出來、在以得來與我們交戰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,2,"耶和華對我說、不要怕他、因我已將他和他的眾民、並他的地、都交在你手中、你要待他像從前待住希實本的亞摩利王西宏一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,3,"於是耶和華我們的　神、也將巴珊王噩、和他的眾民、都交在我們手中、我們殺了他們、沒有留下一個。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,4,"那時我們奪了他所有的城、共有六十座、沒有一座城不被我們所奪、這為亞珥歌伯的全境、就是巴珊地、噩王的國。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,5,"這些城都有堅固的高牆、有門有閂．此外還有許多無城牆的鄉村。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,6,"我們將這些都毀滅了、像從前待希實本王西宏一樣、把有人煙的各城、連女人帶孩子、盡都毀滅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,7,"惟有一切牲畜、和城中的財物、都取為自己的掠物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,8,"那時我們從約但河東兩個亞摩利王的手、將亞嫩谷直到黑門山之地奪過來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,9,"（這黑門山、西頓人稱為西連、亞摩利人稱為示尼珥。）")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,10,"就是奪了平原的各城、基列全地、巴珊全地、直到撒迦、和以得來、都是巴珊王噩國內的城邑。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,11,"（利乏音人所剩下的、只有巴珊王噩．他的床是鐵的、長九肘、寬四肘、都是以人肘為度．現今豈不是在亞捫人的拉巴麼。）")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,12,"那時我們得了這地．從亞嫩谷邊的亞羅珥起、我將基列山地的一半、並其中的城邑、都給了流便人、和迦得人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,13,"其餘的基列地、和巴珊全地、就是噩王的國、我給了瑪拿西半支派．亞珥歌伯全地乃是巴珊全地、這叫作利乏音人之地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,14,"瑪拿西的子孫睚珥、佔了亞珥歌伯全境、直到基述人、和瑪迦人的交界、就按自己的名稱這巴珊地為哈倭特睚珥、直到今日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,15,"我又將基列給了瑪吉。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,16,"從基列到亞嫩谷、以谷中為界、直到亞捫人交界的雅博河、我給了流便人、和迦得人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,17,"又將亞拉巴、和靠近約但河之地、從基尼烈直到亞拉巴海、就是鹽海、並毗斯迦山根東邊之地．都給了他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,18,"那時我吩咐你們說、耶和華你們的　神、已將這地賜給你們為業．你們所有的勇士、都要帶著兵器、在你們的弟兄以色列人前面過去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,19,"但你們的妻子、孩子、牲畜（我知道你們有許多的牲畜）可以住在我所賜給你們的各城裡．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,20,"等到你們弟兄在約但河那邊、也得耶和華你們　神所賜給他們的地、又使他們得享平安、與你們一樣、你們纔可以回到我所賜給你們為業之地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,21,"那時我吩咐約書亞說、你親眼看見了耶和華你　神向這二王所行的、耶和華也必向你所要去的各國照樣行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,22,"你不要怕他們、因那為你爭戰的、是耶和華你的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,23,"那時我懇求耶和華說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,24,"主耶和華阿、你已將你的大力大能顯給僕人看、在天上、在地下、有甚麼神能像你行事、像你有大能的作為呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,25,"求你容我過去、看約但河那邊的美地、就是那佳美的山地、和利巴嫩。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,26,"但耶和華因你們的緣故向我發怒、不應允我、對我說、罷了、你不要向我再提這事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,27,"你且上毗斯迦山頂去、向東、西、南、北、舉目觀望、因為你必不能過這約但河。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,28,"你卻要囑咐約書亞、勉勵他、使他膽壯、因為他必在這百姓前面過去使他們承受你所要觀看之地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",3,29,"於是我們住在伯毗珥對面的谷中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,1,"以色列人哪、現在我所教訓你們的律例、典章、你們要聽從遵行、好叫你們存活、得以進入耶和華你們列祖之　神所賜給你們的地、承受為業。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,2,"所吩咐你們的話、你們不可加添、也不可刪減、好叫你們遵守我所吩咐的、就是耶和華你們　神的命令。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,3,"耶和華因巴力毗珥的事所行的、你們親眼看見了、凡隨從巴力毗珥的人、耶和華你們的　神都從你們中間除滅了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,4,"惟有你們專靠耶和華你們　神的人、今日全都存活。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,5,"我照著耶和華我　神所吩咐的、將律例、典章、教訓你們、使你們在所要進去得為業的地上遵行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,6,"所以你們要謹守遵行、這就是你們在萬民眼前的智慧、聰明．他們聽見這一切律例、必說、這大國的人真是有智慧、有聰明。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,7,"那一大國的人有神與他們相近、像耶和華我們的　神、在我們求告他的時候與我們相近呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,8,"又那一大國有這樣公義的律例、典章、像我今日在你們面前所陳明的這一切律法呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,9,"你只要謹慎、殷勤保守你的心靈、免得忘記你親眼所看見的事、又免得你一生、這事離開你的心．總要傳給你的子子孫孫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,10,"你在何烈山站在耶和華你　神面前的那日．耶和華對我說、你為我招聚百姓、我要叫他們聽見我的話、使他們存活在世的日子、可以學習敬畏我、又可以教訓兒女這樣行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,11,"那時你們近前來、站在山下、山上有火焰沖天、並有昏黑、密雲、幽暗。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,12,"耶和華從火焰中對你們說話、你們只聽見聲音、卻沒有看見形像。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,13,"他將所吩咐你們當守的約、指示你們、就是十條誡．並將這誡寫在兩塊石版上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,14,"那時耶和華又吩咐我將律例、典章教訓你們、使你們在所要過去得為業的地上遵行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,15,"所以你們要分外謹慎、因為耶和華在何烈山、從火中對你們說話的那日、你們沒有看見甚麼形像。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,16,"惟恐你們敗壞自己、雕刻偶像、彷彿甚麼男像、女像、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,17,"或地上走獸的像、或空中飛鳥的像、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,18,"或地上爬物的像、或地底下水中魚的像。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,19,"又恐怕你向天舉目觀看、見耶和華你的　神為天下萬民所擺列的日月星、就是天上的萬像．自己便被勾引敬拜事奉它。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,20,"耶和華將你們從埃及領出來脫離鐵爐、要特作自己產業的子民、像今日一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,21,"耶和華又因你們的緣故、向我發怒起誓、必不容我過約但河、也不容我進入耶和華你　神所賜你為業的那美地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,22,"我只得死在這地、不能過約但河．但你們必過去得那美地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,23,"你們要謹慎、免得忘記耶和華你們　神與你們所立的約、為自己雕刻偶像、就是耶和華你　神所禁止你作的偶像、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,24,"因為耶和華你的　神乃是烈火、是忌邪的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,25,"你們在那地住久了、生子生孫、就雕刻偶像彷彿甚麼形像、敗壞自己、行耶和華你　神眼中看為惡的事、惹他發怒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,26,"我今日呼天喚地向你們作見證、你們必在過約但河得為業的地上、速速滅盡．你們不能在那地上長久、必盡行除滅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,27,"耶和華必使你們分散在萬民中、在他所領你們到的萬國裡、你們剩下的人數稀少。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,28,"在那裡你們必事奉人手所造的神、就是用木石造成、不能看、不能聽、不能喫、不能聞的神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,29,"但你們在那裡必尋求耶和華你的　神．你盡心盡性尋求他的時候、就必尋見。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,30,"日後你遭遇一切患難的時候、你必歸回耶和華你的　神、聽從他的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,31,"耶和華你　神原是有憐憫的　神、他總不撇下你、不滅絕你、也不忘記他起誓與你列祖所立的約。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,32,"你且考察在你以前的世代、自　神造人在世以來、從天這邊到天那邊、曾有何民聽見　神在火中說話的聲音、像你聽見、還能存活呢．這樣的大事、何曾有、何曾聽見呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,33,"見上節")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,34,"　神何曾從別的國中將一國的人民領出來、用試驗、神蹟、奇事、爭戰、大能的手、和伸出來的膀臂、並大可畏的事、像耶和華你們的　神在埃及、在你們眼前為你們所行的一切事呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,35,"這是顯給你看、要使你知道惟有耶和華他是　神、除他以外、再無別神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,36,"他從天上使你聽見他的聲音、為要教訓你、又在地上使你看見他的烈火、並且聽見他從火中所說的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,37,"因他愛你的列祖、所以揀選他們的後裔、用大能親自領你出了埃及、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,38,"要將比你強大的國民、從你面前趕出、領你進去、將他們的地賜你為業、像今日一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,39,"所以今日你要知道、也要記在心上、天上地下、惟有耶和華他是　神、除他以外、再無別神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,40,"我今日將他的律例、誡命、曉諭你、你要遵守、使你和你的子孫可以得福、並使你的日子、在耶和華你　神所賜的地上、得以長久。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,41,"那時摩西在約但河東、向日出之地、分定三座城、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,42,"使那素無仇恨、無心殺了人的、可以逃到這三城之中的一座城、就得存活。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,43,"為流便人、分定曠野平原的比悉．為迦得人、分定基列的拉末．為瑪拿西人、分定巴珊的哥蘭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,44,"摩西在以色列人面前所陳明的律法、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,45,"就是摩西在以色列人出埃及後、所傳給他們的法度、律例、典章。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,46,"在約但河東伯毗珥對面的谷中、在住希實本亞摩利王西宏之地．這西宏、是摩西和以色列人出埃及後所擊殺的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,47,"他們得了他的地、又得了巴珊王噩的地、就是兩個亞摩利王、在約但河東向日出之地．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,48,"從亞嫩谷邊的亞羅珥、直到西雲山、就是黑門山．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",4,49,"還有約但河東的全亞拉巴、直到亞拉巴海、靠近毗斯迦山根。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,1,"摩西將以色列眾人召了來、對他們說、以色列人哪、我今日曉諭你們的律例、典章、你們要聽、可以學習、謹守遵行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,2,"耶和華我們的　神在何烈山與我們立約。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,3,"這約不是與我們列祖立的、乃是與我們今日在這裡存活之人立的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,4,"耶和華在山上、從火中、面對面與你們說話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,5,"（那時我站在耶和華和你們中間、要將耶和華的話傳給你們、因為你們懼怕那火、沒有上山）")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,6,"說、我是耶和華你的　神、曾將你從埃及地、為奴之家領出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,7,"除了我以外、你不可有別的神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,8,"不可為自己雕刻偶像．也不可作甚麼形像、彷彿上天、下地、和地底下水中的百物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,9,"不可跪拜那些像、也不可事奉他、因為我耶和華你的　神、是忌邪的　神、恨我的、我必追討他的罪、自父及子、直到三四代．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,10,"愛我守我誡命的、我必向他們發慈愛直到千代、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,11,"不可妄稱耶和華你　神的名、因為妄稱耶和華名的、耶和華必不以他為無罪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,12,"當照耶和華你　神所吩咐的、守安息日為聖日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,13,"六日要勞碌作你一切的工、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,14,"但第七日、是向耶和華你　神當守的安息日．這一日你和你的兒女、僕婢、牛、驢、牲畜、並在你城裡寄居的客旅、無論何工都不可作、使你的僕婢可以和你一樣安息。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,15,"你也要記念你在埃及地作過奴僕．耶和華你　神用大能的手、和伸出來的膀臂、將你從那裡領出來、因此、耶和華你的　神吩咐你守安息日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,16,"當照耶和華你　神所吩咐的、孝敬父母、使你得福、並使你的日子在耶和華你　神所賜你的地上、得以長久。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,17,"不可殺人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,18,"不可姦淫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,19,"不可偷盜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,20,"不可作假見證陷害人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,21,"不可貪戀人的妻子、也不可貪圖人的房屋、田地、僕婢、牛、驢、並他一切所有的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,22,"這些話是耶和華在山上、從火中、雲中、幽暗中、大聲曉諭你們全會眾的、此外並沒有添別的話．他就把這話寫在兩塊石版上、交給我了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,23,"那時火焰燒山、你們聽見從黑暗中出來的聲音、你們支派中所有的首領、和長老、都來就近我．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,24,"說、看哪、耶和華我們　神將他的榮光、和他的大能、顯給我們看．我們又聽見他的聲音從火中出來．今日我們得見　神與人說話、人還存活。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,25,"現在這大火將要燒滅我們、我們何必冒死呢．若再聽見耶和華我們　神的聲音、就必死亡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,26,"凡屬血氣的、曾有何人聽見永生　神的聲音從火中出來、像我們聽見、還能存活呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,27,"求你近前去、聽耶和華我們　神所要說的一切話、將他對你說的話都傳給我們、我們就聽從遵行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,28,"你們對我說的話、耶和華都聽見了．耶和華對我說、這百姓的話我聽見了、他們所說的、都是。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,29,"惟願他們存這樣的心、敬畏我、常遵守我的一切誡命、使他們和他們的子孫、永遠得福。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,30,"你去對他們說、你們回帳棚去罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,31,"至於你、可以站在我這裡、我要將一切誡命、律例、典章、傳給你、你要教訓他們、使他們在我賜他們為業的地上遵行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,32,"所以你們要照耶和華你們　神所吩咐的謹守遵行．不可偏離左右。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",5,33,"耶和華你們　神所吩咐你們行的、你們都要去行、使你們可以存活得福、並使你們的日子、在所要承受的地上、得以長久。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,1,"這是耶和華你們　神所吩咐教訓你們的誡命、律例、典章、使你們在所要過去得為業的地上、遵行．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,2,"好叫你和你子子孫孫、一生敬畏耶和華你的　神、謹守他的一切律例、誡命、就是我所吩咐你的、使你的日子得以長久。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,3,"以色列阿、你要聽、要謹守遵行、使你可以在那流奶與蜜之地、得以享福、人數極其增多、正如耶和華你列祖的　神所應許你的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,4,"以色列阿、你要聽．耶和華我們　神是獨一的主。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,5,"你要盡心、盡性、盡力、愛耶和華你的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,6,"我今日所吩咐你的話、都要記在心上．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,7,"也要殷勤教訓你的兒女、無論你坐在家裡、行在路上、躺下、起來、都要談論。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,8,"也要繫在手上為記號、戴在額上為經文。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,9,"又要寫在你房屋的門框上、並你的城門上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,10,"耶和華你的　神、領你進他向你列祖亞伯拉罕、以撒、雅各、起誓應許給你的地．那裡有城邑、又大又美、非你所建造的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,11,"有房屋、裝滿各樣美物、非你所裝滿的．有鑿成的水井、非你所鑿成的．還有葡萄園、橄欖園、非你所栽種的、你喫了而且飽足．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,12,"那時你要謹慎、免得你忘記將你從埃及地為奴之家、領出來的耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,13,"你要敬畏耶和華你的　神、事奉他、指著他的名起誓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,14,"不可隨從別神、就是你們四圍國民的神．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,15,"因為在你們中間的耶和華你　神、是忌邪的　神．惟恐耶和華你　神的怒氣向你發作、就把你從地上除滅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,16,"你們不可試探耶和華你們的　神、像你們在瑪撒那樣試探他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,17,"要留意遵守耶和華你們　神所吩咐的誡命、法度、律例。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,18,"耶和華眼中看為正、看為善的、你都要遵行、使你可以享福、並可以進去、得耶和華向你列祖起誓應許的那美地．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,19,"照耶和華所說的、從你面前攆出你的一切仇敵。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,20,"日後、你的兒子問你說、耶和華我們　神吩咐你們的這些法度、律例、典章、是甚麼意思呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,21,"你就告訴你的兒子說、我們在埃及作過法老的奴僕、耶和華用大能的手、將我們從埃及領出來．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,22,"在我們眼前、將重大可怕的神蹟、奇事、施行在埃及地、和法老並他全家的身上．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,23,"將我們從那裡領出來、要領我們進入他向我們列祖起誓應許之地、把這地賜給我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,24,"耶和華又吩咐我們遵行這一切律例、要敬畏耶和華我們的　神、使我們常得好處、蒙他保全我們的生命、像今日一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",6,25,"我們若照耶和華我們　神所吩咐的一切誡命、謹守遵行、這就是我們的義了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,1,"耶和華你　神領你進入要得為業之地、從你面前趕出許多國民、就是赫人、革迦撒人、亞摩利人、迦南人、比利洗人、希未人、耶布斯人、共七國的民、都比你強大。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,2,"耶和華你　神將他們交給你擊殺、那時你要把他們滅絕淨盡、不可與他們立約、也不可憐恤他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,3,"不可與他們結親、不可將你的女兒嫁他們的兒子、也不可叫你的兒子娶他們的女兒．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,4,"因為他必使你兒子轉離不跟從主、去事奉別神、以致耶和華的怒氣向你們發作、就速速的將你們滅絕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,5,"你們卻要這樣待他們、拆毀他們的祭壇、打碎他們的柱像、砍下他們的木偶、用火焚燒他們雕刻的偶像。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,6,"因為你歸耶和華你　神為聖潔的民、耶和華你　神從地上的萬民中揀選你、特作自己的子民。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,7,"耶和華專愛你們、揀選你們、並非因你們的人數多於別民、原來你們的人數、在萬民中是最少的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,8,"只因耶和華愛你們、又因要守他向你們列祖所起的誓、就用大能的手領你們出來、從為奴之家救贖你們、脫離埃及王法老的手。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,9,"所以你要知道耶和華你的　神、他是　神、是信實的　神、向愛他守他誡命的人、守約施慈愛、直到千代．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,10,"向恨他的人、當面報應他們、將他們滅絕．凡恨他的人必報應他們、決不遲延。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,11,"所以你要謹守遵行我今日所吩咐你的誡命、律例、典章。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,12,"你們果然聽從這些典章、謹守遵行、耶和華你　神就必照他向你列祖所起的誓、守約施慈愛。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,13,"他必愛你、賜福與你、使你人數增多、也必在他向你列祖起誓應許給你的地上、賜福與你身所生的、地所產的、並你的五穀、新酒、和油、以及牛犢、羊羔。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,14,"你必蒙福勝過萬民、你們的男女沒有不能生養的、牲畜也沒有不能生育的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,15,"耶和華必使一切的病症離開你、你所知道埃及各樣的惡疾、他不加在你身上、只加在一切恨你的人身上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,16,"耶和華你　神所要交給你的一切人民、你要將他們除滅、你眼不可顧惜他們．你也不可事奉他們的神、因這必成為你的網羅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,17,"你若心裡說、這些國的民比我更多、我怎能趕出他們呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Deu",7,18,"你不要懼怕他們、要牢牢記念耶和華你　神向法老和埃及全地所行的事．")');
