tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",15,29,"巴沙一作王、就殺了耶羅波安的全家．凡有氣息的、沒有留下一個、都滅盡了、正應驗耶和華藉他僕人示羅人亞希雅所說的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",15,30,"這是因為耶羅波安所犯的罪、使以色列人陷在罪裡、惹動耶和華以色列　神的怒氣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",15,31,"拿答其餘的事、凡他所行的、都寫在以色列諸王記上。")');
