tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",15,33,"猶大王亞撒第三年、亞希雅的兒子巴沙在得撒登基作以色列眾人的王、共二十四年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",15,34,"他行耶和華眼中看為惡的事、行耶羅波安所行的道、犯他使以色列人陷在罪裡的那罪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,1,"耶和華的話臨到哈拿尼的兒子耶戶、責備巴沙、說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,2,"我既從塵埃中提拔你、立你作我民以色列的君、你竟行耶羅波安所行的道、使我民以色列陷在罪裡、惹我發怒．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,3,"我必除盡你和你的家、使你的家像尼八的兒子耶羅波安的家一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,4,"凡屬巴沙的人、死在城中的、必被狗喫、死在田野的、必被空中的鳥喫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,5,"巴沙其餘的事、凡他所行的、和他的勇力、都寫在以色列諸王記上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,6,"巴沙與他列祖同睡、葬在得撒．他兒子以拉接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,7,"耶和華的話臨到哈拿尼的兒子、先知耶戶、責備巴沙、和他的家、因他行耶和華眼中看為惡的一切事、以他手所作的惹耶和華發怒、像耶羅波安的家一樣、又因他殺了耶羅波安的全家。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,8,"猶大王亞撒二十六年、巴沙的兒子以拉在得撒登基作以色列王、共二年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,9,"有管理他一半戰車的臣子心利背叛他．當他在得撒家宰亞雜家裡喝醉的時候、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,10,"心利就進去殺了他、篡了他的位．這是猶大王亞撒二十七年的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,11,"心利一坐王位、就殺了巴沙的全家．連他的親屬、朋友、也沒有留下一個男丁。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,12,"心利這樣滅絕巴沙的全家、正如耶和華藉先知耶戶責備巴沙的話．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,13,"這是因巴沙和他兒子以拉的一切罪、就是他們使以色列人陷在罪裡的那罪、以虛無的神惹耶和華以色列　神的怒氣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,14,"以拉其餘的事、凡他所行的、都寫在以色列諸王記上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,15,"猶大王亞撒二十七年、心利在得撒作王七日。那時民正安營圍攻非利士的基比頓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,16,"民在營中聽說心利背叛、又殺了王、故此以色列眾人當日在營中立元帥暗利作以色列王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,17,"暗利率領以色列眾人、從基比頓上去、圍困得撒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,18,"心利見城破失、就進了王宮的衛所、放火焚燒宮殿、自焚而死．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,19,"這是因他犯罪、行耶和華眼中看為惡的事、行耶羅波安所行的、犯他使以色列人陷在罪裡的那罪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,20,"心利其餘的事和他背叛的情形、都寫在以色列諸王記上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,21,"那時以色列民分為兩半．一半隨從基納的兒子提比尼、要立他作王．一半隨從暗利。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,22,"但隨從暗利的民、勝過隨從基納的兒子提比尼的民．提比尼死了、暗利就作了王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,23,"猶大王亞撒三十一年、暗利登基作以色列王共十二年．在得撒作王六年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,24,"暗利用二他連得銀子、向撒瑪買了撒瑪利亞山．在山上造城、就按著山的原主撒瑪的名、給所造的城起名叫撒瑪利亞。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,25,"暗利行耶和華眼中看為惡的事、比他以前的列王作惡更甚。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,26,"因他行了尼八的兒子耶羅波安所行的、犯他使以色列人陷在罪裡的那罪、以虛無的神惹耶和華以色列　神的怒氣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,27,"暗利其餘的事、和他所顯出的勇力、都寫在以色列諸王記上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,28,"暗利與他列祖同睡、葬在撒瑪利亞．他兒子亞哈接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,29,"猶大王亞撒三十八年、暗利的兒子亞哈登基、作了以色列王．暗利的兒子亞哈、在撒瑪利亞作以色列王二十二年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,30,"暗利的兒子亞哈、行耶和華眼中看為惡的事、比他以前的列王更甚、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,31,"犯了尼八的兒子耶羅波安所犯的罪．他還以為輕、又娶了西頓王謁巴力的女兒耶洗別為妻、去事奉敬拜巴力．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,32,"在撒瑪利亞建造巴力的廟、在廟裡為巴力築壇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,33,"亞哈又作亞舍拉、他所行的、惹耶和華以色列　神的怒氣、比他以前的以色列諸王更甚。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",16,34,"亞哈在位的時候、有伯特利人希伊勒重修耶利哥城．立根基的時候、喪了長子亞比蘭、安門的時候、喪了幼子西割、正如耶和華藉嫩的兒子約書亞所說的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,1,"基列寄居的提斯比人以利亞、對亞哈說、我指著所事奉永生耶和華以色列的　神起誓、這幾年我若不禱告、必不降露不下雨。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,2,"耶和華的話臨到以利亞說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,3,"你離開這裡、往東去、藏在約但河東邊的基立溪旁。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,4,"你要喝那溪裡的水．我已吩咐烏鴉在那裡供養你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,5,"於是以利亞照著耶和華的話、去住在約但河東的基立溪旁。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,6,"烏鴉早晚給他叼餅、和肉來．他也喝那溪裡的水。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,7,"過了些日子、溪水就乾了、因為雨沒有下在地上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,8,"耶和華的話臨到他、說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,9,"你起身往西頓的撒勒法去〔撒勒法與路加福音四章二十六節同〕住在那裡．我已吩咐那裡的一個寡婦供養你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,10,"以利亞就起身往撒勒法去、到了城門、見有一個寡婦在那裡撿柴．以利亞呼叫他、說、求你用器皿取點水來給我喝。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,11,"他去取水的時候、以利亞又呼叫他、說、也求你拿點餅來給我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,12,"他說、我指著永生耶和華你的　神起誓、我沒有餅、罈內只有一把麵、瓶裡只有一點油．我現在找兩根柴、回家要為我和我兒子作餅．我們喫了、死就死罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,13,"以利亞對他說、不要懼怕．可以照你所說的去作罷．只要先為我作一個小餅、拿來給我、然後為你和你的兒子作餅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,14,"因為耶和華以色列的　神如此說、罈內的麵必不減少、瓶裡的油必不缺短、直到耶和華使雨降在地上的日子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,15,"婦人就照以利亞的話去行．他和他家中的人、並以利亞、喫了許多日子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,16,"罈內的麵果不減少、瓶裡的油也不缺短、正如耶和華藉以利亞所說的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,17,"這事以後、作那家主母的婦人、他兒子病了．病得甚重、以致身無氣息。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,18,"婦人對以利亞說、神人哪、我與你何干、你竟到我這裡來、使　神想念我的罪、以致我的兒子死呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,19,"以利亞對他說、把你兒子交給我。以利亞就從婦人懷中將孩子接過來、抱到他所住的樓中、放在自己的床上．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,20,"就求告耶和華、說、耶和華我的　神阿、我寄居在這寡婦的家裡、你就降禍與他、使他的兒子死了麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,21,"以利亞三次伏在孩子的身上、求告耶和華、說、耶和華我的　神阿、求你使這孩子的靈魂、仍入他的身體。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,22,"耶和華應允以利亞的話、孩子的靈魂、仍入他的身體、他就活了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,23,"以利亞將孩子從樓上抱下來、進屋子、交給他母親、說、看哪、你的兒子活了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",17,24,"婦人對以利亞說、現在我知道你是神人、耶和華藉你口所說的話是真的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,1,"過了許久、到第三年、耶和華的話臨到以利亞、說、你去、使亞哈得見你．我要降雨在地上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,2,"以利亞就去、要使亞哈得見他。那時撒瑪利亞有大饑荒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,3,"亞哈將他的家宰俄巴底召了來。俄巴底甚是敬畏耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,4,"耶洗別殺耶和華眾先知的時候、俄巴底將一百個先知藏了、每五十人藏在一個洞裡、拿餅和水供養他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,5,"亞哈對俄巴底說、我們走遍這地、到一切水泉旁、和一切溪邊、或者找得著青草、可以救活騾馬、免得絕了牲畜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,6,"於是二人分地遊行、亞哈獨走一路、俄巴底獨走一路。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,7,"俄巴底在路上恰與以利亞相遇、俄巴底認出他來、就俯伏在地、說、你是我主以利亞不是。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,8,"回答說、是、你去告訴你主人、說、以利亞在這裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,9,"俄巴底說、僕人有甚麼罪、你竟要將我交在亞哈手裡、使他殺我呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,10,"我指著永生耶和華你的　神起誓、無論那一邦、那一國、我主都打發人去找你．若說你沒有在那裡、就必使那邦那國的人起誓、說、實在是找不著你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,11,"現在你說、要去告訴你主人、說、以利亞在這裡．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,12,"恐怕我一離開你、耶和華的靈、就提你到我所不知道的地方去．這樣我去告訴亞哈、他若找不著你、就必殺我．僕人卻是自幼敬畏耶和華的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,13,"耶洗別殺耶和華眾先知的時候、我將耶和華的一百個先知藏了、每五十人藏在一個洞裡、拿餅和水供養他們、豈沒有人將這事告訴我主麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,14,"現在你說、要去告訴你主人、說、以利亞在這裡、他必殺我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,15,"以利亞說、我指著所事奉永生的萬軍之耶和華起誓、我今日必使亞哈得見我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,16,"於是俄巴底去迎著亞哈告訴他．亞哈就去迎著以利亞。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,17,"亞哈見了以利亞、便說、使以色列遭災的就是你麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,18,"以利亞說、使以色列遭災的不是我、乃是你、和你父家、因為你們離棄耶和華的誡命、去隨從巴力。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,19,"現在你當差遣人、招聚以色列眾人、和事奉巴力的那四百五十個先知、並耶洗別所供養事奉亞舍拉的那四百個先知、使他們都上迦密山去見我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,20,"亞哈就差遣人招聚以色列眾人、和先知、都上迦密山。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",18,21,"以利亞前來、對眾民說、你們心持兩意要到幾時呢．若耶和華是　神、就當順從耶和華．若巴力是　神、就當順從巴力．眾民一言不答。")');
