tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",20,35,"有先知的一個門徒、奉耶和華的命對他的同伴說、你打我罷．那人不肯打他．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",20,36,"他就對那人說、你既不聽從耶和華的話、你一離開我、必有獅子咬死你．那人一離開他、果然遇見獅子、把他咬死了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",20,37,"先知的門徒又遇見一個人、對他說、你打我罷．那人就打他、將他打傷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",20,38,"他就去了、用頭巾蒙眼、改換面目、在路旁等候王．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",20,39,"王從那裡經過、他向王呼叫說、僕人在陣上的時候、有人帶了一個人來、對我說、你看守這人、若把他失了、你的性命必代替他的性命、不然、你必交出一他連得銀子來．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",20,40,"僕人正在忙亂之間、那人就不見了．以色列王對他說、你自己定妥了．必照樣判斷你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",20,41,"他急忙除掉蒙眼的頭巾．以色列王就認出他是一個先知．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",20,42,"他對王說、耶和華如此說、因你將我定要滅絕的人放去、你的命就必代替他的命、你的民也必代替他的民。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",20,43,"於是以色列王悶悶不樂的回到撒瑪利亞、進了他的宮。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,1,"這事以後、又有一事、耶斯列人拿伯在耶斯列有一個葡萄園、靠近撒瑪利亞王亞哈的宮．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,2,"亞哈對拿伯說、你將你的葡萄園給我作菜園、因為是靠近我的宮、我就把更好的葡萄園換給你、或是你要銀子、我就按著價值給你．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,3,"拿伯對亞哈說、我敬畏耶和華、萬不敢將我先人留下的產業給你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,4,"亞哈因耶斯列人拿伯說、我不敢將我先人留下的產業給你、就悶悶不樂的回宮、躺在床上、轉臉向內、也不喫飯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,5,"王後耶洗別來問他說、你為甚麼心裡這樣憂悶、不喫飯呢．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,6,"他回答說、因我向耶斯列人拿伯說、你將你的葡萄園給我、我給你價銀、或是你願意、我就把別的葡萄園換給你、他卻說我不將我的葡萄園給你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,7,"王後耶洗別對亞哈說、你現在是治理以色列國不是、只管起來、心裡暢暢快快的喫飯、我必將耶斯列人拿伯的葡萄園給你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,8,"於是託亞哈的名寫信、用王的印印上、送給那些與拿伯同城居住的長老貴冑．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,9,"信上寫著說、你們當宣告禁食、叫拿伯坐在民間的高位上、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,10,"又叫兩個匪徒坐在拿伯對面、作見證告他說、你謗瀆　神和王了．隨後就把他拉出去用石頭打死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,11,"那些與拿伯同城居住的長老貴冑得了耶洗別的信、就照信而行．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,12,"宣告禁食、叫拿伯坐在民間的高位上．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,13,"有兩個匪徒來、坐在拿伯的對面、當著眾民作見證告他說、拿伯謗瀆　神和王了。眾人就把他拉到城外、用石頭打死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,14,"於是打發人去見耶洗別說、拿伯被石頭打死了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,15,"耶洗別聽見拿伯被石頭打死、就對亞哈說、你起來得耶斯列人拿伯不肯為價銀給你的葡萄園罷．現在他已經死了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,16,"亞哈聽見拿伯死了、就起來下去、要得耶斯列人拿伯的葡萄園。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,17,"耶和華的話臨到提斯比人以利亞說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,18,"你起來、去見住撒瑪利亞的以色列王亞哈．他下去要得拿伯的葡萄園．現今正在那園裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,19,"你要對他說、耶和華如此說、你殺了人、又得他的產業麼．又要對他說、耶和華如此說、狗在何處餂拿伯的血、也必在何處餂你的血。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,20,"亞哈對以利亞說、我仇敵阿、你找到我麼．他回答說、我找到你了、因為你賣了自己、行耶和華眼中看為惡的事．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,21,"耶和華說、我必使災禍臨到你、將你除盡、凡屬你的男丁、無論困住的、自由的、都從以色列中剪除．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,22,"我必使你的家像尼八的兒子耶羅波安的家、又像亞希雅的兒子巴沙的家、因為你惹我發怒、又使以色列人陷在罪裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,23,"論到耶洗別、耶和華也說、狗在耶斯列的外郭、必喫耶洗別的肉。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,24,"凡屬亞哈的人、死在城中的、必被狗喫、死在田野的、必被空中的鳥喫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,25,"（從來沒有像亞哈的、因他自賣、行耶和華眼中看為惡的事、受了王後耶洗別的聳動．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,26,"就照耶和華在以色列人面前所趕出的亞摩利人、行了最可憎惡的事、信從偶像。）")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,27,"亞哈聽見這話、就撕裂衣服、禁食、身穿麻布、睡臥也穿著麻布、並且緩緩而行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,28,"耶和華的話臨到提斯比人以利亞說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",21,29,"亞哈在我面前這樣自卑、你看見了麼．因他在我面前自卑、他還在世的時候、我不降這禍、到他兒子的時候、我必降這禍與他的家。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,1,"亞蘭國和以色列國三年沒有爭戰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,2,"到第三年猶大王約沙法下去見以色列王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,3,"以色列王對臣僕說、你們不知道基列的拉末是屬我們的麼、我們豈可靜坐不動、不從亞蘭王手裡奪回來麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,4,"亞哈問約沙法說、你肯同我去攻取基列的拉末麼．約沙法對以色列王說、你我不分彼此、我的民與你的民一樣、我的馬與你的馬一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,5,"約沙法對以色列王說、請你先求問耶和華．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,6,"於是以色列王招聚先知、約有四百人、問他們說、我上去攻取基列的拉末、可以不可以．他們說、可以上去、因為主必將那城交在王的手裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,7,"約沙法說、這裡不是還有耶和華的先知、我們可以求問他麼．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,8,"以色列王對約沙法說、還有一個人、是音拉的兒子米該雅、我們可以託他求問耶和華、只是我恨他、因為他指著我所說的預言、不說吉語、單說凶言．約沙法說、王不必這樣說。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,9,"以色列王就召了一個太監來、說、你快去、將音拉的兒子米該雅召來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,10,"以色列王和猶大王約沙法在撒瑪利亞城門前的空場上、各穿朝服、坐在位上．所有的先知都在他們面前說預言．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,11,"基拿拿的兒子西底家造了兩個鐵角、說、耶和華如此說、你要用這角牴觸亞蘭人、直到將他們滅盡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,12,"所有的先知也都這樣預言說、可以上基列的拉末去、必然得勝、因為耶和華必將那城交在王的手中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,13,"那去召米該雅的使者對米該雅說、眾先知一口同音的都向王說吉言、你不如與他們說一樣的話、也說吉言．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,14,"米該雅說、我指著永生的耶和華起誓、耶和華對我說甚麼、我就說甚麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,15,"米該雅到王面前、王問他說、米該雅阿、我們上去攻取基列的拉末、可以不可以．他回答說、可以上去、必然得勝、耶和華必將那城交在王的手中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,16,"王對他說、我當囑咐你幾次、你纔奉耶和華的名向我說實話呢．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,17,"米該雅說、我看見以色列眾民散在山上、如同沒有牧人的羊群一般．耶和華說、這民沒有主人、他們可以平平安安地各歸各家去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,18,"以色列王對約沙法說、我豈沒有告訴你、這人指著我所說的預言、不說吉語、單說凶言麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,19,"米該雅說、你要聽耶和華的話．我看見耶和華坐在寶座上、天上的萬軍侍立在他左右．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,20,"耶和華說、誰去引誘亞哈上基列的拉末去陣亡呢．這個就這樣說、那個就那樣說．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,21,"隨後有一個神靈出來、站在耶和華面前、說、我去引誘他．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,22,"耶和華問他說、你用何法呢．他說、我去要在他眾先知口中作謊言的靈．耶和華說、這樣、你必能引誘他．你去如此行罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,23,"現在耶和華使謊言的靈入了你這些先知的口．並且耶和華已經命定降禍與你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,24,"基拿拿的兒子西底家前來、打米該雅的臉、說、耶和華的靈從那裡離開我與你說話呢．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,25,"米該雅說、你進嚴密的屋子藏躲的那日、就必看見了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,26,"以色列王說、將米該雅帶回交給邑宰亞們和王的兒子約阿施、說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,27,"王如此說、把這個人下在監裡、使他受苦、喫不飽喝不足、等候我平平安安的回來．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,28,"米該雅說、你若能平平安安的回來、那就是耶和華沒有藉我說這話了．又說、眾民哪、你們都要聽。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,29,"以色列王和猶大王約沙法上基列的拉末去了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,30,"以色列王對約沙法說、我要改裝上陣．你可以仍穿王服．以色列王就改裝上陣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,31,"先是亞蘭王吩咐他的三十二個車兵長說、他們的兵將、無論大小、你們都不可與他們爭戰、只要與以色列王爭戰．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,32,"車兵長看見約沙法、便說、這必是以色列王．就轉過去與他爭戰．約沙法便呼喊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,33,"車兵長見不是以色列王、就轉去不追他了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,34,"有一人隨便開弓、恰巧射入以色列王的甲縫裡．王對趕車的說、我受了重傷．你轉過車來、拉我出陣罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,35,"那日陣勢越戰越猛．有人扶王站在車上、抵擋亞蘭人．到晚上、王就死了．血從傷處流在車中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,36,"約在日落的時候、有號令傳遍軍中、說、各歸本城、各歸本地罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,37,"王既死了、眾人將他送到撒瑪利亞、就葬在那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,38,"又有人把他的車、洗在撒瑪利亞的池旁．（妓女在那裡洗澡）狗來餂他的血、正如耶和華所說的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,39,"亞哈其餘的事、凡他所行的、和他所修造的像牙宮、並所建築的一切城邑、都寫在以色列諸王記上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,40,"亞哈與他列祖同睡．他兒子亞哈謝接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,41,"以色列王亞哈第四年、亞撒的兒子約沙法登基作了猶大王．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,42,"約沙法登基的時候、年三十五歲、在耶路撒冷作王二十五年．他母親名叫阿蘇巴、乃示利希的女兒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,43,"約沙法行他父親亞撒所行的道、不偏離左右、行耶和華眼中看為正的事．只是丘壇還沒有廢去、百姓仍在那裡獻祭燒香。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,44,"約沙法與以色列王和好。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,45,"約沙法其餘的事、和他所顯出的勇力、並他怎樣爭戰、都寫在猶大列王記上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,46,"約沙法將他父親亞撒在世所剩下的孌童、都從國中除去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,47,"那時以東沒有王、有總督治理。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,48,"約沙法製造他施船隻、要往俄斐去、將金子運來．只是沒有去．因為船在以旬迦別破壞了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,49,"亞哈的兒子亞哈謝對約沙法說、容我的僕人和你的僕人坐船同去罷．約沙法卻不肯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,50,"約沙法與列祖同睡、葬在大衛城他列祖的墳地裡．他兒子約蘭接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,51,"猶大王約沙法十七年、亞哈的兒子亞哈謝在撒瑪利亞登基、作以色列王共二年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,52,"他行耶和華眼中看為惡的事、效法他的父母、又行尼八的兒子耶羅波安使以色列人陷在罪裡的事．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "1Ki",22,53,"他照他父親一切所行的、事奉敬拜巴力、惹耶和華以色列　神的怒氣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,1,"亞哈死後、摩押背叛以色列。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,2,"亞哈謝在撒瑪利亞、一日從樓上的欄杆裡掉下來、就病了．於是差遣使者說、你們去問以革倫的神巴力西蔔、我這病能好不能好。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,3,"但耶和華的使者對提斯比人以利亞說、你起來、去迎著撒瑪利亞王的使者、對他們說、你們去問以革倫神巴力西蔔、豈因以色列中沒有　神麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,4,"所以耶和華如此說、你必不下你所上的床、必定要死．以利亞就去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,5,"使者回來見王、王問他們說、你們為甚麼回來呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,6,"使者回答說、有一個人迎著我們來、對我們說、你們回去見差你們來的王、對他說、耶和華如此說、你差人去問以革倫神巴力西蔔、豈因以色列中沒有　神麼．所以你必不下所上的床、必定要死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,7,"王問他們說、迎著你們來告訴你們這話的、是怎樣的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,8,"回答說、他身穿毛衣、腰束皮帶．王說、這必是提斯比人以利亞。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,9,"於是王差遣五十夫長、帶領五十人去見以利亞、他就上到以利亞那裡．以利亞正坐在山頂上。五十夫長對他說、神人哪、王吩咐你下來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,10,"以利亞回答說、我若是神人、願火從天上降下來、燒滅你和你那五十人。於是有火從天上降下來、燒滅五十夫長、和他那五十人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,11,"王第二次差遣一個五十夫長、帶領五十人去見以利亞。五十夫長對以利亞說、神人哪、王吩咐你快快下來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,12,"以利亞回答說、我若是神人、願火從天上降下來、燒滅你和你那五十人。於是　神的火從天上降下來、燒滅五十夫長、和他那五十人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,13,"王第三次差遣一個五十夫長、帶領五十人去。這五十夫長上去雙膝跪在以利亞面前、哀求他、說、神人哪、願我的性命、和你這五十個僕人的性命、在你眼前看為寶貴。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,14,"已經有火從天上降下來、燒滅前兩次來的五十夫長、和他們各自帶的五十人．現在願我的性命在你眼前看為寶貴。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,15,"耶和華的使者對以利亞說、你同著他下去、不要怕他．以利亞就起來、同著他下去見王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,16,"對王說、耶和華如此說、你差人去問以革倫神巴力西蔔、豈因以色列中沒有　神可以求問麼．所以你必不下所上的床、必定要死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,17,"亞哈謝果然死了、正如耶和華藉以利亞所說的話．因他沒有兒子、他兄弟約蘭接續他作王、正在猶大王約沙法的兒子約蘭第二年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",1,18,"亞哈謝其餘所行的事、都寫在以色列諸王記上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,1,"耶和華要用旋風接以利亞昇天的時候、以利亞與以利沙從吉甲前往。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,2,"以利亞對以利沙說、耶和華差我往伯特利去、你可以在這裡等候．以利沙說、我指著永生的耶和華、又敢在你面前起誓、我必不離開你。於是二人下到伯特利。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,3,"住伯特利的先知門徒出來見以利沙、對他說、耶和華今日要接你的師傅離開你、你知道不知道。他說、我知道．你們不要作聲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,4,"以利亞對以利沙說、耶和華差遣我往耶利哥去、你可以在這裡等候．以利沙說、我指著永生的耶和華、又敢在你面前起誓、我必不離開你。於是二人到了耶利哥。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,5,"住耶利哥的先知門徒就近以利沙、對他說、耶和華今日要接你的師傅離開你、你知道不知道。他說、我知道．你們不要作聲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,6,"以利亞對以利沙說、耶和華差遣我往約但河去、你可以在這裡等候．以利沙說、我指著永生的耶和華、又敢在你面前起誓、我必不離開你。於是二人一同前往。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,7,"有先知門徒去了五十人、遠遠的站在他們對面．二人在約但河邊站住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,8,"以利亞將自己的外衣捲起來、用以打水、水就左右分開、二人走乾地而過。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,9,"過去之後、以利亞對以利沙說、我未曾被接去離開你、你要我為你作甚麼只管求我。以利沙說、願感動你的靈加倍的感動我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,10,"以利亞說、你所求的難得、雖然如此、我被接去離開你的時候、你若看見我、就必得著．不然、必得不著了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,11,"他們正走著說話、忽有火車火馬、將二人隔開、以利亞就乘旋風昇天去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,12,"以利沙看見、就呼叫說、我父阿、我父阿、以色列的戰車馬兵阿。以後不再見他了．於是以利沙把自己的衣服撕為兩片。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,13,"他拾起以利亞身上掉下來的外衣、回去站在約但河邊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,14,"他用以利亞身上掉下來的外衣、打水、說、耶和華、以利亞的　神在哪裡呢。打水之後、水也左右分開、以利沙就過來了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,15,"住耶利哥的先知門徒從對面看見他、就說、感動以利亞的靈感動以利沙了。他們就來迎接他、在他面前俯伏於地、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,16,"對他說、僕人們這裡有五十個壯士．求你容他們去尋找你師傅、或者耶和華的靈將他提起來、投在某山某谷。以利沙說、你們不必打發人去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,17,"他們再三催促他、他難以推辭、就說、你們打發人去罷。他們便打發五十人去、尋找了三天、也沒有找著。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,18,"以利沙仍然在耶利哥、等候他們回到他那裡．他對他們說、我豈沒有告訴你們、不必去麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,19,"耶利哥城的人對以利沙說、這城的地勢美好、我主看見了．只是水惡劣、土產不熟而落。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,20,"以利沙說、你們拿一個新瓶來裝鹽給我、他們就拿來給他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,21,"他出到水源、將鹽倒在水中、說、耶和華如此說、我治好了這水、從此必不再使人死、也不再使地土不生產。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,22,"於是那水治好了、直到今日、正如以利沙所說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,23,"以利沙從那裡上伯特利去．正上去的時候、有些童子從城裡出來、戲笑他、說、禿頭的上去罷、禿頭的上去罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,24,"他回頭看見、就奉耶和華的名咒詛他們。於是有兩個母熊從林中出來、撕裂他們中間四十二個童子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",2,25,"以利沙從伯特利上迦密山、又從迦密山回到撒瑪利亞。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,1,"猶大王約沙法十八年、亞哈的兒子約蘭在撒瑪利亞登基、作了以色列王十二年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,2,"他行耶和華眼中看為惡的事、但不至像他父母所行的、因為除掉他父所造巴力的柱像。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,3,"然而他貼近尼八的兒子耶羅波安使以色列人陷在罪裡的那罪、總不離開。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,4,"摩押王米沙牧養許多羊．每年將十萬羊羔的毛、和十萬公綿羊的毛、給以色列王進貢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,5,"亞哈死後、摩押王背叛以色列王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,6,"那時約蘭王出撒瑪利亞、數點以色列眾人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,7,"前行的時候、差人去見猶大王約沙法、說、摩押王背叛我．你肯同我去攻打摩押麼。他說、我肯上去．你我不分彼此、我的民與你的民一樣、我的馬與你的馬一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,8,"約蘭說、我們從那條路上去呢。回答說、從以東曠野的路上去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,9,"於是以色列王、和猶大王、並以東王、都一同去繞行七日的路程．軍隊和所帶的牲畜沒有水喝。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,10,"以色列王說、哀哉、耶和華招聚我們這三王、乃要交在摩押人的手裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,11,"約沙法說、這裡不是有耶和華的先知麼．我們可以託他求問耶和華。以色列王的一個臣子回答說、這裡有沙法的兒子以利沙、就是從前服事以利亞的。〔原文作倒水在以利亞手上的〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,12,"約沙法說、他必有耶和華的話。於是以色列王、和約沙法、並以東王、都下去見他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,13,"以利沙對以色列王說、我與你何干．去問你父親的先知、和你母親的先知罷。以色列王對他說、不要這樣說、耶和華招聚我們這三王、乃要交在摩押人的手裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,14,"以利沙說、我指著所事奉永生的萬軍耶和華起誓、我若不看猶大王約沙法的情面、必不理你、不顧你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,15,"現在你們給我找一個彈琴的來。彈琴的時候、耶和華的靈〔原文作手〕就降在以利沙身上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,16,"他便說、耶和華如此說、你們要在這谷中滿處挖溝。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,17,"因為耶和華如此說、你們雖不見風、不見雨、這谷必滿了水、使你們、和牲畜有水喝。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,18,"在耶和華眼中這還算為小事．他也必將摩押人交在你們手中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,19,"你們必攻破一切堅城美邑、砍伐各種佳樹、塞住一切水泉、用石頭糟踏一切美田。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,20,"次日早晨、約在獻祭的時候、有水從以東而來、遍地就滿了水。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,21,"摩押眾人聽見這三王上來、要與他們爭戰、凡能頂盔貫甲的、無論老少、盡都聚集、站在邊界上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,22,"次日早晨、日光照在水上、摩押人起來、看見對面水紅如血．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,23,"就說、這是血阿．必是三王互相擊殺、俱都滅亡．摩押人哪、我們現在去搶奪財物罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,24,"摩押人到了以色列營、以色列人就起來攻打他們、以致他們在以色列人面前逃跑．以色列人往前追殺摩押人、直殺入摩押的境內．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,25,"拆毀摩押的城邑．各人拋石填滿一切美田、塞住一切水泉、砍伐各種佳樹、只剩下吉珥哈列設的石牆．甩石的兵在四圍攻打那城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,26,"摩押王見陣勢甚大、難以對敵、就率領七百拿刀的兵、要衝過陣去到以東王那裡、卻是不能．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",3,27,"便將那應當接續他作王的長子、在城上獻為燔祭。以色列人遭遇耶和華的大怒。〔或作招人痛恨〕於是三王離開摩押王、各回本國去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,1,"有一個先知門徒的妻、哀求以利沙說、你僕人我丈夫死了．他敬畏耶和華是你所知道的．現在有債主來、要取我兩個兒子作奴僕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,2,"以利沙問他說、我可以為你作甚麼呢、你告訴我．你家裡有甚麼、他說、婢女家中、除了一瓶油之外、沒有甚麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,3,"以利沙說、你去、向你眾鄰舍借空器皿．不要少借、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,4,"回到家裡、關上門、你和你兒子在裡面、將油倒在所有的器皿裡、倒滿了的放在一邊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,5,"於是婦人離開以利沙去了、關上門、自己和兒子在裡面．兒子把器皿拿來、他就倒油．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,6,"器皿都滿了、他對兒子說、再給我拿器皿來。兒子說、再沒有器皿了、油就止住了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,7,"婦人去告訴神人．神人說、你去賣油還債、所剩的、你和你兒子可以靠著度日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,8,"一日以利沙走到書念．在那裡有一個大戶的婦人、強留他喫飯。此後、以利沙每從那裡經過、就進去喫飯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,9,"婦人對丈夫說、我看出那常從我們這裡經過的、是聖潔的神人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,10,"我們可以為他在牆上蓋一間小樓．在其中安放床榻、桌子、椅子、燈臺．他來到我們這裡、就可以住在其間。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,11,"一日、以利沙來到那裡、就進了那樓躺臥。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,12,"以利沙吩咐僕人基哈西說、你叫這書念婦人來．他就把婦人叫了來、婦人站在以利沙面前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,13,"以利沙吩咐僕人說、你對他說、你既為我們費了許多心思、可以為你作甚麼呢．你向王、或元帥、有所求的沒有．他回答說、我在我本鄉安居無事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,14,"以利沙對僕人說、究竟當為他作甚麼呢。基哈西說、他沒有兒子、他丈夫也老了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,15,"以利沙說、再叫他來．於是叫了他來、他就站在門口。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,16,"以利沙說、明年到這時候、你必抱一個兒子。他說、神人、我主阿、不要那樣欺哄婢女。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,17,"婦人果然懷孕、到了那時候、生了一個兒子、正如以利沙所說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,18,"孩子漸漸長大、一日到他父親和收割的人那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,19,"他對父親說、我的頭阿、我的頭阿。他父親對僕人說、把他抱到他母親那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,20,"僕人抱去、交給他母親、孩子坐在母親的膝上、到晌午就死了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,21,"他母親抱他上了樓、將他放在神人的床上、關上門出來、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,22,"呼叫他丈夫說、你叫一個僕人給我牽一匹驢來．我要快快的去見神人、就回來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,23,"丈夫說、今日不是月朔、也不是安息日．你為何要去見他呢。婦人說、平安無事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,24,"於是備上驢、對僕人說、你快快趕著走．我若不吩咐你、就不要遲慢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,25,"婦人就往迦密山去見神人。神人遠遠的看見他、對僕人基哈西說、看哪、書念的婦人來了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,26,"你跑去迎接他、問他說、你平安麼．你丈夫平安麼．孩子平安麼．他說、平安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,27,"婦人上了山、到神人那裡、就抱住神人的腳。基哈西前來要推開他、神人說、由他罷、因為他心裡愁苦．耶和華向我隱瞞、沒有指示我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,28,"婦人說、我何嘗向我主求過兒子呢。我豈沒有說過、不要欺哄我麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,29,"以利沙吩咐基哈西說、你束上腰、手拿我的杖前去．若遇見人、不要向他問安．人若向你問安、也不要回答．要把我的杖放在孩子臉上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,30,"孩子的母親說、我指著永生的耶和華、又敢在你面前起誓、我必不離開你。於是以利沙起身、隨著他去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,31,"基哈西先去、把杖放在孩子臉上、卻沒有聲音、也沒有動靜．基哈西就迎著以利沙回來、告訴他說、孩子還沒有醒過來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,32,"以利沙來到、進了屋子、看見孩子死了、放在自己的床上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,33,"他就關上門、只有自己和孩子在裡面、他便祈禱耶和華．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,34,"上床伏在孩子身上、口對口、眼對眼、手對手．既伏在孩子身上、孩子的身體就漸漸溫和了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,35,"然後他下來、在屋裡來往走了一趟．又上去伏在孩子身上．孩子打了七個噴嚏、就睜開眼睛了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,36,"以利沙叫基哈西說、你叫這書念婦人來。於是叫了他來。以利沙說、將你兒子抱起來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,37,"婦人就進來、在以利沙腳前俯伏於地、抱起他兒子出去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,38,"以利沙又來到吉甲．那地正有饑荒．先知門徒坐在他面前．他吩咐僕人說、你將大鍋放在火上、給先知門徒熬湯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,39,"有一個人去到田野掐菜、遇見一棵野瓜藤、就摘了一兜野瓜、回來切了、擱在熬湯的鍋中．因為他們不知道是甚麼東西．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,40,"倒出來給眾人喫、喫的時候、都喊叫說、神人哪、鍋中有致死的毒物。所以眾人不能喫了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,41,"以利沙說、拿點麵來、就把麵撒在鍋中、說、倒出來、給眾人喫罷．鍋中就沒有毒了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,42,"有一個人從巴力沙利沙來、帶著初熟大麥作的餅二十個、並新穗子、裝在口袋裡、送給神人．神人說、把這些給眾人喫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,43,"僕人說、這一點豈可擺給一百人喫呢。以利沙說、你只管給眾人喫罷．因為耶和華如此說、眾人必喫了、還剩下。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",4,44,"僕人就擺在眾人面前、他們喫了、果然還剩下、正如耶和華所說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,1,"亞蘭王的元帥乃縵、在他主人面前為尊為大、因耶和華曾藉他使亞蘭人得勝．他又是大能的勇士、只是長了大痲瘋。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,2,"先前亞蘭人成群的出去、從以色列國擄了一個小女子．這女子就服事乃縵的妻。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,3,"他對主母說、巴不得我主人去見撒瑪利亞的先知．必能治好他的大痲瘋。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,4,"乃縵進去、告訴他主人說、以色列國的女子如此如此說。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,5,"亞蘭王說、你可以去、我也達信於以色列王。於是乃縵帶銀子十他連得、金子六千舍客勒、衣裳十套、就去了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,6,"且帶信給以色列王、信上說、我打發臣僕乃縵去見你、你接到這信、就要治好他的大痲瘋。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,7,"以色列王看了信、就撕裂衣服、說、我豈是　神、能使人死、使人活呢。這人竟打發人來、叫我治好他的大痲瘋。你們看一看、這人何以尋隙攻擊我呢．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,8,"神人以利沙聽見以色列王撕裂衣服、就打發人去見王、說、你為甚麼撕了衣服呢．可使那人到我這裡來、他就知道以色列中有先知了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,9,"於是乃縵帶著車馬到了以利沙的家、站在門前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,10,"以利沙打發一個使者、對乃縵說、你去在約但河中沐浴七回、你的肉就必復原、而得潔淨。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,11,"乃縵卻發怒走了、說、我想他必定出來見我、站著求告耶和華他　神的名、在患處以上搖手、治好這大痲瘋。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,12,"大馬色的河、亞罷拿和法珥法豈不比以色列的一切水更好麼．我在那裡沐浴不得潔淨麼。於是氣忿忿的轉身去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,13,"他的僕人進前來、對他說、我父阿、先知若吩咐你作一件大事、你豈不作麼．何況說你去沐浴、而得潔淨呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,14,"於是乃縵下去、照著神人的話．在約但河裡沐浴七回、他的肉復原、好像小孩子的肉、他就潔淨了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,15,"乃縵帶著一切跟隨他的人、回到神人那裡、站在他面前說、如今我知道、除了以色列之外、普天下沒有　神．現在求你收點僕人的禮物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,16,"以利沙說、我指著所事奉永生的耶和華起誓、我必不受。乃縵再三的求他、他卻不受。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,17,"乃縵說、你若不肯受、請將兩騾子馱的土賜給僕人．從今以後、僕人必不再將燔祭、或平安祭、獻與別神、只獻給耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,18,"惟有一件事、願耶和華饒恕你僕人．我主人進臨門廟叩拜的時候、我用手攙他在臨門廟、我也屈身．我在臨門廟屈身的這事、願耶和華饒恕我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,19,"以利沙對他說、你可以平平安安的回去。乃縵就離開他去了、走了不遠。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,20,"神人以利沙的僕人、基哈西心裡說、我主人不願從這亞蘭人乃縵手裡受他帶來的禮物．我指著永生的耶和華起誓、我必跑去追上他、向他要些。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,21,"於是基哈西追趕乃縵。乃縵看見有人追趕、就急忙下車迎著他、說、都平安麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,22,"說、都平安。我主人打發我來說、剛纔有兩個少年人、是先知門徒、從以法蓮山地來見我．請你賜他們一他連得銀子、兩套衣裳。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,23,"乃縵說、請受二他連得．再三的請受、便將二他連得銀子裝在兩個口袋裡、又將兩套衣裳交給兩個僕人、他們就在基哈西前頭抬著走。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,24,"到了山岡、基哈西從他們手中接過來、放在屋裡、打發他們回去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,25,"基哈西進去、站在他主人面前。以利沙問他說、基哈西你從那裡來．回答說、僕人沒有往那裡去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,26,"以利沙對他說、那人下車轉回迎你的時候、我的心豈沒有去呢。這豈是受銀子、衣裳、買橄欖園、葡萄園、牛羊、僕婢的時候呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",5,27,"因此乃縵的大痲瘋必沾染你、和你的後裔、直到永遠。基哈西從以利沙面前退出去、就長了大痲瘋、像雪那樣白。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,1,"先知門徒對以利沙說、看哪、我們同你所住的地方過於窄小。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,2,"求你容我們往約但河去、各人從那裡取一根木料、建造房屋居住。他說、你們去罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,3,"有一人說、求你與僕人同去．回答說、我可以去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,4,"於是以利沙與他們同去。到了約但河、就砍伐樹木。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,5,"有一人砍樹的時候、斧頭掉在水裡、他就呼叫說、哀哉、我主阿、這斧子是借的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,6,"神人問說、掉在那裡了。他將那地方指給以利沙看。以利沙砍了一根木頭、拋在水裡、斧頭就漂上來了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,7,"以利沙說、拿起來罷。那人就伸手、拿起來了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,8,"亞蘭王與以色列人爭戰、和他的臣僕商議說、我要在某處某處安營。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,9,"神人打發人去見以色列王、說、你要謹慎、不要從某處經過．因為亞蘭人從那裡下來了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,10,"以色列王差人去窺探神人所告訴所警戒他去的地方、就防備未受其害、不止一兩次。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,11,"亞蘭王因這事心裡驚疑、召了臣僕來、對他們說、我們這裡有誰幫助以色列王．你們不指給我麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,12,"有一個臣僕說、我主、我王．無人幫助他、只有以色列中的先知以利沙、將王在臥房所說的話、告訴以色列王了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,13,"王說、你們去探他在那裡、我好打發人去捉拿他。有人告訴王說、他在多坍。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,14,"王就打發車馬、和大軍、往那裡去、夜間到了、圍困那城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,15,"神人的僕人清早起來出去、看見車馬軍兵圍困了城。僕人對神人說、哀哉、我主阿、我們怎樣行纔好呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,16,"神人說、不要懼怕、與我們同在的、比與他們同在的更多。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,17,"以利沙禱告說、耶和華阿、求你開這少年人的眼目、使他能看見。耶和華開他的眼目、他就看見滿山有火車火馬圍繞以利沙。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,18,"敵人下到以利沙那裡、以利沙禱告耶和華說、求你使這些人的眼目昏迷。耶和華就照以利沙的話、使他們的眼目昏迷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,19,"以利沙對他們說、這不是那道、也不是那城．你們跟我去、我必領你們到所尋找的人那裡。於是領他們到了撒瑪利亞。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,20,"他們進了撒瑪利亞、以利沙禱告說、耶和華阿、求你開這些人的眼目、使他們能看見。耶和華開他們的眼目、他們就看見了．不料、是在撒瑪利亞的城中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,21,"以色列王見了他們、就問以利沙說、我父阿、我可以擊殺他們麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,22,"回答說、不可擊殺他們．就是你用刀用弓擄來的、豈可擊殺他們麼。〔或作也不可擊殺何況這些人呢〕當在他們面前設擺飲食、使他們喫喝回到他們的主人那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,23,"王就為他們預備了許多食物．他們喫喝完了、打發他們回到他們主人那裡。從此、亞蘭軍不再犯以色列境了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,24,"此後、亞蘭王便哈達聚集他的全軍、上來圍困撒瑪利亞。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,25,"於是撒瑪利亞被圍困．有饑荒、甚至一個驢頭值銀八十舍客勒、二升鴿子糞值銀五舍客勒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,26,"一日、以色列王在城上經過、有一個婦人向他呼叫說、我主、我王阿、求你幫助。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,27,"王說、耶和華不幫助你、我從何處幫助你．是從禾場、是從酒醡呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,28,"王問婦人說、你有甚麼苦處。他回答說、這婦人對我說、將你的兒子取來、我們今日可以喫、明日可以喫我的兒子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,29,"我們就煮了我的兒子喫了。次日我對他說、要將你的兒子取來、我們可以喫．他卻將他的兒子藏起來了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,30,"王聽見婦人的話、就撕裂衣服．（王在城上經過）百姓看見王貼身穿著麻衣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,31,"王說、我今日若容沙法的兒子以利沙的頭仍在他項上、願　神重重的降罰與我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,32,"那時以利沙正坐在家中、長老也與他同坐．王打發一個伺候他的人去．他還沒有到、以利沙對長老說、你們看這兇手之子、打發人來斬我的頭．你們看著使者來到、就關上門、用門將他推出去．在他後頭不是有他主人腳步的響聲麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",6,33,"正說話的時候、使者來到．王也到了、說、這災禍是從耶和華那裡來的．我何必再仰望耶和華呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,1,"以利沙說、你們要聽耶和華的話．耶和華如此說明日約到這時候、在撒瑪利亞城門口、一細亞細麵要賣銀一舍客勒、二細亞大麥也要賣銀一舍客勒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,2,"有一個攙扶王的軍長、對神人說、即便耶和華使天開了窗戶、也不能有這事。以利沙說、你必親眼看見、卻不得喫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,3,"在城門那裡有四個長大痲瘋的人．他們彼此說、我們為何坐在這裡等死呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,4,"我們若說、進城去罷、城裡有饑荒、必死在那裡．若在這裡坐著不動、也必是死。來罷、我們去投降亞蘭人的軍隊．他們若留我們的活命、就活著．若殺我們、就死了罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,5,"黃昏的時候、他們起來往亞蘭人的營盤去．到了營邊、不見一人在那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,6,"因為主使亞蘭人的軍隊聽見車馬的聲音、是大軍的聲音．他們就彼此說、這必是以色列王賄買赫人的諸王、和埃及人的諸王、來攻擊我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,7,"所以在黃昏的時候他們起來逃跑、撇下帳棚、馬、驢、營盤照舊、只顧逃命。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,8,"那些長大痲瘋的到了營邊、進了帳棚、喫了、喝了、且從其中拿出金銀、和衣服來、去收藏了．回來、又進了一座帳棚、從其中拿出財物來、去收藏了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,9,"那時他們彼此說、我們所作的不好．今日是有好信息的日子、我們竟不作聲．若等到天亮、罪必臨到我們．來罷、我們與王家報信去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,10,"他們就去叫守城門的、告訴他們說、我們到了亞蘭人的營、不見一人在那裡、也無人聲、只有拴著的馬、和驢、帳棚都照舊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,11,"守城門的、叫了眾守門的人來．他們就進去、與王家報信。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,12,"王夜間起來、對臣僕說、我告訴你們亞蘭人向我們如何行。他們知道我們飢餓、所以離營、埋伏在田野、說、以色列人出城的時候、我們就活捉他們、得以進城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,13,"有一個臣僕對王說、我們不如用城裡剩下之馬中的五匹馬、（馬和城裡剩下的以色列人都是一樣、快要滅絕）打發人去窺探。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,14,"於是取了兩輛車和馬．王差人去追尋亞蘭軍、說、你們去窺探窺探。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,15,"他們就追尋到約但河、看見滿道上都是亞蘭人急跑時丟棄的衣服器具。使者就回來報告王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,16,"眾人就出去、擄掠亞蘭人的營盤。於是一細亞細麵賣銀一舍客勒、二細亞大麥也賣銀一舍客勒、正如耶和華所說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,17,"王派攙扶他的那軍長、在城門口彈壓．眾人在那裡將他踐踏、他就死了、正如神人在王下來見他的時候所說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,18,"神人曾對王說、明日約到這時候、在撒瑪利亞城門口、二細亞大麥要賣銀一舍客勒、一細亞細麵也要賣銀一舍客勒．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,19,"那軍長對神人說、即便耶和華使天開了窗戶、也不能有這事。神人說、你必親眼看見、卻不得喫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",7,20,"這話果然應驗在他身上．因為眾人在城門口將他踐踏、他就死了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,1,"以利沙曾對所救活之子的那婦人說、你和你的全家、要起身往你可住的地方去住．因為耶和華命饑荒降在這地七年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,2,"婦人就起身、照神人的話、帶著全家往非利士地去、住了七年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,3,"七年完了、那婦人從非利士地回來．就出去為自己的房屋田地哀告王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,4,"那時王正與神人的僕人基哈西說、請你將以利沙所行的一切大事告訴我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,5,"基哈西告訴王以利沙如何使死人復活、恰巧以利沙所救活他兒子的那婦人、為自己的房屋田地來哀告王。基哈西說、我主我王、這就是那婦人、這是他的兒子、就是以利沙所救活的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,6,"王問那婦人、他就把那事告訴王。於是王為他派一個太監、說、凡屬這婦人的、都還給他、自從他離開本地、直到今日、他田地的出產、也都還給他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,7,"以利沙來到大馬色．亞蘭王便哈達正患病．有人告訴王說、神人來到這裡了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,8,"王就吩咐哈薛說、你帶著禮物去見神人、託他求問耶和華、我這病能好不能好。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,9,"於是哈薛用四十個駱駝、馱著大馬色的各樣美物為禮物、去見以利沙．到了他那裡、站在他面前、說、你兒子亞蘭王便哈達、打發我來見你．他問說、我這病能好不能好。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,10,"以利沙對哈薛說、你回去告訴他說、這病必能好．但耶和華指示我他必要死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,11,"神人定睛看著哈薛、甚致他慚愧．神人就哭了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,12,"哈薛說、我主為甚麼哭．回答說、因為我知道你必苦害以色列人．用火焚燒他們的保障、用刀殺死他們的壯丁、摔死他們的嬰孩、剖開他們的孕婦。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,13,"哈薛說、你僕人算甚麼、不過是一條狗．焉能行這大事呢。以利沙回答說、耶和華指示我、你必作亞蘭王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,14,"哈薛離開以利沙、回去見他的主人．主人問他說、以利沙對你說甚麼．回答說、他告訴我你必能好。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,15,"次日哈薛拿被窩、浸在水中、蒙住王的臉、王就死了．於是哈薛篡了他的位。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,16,"以色列王亞哈的兒子約蘭第五年、猶大王約沙法還在位的時候、約沙法的兒子約蘭登基、作了猶大王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,17,"約蘭登基的時候、年三十二歲．在耶路撒冷作王八年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,18,"他行以色列諸王所行的、與亞哈家一樣．因為他娶了亞哈的女兒為妻、行耶和華眼中看為惡的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,19,"耶和華卻因他僕人大衛的緣故、仍不肯滅絕猶大、照他所應許大衛的話、永遠賜燈光與他的子孫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,20,"約蘭年間、以東人背叛猶大、脫離他的權下、自己立王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,21,"約蘭率領所有的戰車、往撒益去．夜間起來、攻打圍困他的以東人和車兵長．猶大兵就逃跑、各回各家去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,22,"這樣、以東人背叛猶大、脫離他的權下、直到今日。那時立拿人也背叛了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,23,"約蘭其餘的事、凡他所行的都寫在猶大列王記上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,24,"約蘭與他列祖同睡、葬在大衛城、他列祖的墳地裡．他兒子亞哈謝接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,25,"以色列王亞哈的兒子約蘭十二年、猶大王約蘭的兒子亞哈謝登基。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,26,"他登基的時候、年二十二歲、在耶路撒冷作王一年．他母親名叫亞他利雅、是以色列王暗利的孫女。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,27,"亞哈謝效法亞哈家行耶和華眼中看為惡的事、與亞哈家一樣．因為他是亞哈家的女婿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,28,"他與亞哈的兒子約蘭、同往基列的拉末去、與亞蘭王哈薛爭戰．亞蘭人打傷了約蘭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",8,29,"約蘭王回到耶斯列、醫治在拉末與亞蘭王哈薛打仗的時候所受的傷。猶大王約蘭的兒子亞哈謝、因為亞哈的兒子約蘭病了、就下到耶斯列看望他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,1,"先知以利沙叫了一個先知門徒來、吩咐他說、你束上腰、手拿這瓶膏油、往基列的拉末去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,2,"到了那裡、要尋找寧示的孫子、約沙法的兒子耶戶。使他從同僚中起來、帶他進嚴密的屋子、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,3,"將瓶裡的膏油倒在他頭上、說、耶和華如此說、我膏你作以色列王。說完了、就開門逃跑、不要遲延。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,4,"於是那少年先知往基列的拉末去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,5,"到了那裡、看見眾軍長都坐著、就說、將軍哪、我有話對你說。耶戶說、我們眾人裡、你要對那一個說呢．回答說、將軍哪、我要對你說。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,6,"耶戶就起來、進了屋子、少年人將膏油倒在他頭上、對他說、耶和華以色列的　神如此說、我膏你作耶和華民以色列的王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,7,"你要擊殺你主人亞哈的全家、我好在耶洗別身上伸我僕人眾先知、和耶和華一切僕人流血的冤。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,8,"亞哈全家必都滅亡．凡屬亞哈的男丁、無論是困住的、自由的、我必從以色列中剪除．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,9,"使亞哈的家像尼八兒子耶羅波安的家、又像亞希雅兒子巴沙的家。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,10,"耶洗別必在耶斯列田裡被狗所喫、無人葬埋。說完了、少年人就開門逃跑了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,11,"耶戶出來、回到他主人的臣僕那裡．有一人問他說、平安麼．這狂妄的人來見你有甚麼事呢．回答說、你們認得那人、也知道他說甚麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,12,"他們說、這是假話．你據實的告訴我們．回答說、他如此如此對我說．他說、耶和華如此說、我膏你作以色列王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,13,"他們就急忙、各將自己的衣服鋪在上層台階、使耶戶坐在其上．他們吹角、說、耶戶作王了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,14,"這樣、寧示的孫子、約沙法的兒子耶戶、背叛約蘭。先是約蘭、和以色列眾人、因為亞蘭王哈薛的緣故、把守基列的拉末．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,15,"但約蘭王回到耶斯列、醫治與亞蘭王哈薛打仗所受的傷。耶戶說、若合你們的意思、就不容人逃出城往耶斯列報信去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,16,"於是耶戶坐車、往耶斯列去．因為約蘭病臥在那裡。猶大王亞哈謝已經下去看望他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,17,"有一個守望的人、站在耶斯列的樓上、看見耶戶帶著一群人來、就說、我看見一群人。約蘭說、打發一個騎馬的去、迎接他們、問說、平安不平安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,18,"騎馬的就去、迎接耶戶、說、王問說、平安不平安。耶戶說、平安不平安與你何干．你轉在我後頭罷。守望的人又說、使者到了他們那裡、卻不回來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,19,"王又打發一個騎馬的去．這人到了他們那裡、說、王問說、平安不平安。耶戶說、平安不平安與你何干．你轉在我後頭罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,20,"守望的人又說、他到了他們那裡、也不回來．車趕得甚猛、像寧示的孫子耶戶的趕法。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,21,"約蘭吩咐說、套車．人就給他套車。以色列王約蘭、和猶大王亞哈謝、各坐自己的車、出去迎接耶戶、在耶斯列人拿伯的田那裡遇見他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,22,"約蘭見耶戶就說、耶戶阿、平安麼。耶戶說、你母親耶洗別的淫行邪術這樣多、焉能平安呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,23,"約蘭就轉車逃跑、對亞哈謝說、亞哈謝阿、反了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,24,"耶戶開滿了弓、射中約蘭的脊背．箭從心窩穿出、約蘭就僕倒在車上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,25,"耶戶對他的軍長畢甲說、你把他拋在耶斯列人拿伯的田間．你當追想、你我一同坐車跟隨他父亞哈的時候、耶和華對亞哈所說的預言、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,26,"說、我昨日看見拿伯的血、和他眾子的血．我必在這塊田上報應你．這是耶和華說的．現在你要照著耶和華的話、把他拋在這田間。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,27,"猶大王亞哈謝見這光景、就從園亭之路逃跑。耶戶追趕他說、把這人也殺在車上。到了靠近以伯蓮姑珥的坡上擊傷了他。他逃到米吉多、就死在那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,28,"他的臣僕用車將他的屍首送到耶路撒冷、葬在大衛城、他自己的墳墓裡、與他列祖同葬。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,29,"亞哈謝登基、作猶大王的時候、是在亞哈的兒子約蘭第十一年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,30,"耶戶到了耶斯列、耶洗別聽見、就擦粉、梳頭、從窗戶裡往外觀看。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,31,"耶戶進門的時候、耶洗別說、殺主人的心利阿、平安麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,32,"耶戶抬頭向窗戶觀看、說、誰順從我。有兩三個太監從窗戶往外看他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,33,"耶戶說、把他扔下來．他們就把他扔下來．他的血濺在牆上、和馬上．於是把他踐踏了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,34,"耶戶進去、喫了喝了、吩咐說、你們把這被咒詛的婦人葬埋了．因為他是王的女兒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,35,"他們就去葬埋他．只尋得他的頭骨、和腳、並手掌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,36,"他們回去告訴耶戶。耶戶說、這正應驗耶和華、藉他僕人提斯比人以利亞所說的話、說、在耶斯列田間、狗必喫耶洗別的肉．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",9,37,"耶洗別的屍首必在耶斯列田間如同糞土、甚至人不能說、這是耶洗別。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,1,"亞哈有七十個兒子在撒瑪利亞。耶戶寫信、送到撒瑪利亞、通知耶斯列的首領、就是長老、和教養亞哈眾子的人、說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,2,"你們那裡、既有你們主人的眾子、和車馬、器械、堅固城、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,3,"接了這信、就可以在你們主人的眾子中、選擇一個賢能合宜的、使他坐他父親的位、你們也可以為你們主人的家爭戰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,4,"他們卻甚懼怕、彼此說、二王在他面前尚且站立不住、我們怎能站得住呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,5,"家宰、邑宰、和長老、並教養眾子的人、打發人去見耶戶、說、我們是你的僕人．凡你所吩咐我們的、都必遵行．我們不立誰作王．你看怎樣好、就怎樣行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,6,"耶戶又給他們寫信說、你們若歸順我、聽從我的話、明日這時候、要將你們主人眾子的首級、帶到耶斯列來見我。那時王的兒子七十人、都住在教養他們那城中的尊貴人家裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,7,"信一到、他們就把王的七十個兒子殺了、將首級裝在筐裡、送到耶斯列耶戶那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,8,"有使者來告訴耶戶說、他們將王眾子的首級送來了。耶戶說、將首級在城門口堆作兩堆、擱到明日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,9,"次日早晨、耶戶出來、站著對眾民說、你們都是公義的．我背叛我主人、將他殺了．這些人卻是誰殺的呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,10,"由此可知、耶和華指著亞哈家所說的話、一句沒有落空．因為耶和華藉他僕人以利亞所說的話、都成就了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,11,"凡亞哈家在耶斯列所剩下的人、和他的大臣、密友、祭司、耶戶盡都殺了、沒有留下一個。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,12,"耶戶起身、往撒瑪利亞去．在路上、牧人剪羊毛之處、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,13,"遇見猶大王、亞哈謝的弟兄、問他們說、你們是誰．回答說、我們是亞哈謝的弟兄．現在下去、要問王、和太後的眾子安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,14,"耶戶吩咐說、活捉他們。跟從的人就活捉了他們、將他們殺在剪羊毛之處的坑邊．共四十二人、沒有留下一個。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,15,"耶戶從那裡前行、恰遇利甲的兒子約拿達、來迎接他．耶戶問他安、對他說、你誠心待我、像我誠心待你麼．約拿達回答說、是。耶戶說、若是這樣、你向我伸手．他就伸手．耶戶拉他上車。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,16,"耶戶說、你和我同去、看我為耶和華怎樣熱心。於是請他坐在車上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,17,"到了撒瑪利亞、就把撒瑪利亞亞哈家剩下的人都殺了、直到滅盡、正如耶和華對以利亞所說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,18,"耶戶招聚眾民、對他們說、亞哈事奉巴力還冷淡．耶戶卻更熱心。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,19,"現在我要給巴力獻大祭．應當叫巴力的眾先知、和一切拜巴力的人、並巴力的眾祭司、都到我這裡來、不可缺少一個．凡不來的必不得活。耶戶這樣行、是用詭計、要殺盡拜巴力的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,20,"耶戶說、要為巴力宣告嚴肅會．於是宣告了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,21,"耶戶差人走遍以色列地．凡拜巴力的人都來齊了、沒有一個不來的．他們進了巴力廟．巴力廟中、從前邊直到後邊、都滿了人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,22,"耶戶吩咐掌管禮服的人說、拿出禮服來、給一切拜巴力的人穿。他就拿出禮服來給了他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,23,"耶戶和利甲的兒子約拿達、進了巴力廟、對拜巴力的人說、你們察看察看、在你們這裡不可有耶和華的僕人、只可容留拜巴力的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,24,"耶戶和約拿達進去、獻平安祭和燔祭。耶戶先安排八十人在廟外、吩咐說、我將這些人交在你們手中、若有一人脫逃、誰放的、必叫他償命。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,25,"耶戶獻完了燔祭、就出來吩咐護衛兵、和眾軍長、說、你們進去殺他們．不容一人出來。護衛兵和軍長就用刀殺他們、將屍首拋出去、便到巴力廟的城去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,26,"將巴力廟中的柱像、都拿出來燒了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,27,"毀壞了巴力柱像、拆毀了巴力廟、作為廁所、直到今日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,28,"這樣、耶戶在以色列中滅了巴力。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,29,"只是耶戶不離開尼八的兒子耶羅波安使以色列人陷在罪裡的那罪、就是拜伯特利和但的金牛犢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,30,"耶和華對耶戶說、因你辦好我眼中看為正的事、照我的心意待亞哈家、你的子孫必接續你坐以色列的國位、直到四代。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,31,"只是耶戶不盡心遵守耶和華以色列　神的律法、不離開耶羅波安使以色列人陷在罪裡的那罪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,32,"在那些日子、耶和華纔割裂以色列國、使哈薛攻擊以色列的境界。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,33,"乃是約但河東、基列全地、從靠近亞嫩谷邊的亞羅珥起、就是基列和巴珊的迦得人、流便人、瑪拿西人之地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,34,"耶戶其餘的事、凡他所行的、和他的勇力、都寫在以色列諸王記上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,35,"耶戶與他列祖同睡、葬在撒瑪利亞．他兒子約哈斯接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",10,36,"耶戶在撒瑪利亞作以色列王二十八年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,1,"亞哈謝的母親亞他利雅見他兒子死了、就起來剿滅王室。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,2,"但約蘭王的女兒、亞哈謝的妹子約示巴、將亞哈謝的兒子約阿施從那被殺的王子中偷出來、把他和他的乳母、都藏在臥房裡、躲避亞他利雅、免得被殺．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,3,"約阿施和他的乳母藏在耶和華的殿裡六年．亞他利雅篡了國位。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,4,"第七年、耶何耶大打發人叫迦利人〔或作親兵〕和護衛兵的眾百夫長來、領他們進了耶和華的殿、與他們立約、使他們在耶和華殿裡起誓．又將王的兒子指給他們看．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,5,"吩咐他們說、你們當這樣行、凡安息日進班的三分之一、要看守王宮．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,6,"三分之一、要在蘇珥門．三分之一、要在護衛兵院的後門．這樣把守王宮、攔阻閒人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,7,"你們安息日所有出班的三分之二、要在耶和華的殿裡護衛王．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,8,"各人手拿兵器、四圍護衛王．凡擅入你們班次的、必當治死、王出入的時候、你們當跟隨他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,9,"眾百夫長就照著祭司耶何耶大一切所吩咐的去行．各帶所管安息日進班出班的人來見祭司耶何耶大．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,10,"祭司便將耶和華殿裡所藏大衛王的槍和盾牌交給百夫長。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,11,"護衛兵手中各拿兵器、在壇和殿那裡、從殿右直到殿左、站在王子的四圍．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,12,"祭司領王子出來、給他戴上冠冕、將律法書交給他、膏他作王．眾人就拍掌說、願王萬歲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,13,"亞他利雅聽見護衛兵和民的聲音、就到民那裡、進耶和華的殿．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,14,"看見王照例站在柱旁、百夫長和吹號的人侍立在王左右、國中的眾民歡樂吹號．亞他利雅就撕裂衣服、喊叫說、反了、反了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,15,"祭司耶何耶大吩咐管轄軍兵的百夫長說、將他趕出班外．凡跟隨他的、必用刀殺死．因為祭司說、不可在耶和華殿裡殺他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,16,"眾兵就閃開讓他去．他從馬路上王宮去．便在那裡被殺。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,17,"耶何耶大使王和民與耶和華立約、作耶和華的民．又使王與民立約。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,18,"於是國民都到巴力廟、拆毀了廟、打碎壇和像、又在壇前將巴力的祭司瑪坦殺了。祭司耶何耶大派官看守耶和華的殿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,19,"又率領百夫長和迦利人〔或作親兵〕與護衛兵、以及國中的眾民、請王從耶和華殿下來、由護衛兵的門進入王宮。他就坐了王位。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,20,"國民都歡樂、閤城都安靜。眾人已將亞他利雅在王宮那裡用刀殺了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",11,21,"約阿施登基的時候、年方七歲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",12,1,"耶戶第七年、約阿施登基、在耶路撒冷作王四十年．他母親名叫西比亞、是別是巴人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",12,2,"約阿施在祭司耶何耶大教訓他的時候、就行耶和華眼中看為正的事．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",12,3,"只是丘壇還沒有廢去．百姓仍在那裡獻祭燒香。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",12,4,"約阿施對眾祭司說、凡奉到耶和華殿分別為聖之物所值通用的銀子、或各人當納的身價、或樂意奉到耶和華殿的銀子、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",12,5,"你們當從所認識的人收了來、修理殿的一切破壞之處。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",12,6,"無奈到了約阿施王二十三年、祭司仍未修理殿的破壞之處。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",12,7,"所以約阿施王召了大祭司耶何耶大、和眾祭司來、對他們說、你們怎麼不修理殿的破壞之處呢．從今以後、你們不要從所認識的人再收銀子、要將所收的交出來、修理殿的破壞之處．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",12,8,"眾祭司答應不再收百姓的銀子、也不修理殿的破壞之處。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",12,9,"祭司耶何耶大取了一個櫃子、在櫃蓋上鑽了一個窟窿、放於壇旁、在進耶和華殿的右邊．守門的祭司將奉到耶和華殿的一切銀子投在櫃裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",12,10,"他們見櫃裡的銀子多了、便叫王的書記和大祭司上來、將耶和華殿裡的銀子數算包起來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",12,11,"把所平的銀子交給督工的、就是耶和華殿裡辦事的人．他們把銀子轉交修理耶和華殿的木匠和工人、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",12,12,"並瓦匠、石匠、又買木料和鑿成的石頭、修理耶和華殿的破壞之處、以及修理殿的各樣使用。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",12,13,"但那奉到耶和華殿的銀子、沒有用以作耶和華殿裡的銀杯、蠟剪、碗、號、和別樣的金銀器皿．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",12,14,"乃將那銀子交給督工的人、修理耶和華的殿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ki",12,15,"且將銀子交給辦事的人、轉交作工的人、不與他們算賬．因為他們辦事誠實。")');
