tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,14,"另外還有商人所進的金子．並且亞拉伯諸王、與屬國的省長、都帶金銀給所羅門。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,15,"所羅門王用錘出來的金子打成擋牌二百面、每面用金子六百舍客勒．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,16,"又用錘出來的金子打成盾牌三百面、每面用金子三百舍客勒．都放在利巴嫩林宮裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,17,"王用像牙製造一個大寶座、用精金包裹．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,18,"寶座有六層臺階、又有金腳凳、與寶座相連、寶座兩旁有扶手、靠近扶手有兩個獅子站立．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,19,"六層臺階上有十二個獅子站立、每層有兩個、左邊一個、右邊一個．在列國中沒有這樣作的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,20,"所羅門王一切的飲器都是金的、利巴嫩林宮裡的一切器皿都是精金的．所羅門年間、銀子算不了甚麼．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,21,"因為王的船隻與希蘭的僕人一同往他施去．他施船隻三年一次裝載金、銀、像牙、猿猴、孔雀回來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,22,"所羅門王的財寶與智慧勝過天下的列王．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,23,"普天下的王都求見所羅門、要聽　神賜給他智慧的話．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,24,"他們各帶貢物、就是金器、銀器、衣服、軍械、香料、騾馬、每年有一定之例。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,25,"所羅門有套車的馬四千棚、有馬兵一萬二千、安置在屯車的城邑和耶路撒冷、就是王那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,26,"所羅門統管諸王、從大河到非利士地直到埃及的邊界。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,27,"王在耶路撒冷使銀子多如石頭、香柏木多如高原的桑樹。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,28,"有人從埃及和各國為所羅門趕馬群來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,29,"所羅門其餘的事、自始至終、不都寫在先知拿單的書上、和示羅人亞希雅的預言書上、並先見易多論尼八兒子耶羅波安的默示書上麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,30,"所羅門在耶路撒冷作以色列眾人的王、共四十年．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",9,31,"所羅門與他列祖同睡、葬在他父大衛城裡．他兒子羅波安接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,1,"羅波安往示劍去、因為以色列人都到了示劍、要立他作王．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,2,"尼八的兒子耶羅波安先前躲避所羅門王、逃往埃及、住在那裡．他聽見這事、就從埃及回來．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,3,"以色列人打發人去請他．他就和以色列眾人來見羅波安、對他說、")');
