tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,5,"羅波安對他們說、第三日再來見我罷．民就去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,6,"羅波安之父所羅門在世的日子、有侍立在他面前的老年人、羅波安王和他們商議、說、你們給我出個甚麼主意、我好回覆這民．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,7,"老年人對他說、王若恩待這民、使他們喜悅、用好話回覆他們、他們就永遠作王的僕人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,8,"王卻不用老年人給他出的主意、就和那些與他一同長大、在他面前侍立的少年人商議、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,9,"說、這民對我說、你父親使我們負重軛、求你使我們輕鬆些．你們給我出個甚麼主意、我好回覆他們．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,10,"那同他長大的少年人說、這民對王說、你父親使我們負重軛、求你使我們輕鬆些．王要對他們如此說、我的小拇指比我父親的腰還粗．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,11,"我父親使你們負重軛、我必使你們負更重的軛．我父親用鞭子責打你們、我要用蠍子鞭責打你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,12,"耶羅波安和眾百姓遵著羅波安王所說、你們第三日再來見我的那話．第三日他們果然來了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,13,"羅波安王用嚴厲的話回覆他們、不用老年人所出的主意．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,14,"照著少年人所出的主意對他們說、我父親使你們負重軛、我必使你們負更重的軛．我父親用鞭子責打你們、我要用蠍子鞭責打你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,15,"王不肯依從百姓．這事乃出於　神、為要應驗耶和華藉示羅人亞希雅對尼八兒子耶羅波安所說的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,16,"以色列眾民見王不依從他們、就對王說、我們與大衛有甚麼分兒呢、與耶西的兒子並沒有關涉．以色列人哪、各回各家去罷．大衛家阿、自己顧自己罷．於是、以色列眾人都回自己家裡去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,17,"惟獨住在猶大城邑的以色列人、羅波安仍作他們的王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,18,"羅波安王差遣掌管服苦之人的哈多蘭往以色列人那裡去、以色列人就用石頭打死他．羅波安王急忙上車、逃回耶路撒冷去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",10,19,"這樣、以色列人背叛大衛家、直到今日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,1,"羅波安來到耶路撒冷、招聚猶大家、和便雅憫家、共十八萬人、都是挑選的戰士、要與以色列人爭戰、好將國奪回再歸自己。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,2,"但耶和華的話臨到神人示瑪雅說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,3,"你去告訴所羅門的兒子猶大王羅波安、和住猶大便雅憫的以色列眾人、說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,4,"耶和華如此說、你們不可上去、與你們的弟兄爭戰．各歸各家去罷．因為這事出於我。眾人就聽從耶和華的話、歸回不去與耶羅波安爭戰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,5,"羅波安住在耶路撒冷、在猶大地修築城邑。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,6,"為保障修築伯利恆、以坦、提哥亞、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,7,"伯夙、梭哥、亞杜蘭、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,8,"迦特、瑪利沙、西弗、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,9,"亞多萊音、拉吉、亞西加、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,10,"瑣拉、亞雅崙、希伯崙．這都是猶大和便雅憫的堅固城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,11,"羅波安又堅固各處的保障、在其中安置軍長、又預備下糧食、油、酒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,12,"他在各城裡預備盾牌和槍、且使城極其堅固。猶大和便雅憫都歸了他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,13,"以色列全地的祭司和利未人、都從四方來歸羅波安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,14,"利未人撇下他們的郊野和產業、來到猶大與耶路撒冷．是因耶羅波安和他的兒子拒絕他們、不許他們供祭司職分事奉耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,15,"耶羅波安為丘壇、為鬼魔、〔原文作公山羊〕為自己所鑄造的牛犢、設立祭司。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,16,"以色列各支派中、凡立定心意尋求耶和華以色列　神的、都隨從利未人、來到耶路撒冷祭祀耶和華他們列祖的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,17,"這樣、就堅固猶大國、使所羅門的兒子羅波安強盛三年、因為他們三年遵行大衛和所羅門的道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,18,"羅波安娶大衛兒子耶利摩的女兒瑪哈拉為妻、又娶耶西兒子以利押的女兒亞比孩為妻。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,19,"從他生了幾個兒子、就是耶烏施、示瑪利雅、撒罕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,20,"後來又娶押沙龍的女兒瑪迦．〔十三章二節作烏列的女兒米該雅〕從他生了亞比雅、亞太、細撒、示羅密。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,21,"羅波安娶十八個妻、立六十個妾、生二十八個兒子、六十個女兒．他卻愛押沙龍的女兒瑪迦、比愛別的妻妾更甚。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,22,"羅波安立瑪迦的兒子亞比雅作太子、在他弟兄中為首．因為想要立他接續作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",11,23,"羅波安辦事精明、使他眾子分散在猶大和便雅憫全地各堅固城裡、又賜他們許多糧食、為他們多尋妻子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",12,1,"羅波安的國堅立、他強盛的時候、就離棄耶和華的律法、以色列人也都隨從他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",12,2,"羅波安王第五年、埃及王示撒上來攻打耶路撒冷．因為王和民得罪了耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",12,3,"示撒帶戰車一千二百輛、馬兵六萬、並且跟從他出埃及的路比人、蘇基人、和古實人、多得不可勝數。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",12,4,"他攻取了猶大的堅固城、就來到耶路撒冷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",12,5,"那時、猶大的首領、因為示撒就聚集在耶路撒冷．有先知示瑪雅去見羅波安、和眾首領、對他們說、耶和華如此說、你們離棄了我、所以我使你們落在示撒手裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",12,6,"於是王和以色列的眾首領、都自卑說、耶和華是公義的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",12,7,"耶和華見他們自卑、耶和華的話就臨到示瑪雅說、他們既自卑、我必不滅絕他們．必使他們略得拯救、我不藉著示撒的手、將我的怒氣倒在耶路撒冷．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",12,8,"然而他們必作示撒的僕人、好叫他們知道服事我、與服事外邦人有何分別。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",12,9,"於是埃及王示撒上來攻取耶路撒冷、奪了耶和華殿和王宮裡的寶物、盡都帶走．又奪去所羅門製造的金盾牌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",12,10,"羅波安王製造銅盾牌代替那金盾牌．交給守王宮門的護衛長看守。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",12,11,"王每逢進耶和華的殿、護衛兵就拿這盾牌、隨後仍將盾牌送回、放在護衛房。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",12,12,"王自卑的時候、耶和華的怒氣就轉消了、不將他滅盡．並且在猶大中間也有善益的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",12,13,"羅波安王自強、在耶路撒冷作王．他登基的時候年四十一歲、在耶路撒冷、就是耶和華從以色列眾支派中所選擇立他名的城、作王十七年．羅波安的母親名叫拿瑪、是亞捫人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",12,14,"羅波安行惡、因他不立定心意尋求耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",12,15,"羅波安所行的事、自始至終、不都寫在先知示瑪雅、和先見易多的史記上麼．羅波安與耶羅波安時常爭戰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",12,16,"羅波安與他列祖同睡、葬在大衛城裡．他兒子亞比雅接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,1,"耶羅波安王十八年．亞比雅登基、作猶大王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,2,"在耶路撒冷作王三年．他母親名叫米該亞、〔米該亞又作瑪迦〕是基比亞人烏列的女兒。亞比雅常與耶羅波安爭戰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,3,"有一次亞比雅率領挑選的兵四十萬擺陣、都是勇敢的戰士．耶羅波安也挑選大能的勇士八十萬、對亞比雅擺陣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,4,"亞比雅站在以法蓮山地中的洗瑪臉山上、說、耶羅波安和以色列眾人哪、要聽我說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,5,"耶和華以色列的　神曾立鹽約、〔鹽即不廢壞的意思〕將以色列國永遠賜給大衛和他的子孫、你們不知道麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,6,"無奈大衛兒子所羅門的臣僕、尼八兒子耶羅波安、起來背叛他的主人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,7,"有些無賴的匪徒聚集跟從他、逞強攻擊所羅門的兒子羅波安、那時羅波安還幼弱、不能抵擋他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,8,"現在你們有意抗拒大衛子孫手下所治耶和華的國．你們的人也甚多、你們那裡又有耶羅波安為你們所造當作神的金牛犢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,9,"你們不是驅逐耶和華的祭司亞倫的後裔、和利未人麼．不是照著外邦人的惡俗為自己立祭司麼．無論何人牽一隻公牛犢、七隻公綿羊、將自己分別出來、就可作虛無之神的祭司。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,10,"至於我們、耶和華是我們的　神、我們並沒有離棄他．我們有事奉耶和華的祭司、都是亞倫的後裔、並有利未人各盡其職。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,11,"每日早晚向耶和華獻燔祭、燒美香、又在精金的桌子上擺陳設餅．又有金燈臺和燈盞、每晚點起、因為我們遵守耶和華我們　神的命．惟有你們離棄了他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,12,"率領我們的是　神、我們這裡也有　神的祭司、拿號向你們吹出大聲。以色列人哪、不要與耶和華你們列祖的　神爭戰．因你們必不能亨通。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,13,"耶羅波安卻在猶大人的後頭設伏兵．這樣、以色列人在猶大人的前頭、伏兵在猶大人的後頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,14,"猶大人回頭觀看、見前後都有敵兵．就呼求耶和華、祭司也吹號。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,15,"於是猶大人吶喊．猶大人吶喊的時候、　神就使耶羅波安和以色列眾人、敗在亞比雅與猶大人面前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,16,"以色列人在猶大人面前逃跑．　神將他們交在猶大人手裡．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,17,"亞比雅和他的軍兵大大殺戮以色列人．以色列人僕倒死亡的精兵有五十萬。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,18,"那時、以色列人被制伏了、猶大人得勝、是因倚靠耶和華他們列祖的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,19,"亞比雅追趕耶羅波安、攻取了他的幾座城、就是伯特利和屬伯特利的鎮市、耶沙拿和屬耶沙拿的鎮市、以法拉音〔或作以弗倫〕和屬以法拉音的鎮市。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,20,"亞比雅在世的時候、耶羅波安不能再強盛．耶和華攻擊他、他就死了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,21,"亞比雅卻漸漸強盛、娶妻妾十四個、生了二十二個兒子、十六個女兒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",13,22,"亞比雅其餘的事、和他的言行、都寫在先知易多的傳上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",14,1,"亞比雅與他列祖同睡、葬在大衛城裡．他兒子亞撒接續他作王。亞撒年間、國中太平十年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",14,2,"亞撒行耶和華他　神眼中看為善為正的事．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",14,3,"除掉外邦神的壇、和丘壇、打碎柱像、砍下木偶、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",14,4,"吩咐猶大人尋求耶和華他們列祖的　神、遵行他的律法、誡命．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",14,5,"又在猶大各城邑除掉丘壇、和日像．那時國享太平。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",14,6,"又在猶大建造了幾座堅固城．國中太平數年、沒有戰爭、因為耶和華賜他平安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",14,7,"他對猶大人說、我們要建造這些城邑、四圍築牆、蓋樓、安門、作閂．地還屬我們、是因尋求耶和華我們的　神．我們既尋求他、他就賜我們四境平安。於是建造城邑、諸事亨通。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",14,8,"亞撒的軍兵、出自猶大拿盾牌拿槍的三十萬人．出自便雅憫拿盾牌拉弓的二十八萬人．這都是大能的勇士。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",14,9,"有古實王謝拉率領軍兵一百萬、戰車三百輛、出來攻擊猶大人、到了瑪利沙。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",14,10,"於是亞撒出去、與他迎敵、就在瑪利沙的洗法谷、彼此擺陣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",14,11,"亞撒呼求耶和華他的　神、說、耶和華阿、惟有你能幫助軟弱的、勝過強盛的．耶和華我們的　神阿、求你幫助我們．因為我們仰賴你、奉你的名來攻擊這大軍。耶和華阿、你是我們的　神、不要容人勝過你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",14,12,"於是耶和華使古實人敗在亞撒和猶大人面前．古實人就逃跑了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",14,13,"亞撒和跟隨他的軍兵追趕他們、直到基拉耳．古實人被殺的甚多、不能再強盛．因為敗在耶和華與他軍兵面前．猶大人就奪了許多財物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",14,14,"又打破基拉耳四圍的城邑．耶和華使其中的人、都甚恐懼．猶大人又將所有的城擄掠一空、因其中的財物甚多。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",14,15,"又毀壞了群畜的圈、奪取許多的羊和駱駝、就回耶路撒冷去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,1,"　神的靈感動俄德的兒子亞撒利雅．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,2,"他出來迎接亞撒、對他說、亞撒和猶大便雅憫眾人哪、要聽我說、你們若順從耶和華、耶和華必與你們同在．你們若尋求他、就必尋見．你們若離棄他、他必離棄你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,3,"以色列人不信真神、沒有訓誨的祭司、也沒有律法、已經好久了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,4,"但他們在急難的時候歸向耶和華以色列的　神、尋求他、他就被他們尋見。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,5,"那時出入的人、不得平安．列國的居民、都遭大亂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,6,"這國攻擊那國、這城攻擊那城、互相破壞．因為　神用各樣災難擾亂他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,7,"現在你們要剛強、不要手軟．因你們所行的、必得賞賜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,8,"亞撒聽見這話、和俄德兒子先知亞撒利雅的預言、就壯起膽來、在猶大、便雅憫全地、並以法蓮山地所奪的各城、將可憎之物、盡都除掉．又在耶和華殿的廊前、重新修築耶和華的壇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,9,"又招聚猶大便雅憫的眾人、並他們中間寄居的以法蓮人、瑪拿西人、西緬人．有許多以色列人歸降亞撒．因見耶和華他的　神與他同在。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,10,"亞撒十五年三月、他們都聚集在耶路撒冷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,11,"當日他們從所取的擄物中、將牛七百隻、羊七千隻、獻給耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,12,"他們就立約、要盡心盡性地尋求耶和華他們列祖的　神．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,13,"凡不尋求耶和華以色列　神的、無論大小、男女、必被治死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,14,"他們就大聲歡呼、吹號吹角、向耶和華起誓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,15,"猶大眾人為所起的誓歡喜．因他們是盡心起誓、盡意尋求耶和華、耶和華就被他們尋見、且賜他們四境平安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,16,"亞撒王貶了他祖母瑪迦太後的位、因他造了可憎的偶像亞舍拉．亞撒砍下他的偶像、搗得粉碎、燒在汲淪溪邊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,17,"只是丘壇還沒有從以色列中廢去．然而亞撒的心一生誠實。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,18,"亞撒將他父所分別為聖、與自己所分別為聖的金銀、和器皿、都奉到　神的殿裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",15,19,"從這時直到亞撒三十五年、都沒有爭戰的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",16,1,"亞撒三十六年、以色列王巴沙上來攻擊猶大、修築拉瑪不許人從猶大王亞撒那裡出入。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",16,2,"於是亞撒從耶和華殿和王宮的府庫裡、拿出金銀來、送與住大馬色的亞蘭王便哈達、說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",16,3,"你父曾與我父立約、我與你也要立約．現在我將金銀送給你．求你廢掉你與以色列王巴沙所立的約、使他離開我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",16,4,"便哈達聽從亞撒王的話、派軍長去攻擊以色列的城邑．他們就攻破以雲、但、亞伯瑪音、和拿弗他利一切的積貨城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",16,5,"巴沙聽見就停工、不修築拉瑪了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",16,6,"於是亞撒王率領猶大眾人、將巴沙修築拉瑪所用的石頭、木頭、都運去、用以修築迦巴和米斯巴。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",16,7,"那時、先見哈拿尼來見猶大王亞撒、對他說、因你仰賴亞蘭王、沒有仰賴耶和華你的　神、所以亞蘭王的軍兵脫離了你的手。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",16,8,"古實人路比人的軍隊不是甚大麼、戰車馬兵不是極多麼．只因你仰賴耶和華、他便將他們交在你手裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",16,9,"耶和華的眼目遍察全地、要顯大能幫助向他心存誠實的人。你這事行得愚昧．此後、你必有爭戰的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",16,10,"亞撒因此惱恨先見、將他囚在監裡。那時亞撒也虐待一些人民。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",16,11,"亞撒所行的事、自始至終、都寫在猶大和以色列諸王記上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",16,12,"亞撒作王三十九年、他腳上有病、而且甚重．病的時候沒有求耶和華、只求醫生。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",16,13,"他作王四十一年而死、與他列祖同睡、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",16,14,"葬在大衛城自己所鑿的墳墓裡、放在床上、其床堆滿各樣馨香的香料、就是按作香的作法調和的香料．又為他燒了許多的物件。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,1,"亞撒的兒子約沙法接續他作王、奮勇自強、防備以色列人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,2,"安置軍兵在猶大一切堅固城裡、又安置防兵在猶大地、和他父亞撒所得以法蓮的城邑中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,3,"耶和華與約沙法同在、因為他行他祖大衛初行的道、不尋求巴力、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,4,"只尋求他父親的　神遵行他的誡命、不效法以色列人的行為。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,5,"所以耶和華堅定他的國、猶大眾人給他進貢．約沙法大有尊榮資財。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,6,"他高興遵行耶和華的道．並且從猶大除掉一切丘壇、和木偶。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,7,"他作王第三年就差遣臣子便亥伊勒、俄巴底、撒迦利雅、拿坦業、米該亞、往猶大各城去教訓百姓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,8,"同著他們有利未人、示瑪雅、尼探雅、西巴第雅、亞撒黑、示米拉末、約拿單、亞多尼雅、多比雅、駝巴多尼雅．又有祭司以利沙瑪、約蘭、同著他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,9,"他們帶著耶和華的律法書、走遍猶大各城教訓百姓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,10,"耶和華使猶大四圍的列國、都甚恐懼、不敢與約沙法爭戰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,11,"有些非利士人與約沙法送禮物、納貢銀．亞拉伯人也送他公綿羊七千七百隻、公山羊七千七百隻。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,12,"約沙法日漸強大．在猶大建造營寨、和積貨城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,13,"他在猶大城邑中有許多工程．又在耶路撒冷有戰士、就是大能的勇士。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,14,"他們的數目、按著宗族記在下面．猶大族的千夫長押拿為首、率領大能的勇士三十萬．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,15,"其次是千夫長約哈難、率領大能的勇士二十八萬．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,16,"其次是細基利的兒子亞瑪斯雅．他為耶和華犧牲自己、率領大能的勇士二十萬．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,17,"便雅憫族、是大能的勇士以利雅大、率領拿弓箭和盾牌的二十萬．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,18,"其次是約薩拔、率領預備打仗的十八萬。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",17,19,"這都是伺候王的。還有王在猶大全地堅固城所安置的不在其內。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,1,"約沙法大有尊榮資財、就與亞哈結親。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,2,"過了幾年、他下到撒瑪利亞去見亞哈。亞哈為他和跟從他的人、宰了許多牛羊、勸他與自己同去攻取基列的拉末。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,3,"以色列王亞哈問猶大王約沙法說、你肯同我去攻取基列的拉末麼。他回答說、你我不分彼此、我的民與你的民一樣．必與你同去爭戰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,4,"約沙法對以色列王說、請你先求問耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,5,"於是以色列王招聚先知四百人、問他們說、我們上去攻取基列的拉末可以不可以．他們說、可以上去．因為　神必將那城交在王的手裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,6,"約沙法說、這裡不是還有耶和華的先知、我們可以求問他麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,7,"以色列王對約沙法說、還有一個人、是音拉的兒子米該雅、我們可以託他求問耶和華．只是我恨他、因為他指著我所說的預言、不說吉語、常說凶言。約沙法說、王不必這樣說。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,8,"以色列王就召了一個太監來、說、你快去將音拉的兒子米該雅召來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,9,"以色列王、和猶大王約沙法、在撒瑪利亞城門前的空場上、各穿朝服坐在位上．所有的先知都在他們面前說預言。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,10,"基拿拿的兒子西底家造了兩個鐵角、說、耶和華如此說、你要用這角牴觸亞蘭人、直到將他們滅盡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,11,"所有的先知也都這樣預言、說、可以上基列的拉末去、必然得勝．因為耶和華必將那城交在王的手中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,12,"那去召米該雅的使者對米該雅說、眾先知一口同音地都向王說吉言．你不如與他們說一樣的話、也說吉言。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,13,"米該雅說、我指著永生的耶和華起誓、我的　神說甚麼、我就說甚麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,14,"米該雅到王面前、王問他說、米該雅阿、我們上去攻取基列的拉末可以不可以．他說、可以上去、必然得勝．敵人必交在你們手裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,15,"王對他說、我當囑咐你幾次、你纔奉耶和華的名向我說實話呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,16,"米該雅說、我看見以色列眾民散在山上、如同沒有牧人的羊群一般．耶和華說、這民沒有主人．他們可以平平安安地各歸各家去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,17,"以色列王對約沙法說、我豈沒有告訴你、這人指著我所說的預言、不說吉語、單說凶言麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,18,"米該雅說、你們要聽耶和華的話．我看見耶和華坐在寶座上、天上的萬軍侍立在他左右。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,19,"耶和華說、誰去引誘以色列王亞哈上基列的拉末去陣亡呢。這個就這樣說、那個就那樣說。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,20,"隨後有一個神靈出來、站在耶和華面前說、我去引誘他。耶和華問他說、你用何法呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,21,"他說、我去、要在他眾先知口中作謊言的靈。耶和華說、這樣、你必能引誘他．你去如此行罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,22,"現在耶和華使謊言的靈、入了你這些先知的口．並且耶和華已經命定降禍與你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,23,"基拿拿的兒子西底家前來打米該雅的臉、說、耶和華的靈從那裡離開我與你說話呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,24,"米該雅說、你進嚴密的屋子藏躲的那日、就必看見了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,25,"以色列王說、將米該雅帶回、交給邑宰亞們、和王的兒子約阿施、說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,26,"王如此說、把這個人下在監裡、使他受苦、喫不飽喝不足、等候我平平安安地回來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,27,"米該雅說、你若能平安回來、那就是耶和華沒有藉我說這話了．又說、眾民哪、你們都要聽。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,28,"以色列王和猶大王約沙法、上基列的拉末去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,29,"以色列王對約沙法說、我要改裝上陣．你可以仍穿王服。於是以色列王改裝、他們就上陣去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,30,"先是亞蘭王吩咐車兵長說、他們的兵將、無論大小、你們都不可與他們爭戰、只要與以色列王爭戰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,31,"車兵長看見約沙法便說、這必是以色列王、就轉過去與他爭戰。約沙法一呼喊、耶和華就幫助他．　神又感動他們離開他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,32,"車兵長見不是以色列王、就轉去不追他了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,33,"有一人隨便開弓、恰巧射入以色列王的甲縫裡．王對趕車的說、我受了重傷、你轉過車來、拉我出陣罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",18,34,"那日陣勢越戰越猛．以色列王勉強站在車上抵擋亞蘭人、直到晚上．約在日落的時候、王就死了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",19,1,"猶大王約沙法平平安安地回耶路撒冷、到宮裡去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",19,2,"先見哈拿尼的兒子耶戶出來迎接約沙法王、對他說、你豈當幫助惡人、愛那恨惡耶和華的人呢．因此耶和華的忿怒臨到你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",19,3,"然而你還有善行、因你從國中除掉木偶、立定心意尋求　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",19,4,"約沙法住在耶路撒冷．以後又出巡民間、從別是巴直到以法蓮山地、引導民歸向耶和華他們列祖的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",19,5,"又在猶大國中遍地的堅固城裡、設立審判官、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",19,6,"對他們說、你們辦事應當謹慎．因為你們判斷不是為人、乃是為耶和華．判斷的時候、他必與你們同在。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",19,7,"現在你們應當敬畏耶和華．謹慎辦事．因為耶和華我們的　神沒有不義、不偏待人、也不受賄賂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",19,8,"約沙法從利未人和祭司、並以色列族長中派定人、在耶路撒冷為耶和華判斷、聽民間的爭訟、就回耶路撒冷去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",19,9,"約沙法囑咐他們說、你們當敬畏耶和華、忠心誠實辦事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",19,10,"住在各城裡你們的弟兄、若有爭訟的事、來到你們這裡、或為流血、或犯律法、誡命、律例、典章、你們要警戒他們、免得他們得罪耶和華、以致他的忿怒臨到你們、和你們的弟兄．這樣行、你們就沒有罪了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",19,11,"凡屬耶和華的事、有大祭司亞瑪利雅管理你們．凡屬王的事、有猶大支派的族長以實瑪利的兒子西巴第雅管理你們．在你們面前有利未人作官長．你們應當壯膽辦事、願耶和華與善人同在。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,1,"此後、摩押人和亞捫人、又有米烏尼人、一同來攻擊約沙法。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,2,"有人來報告約沙法說、從海外亞蘭〔亞蘭又作以東〕那邊有大軍來攻擊你、如今他們在哈洗遜他瑪、就是隱基底。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,3,"約沙法便懼怕、定意尋求耶和華．在猶大全地宣告禁食。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,4,"於是猶大人聚會、求耶和華幫助．猶大各城都有人出來尋求耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,5,"約沙法就在猶大和耶路撒冷的會中、站在耶和華殿的新院前、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,6,"說、耶和華我們列祖的　神阿、你不是天上的　神麼．你不是萬邦萬國的主宰麼。在你手中有大能大力、無人能抵擋你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,7,"我們的　神阿、你不是曾在你民以色列人面前驅逐這地的居民、將這地賜給你朋友亞伯拉罕的後裔永遠為業麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,8,"他們住在這地、又為你的名建造聖所、說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,9,"倘有禍患臨到我們、或刀兵災殃、或瘟疫饑荒、我們在急難的時候、站在這殿前向你呼求、你必垂聽而拯救．因為你的名在這殿裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,10,"從前以色列人出埃及地的時候、你不容以色列人侵犯亞捫人、摩押人、和西珥山人．以色列人就離開他們、不滅絕他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,11,"看哪、他們怎樣報復我們、要來驅逐我們出離你的地、就是你賜給我們為業之地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,12,"我們的　神阿、你不懲罰他們麼．因為我們無力抵擋這來攻擊我們的大軍．我們也不知道怎樣行、我們的眼目、單仰望你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,13,"猶大眾人、和他們的嬰孩、妻子、兒女、都站在耶和華面前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,14,"那時、耶和華的靈在會中臨到利未人亞薩的後裔瑪探雅的玄孫耶利的曾孫比拿雅的孫子撒迦利雅的兒子雅哈悉．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,15,"他說、猶大眾人、耶路撒冷的居民、和約沙法王、你們請聽、耶和華對你們如此說、不要因這大軍恐懼、驚惶、因為勝敗不在乎你們、乃在乎　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,16,"明日你們要下去迎敵．他們是從洗斯坡上來．你們必在耶魯伊勒曠野前的谷口遇見他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,17,"猶大和耶路撒冷人哪、這次你們不要爭戰、要擺陣站著、看耶和華為你們施行拯救．不要恐懼、也不要驚惶．明日當出去迎敵．因為耶和華與你們同在。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,18,"約沙法就面伏於地．猶大眾人和耶路撒冷的居民、也俯伏在耶和華面前、叩拜耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,19,"哥轄族和可拉族的利未人都起來、用極大的聲音讚美耶和華以色列的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,20,"次日清早眾人起來、往提哥亞的曠野去．出去的時候、約沙法站著說、猶大人和耶路撒冷的居民哪、要聽我說、信耶和華你們的　神、就必立穩．信他的先知就必亨通。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,21,"約沙法既與民商議了、就設立歌唱的人、頌讚耶和華、使他們穿上聖潔的禮服、走在軍前讚美耶和華、說、當稱謝耶和華．因他的慈愛永遠長存。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,22,"眾人方唱歌讚美的時候、耶和華就派伏兵擊殺那來攻擊猶大人的亞捫人、摩押人、和西珥山人．他們就被打敗了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,23,"因為亞捫人和摩押人起來、擊殺住西珥山的人、將他們滅盡．滅盡住西珥山的人之後、他們又彼此自相擊殺。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,24,"猶大人來到曠野的望樓、向那大軍觀看、見屍橫遍地、沒有一個逃脫的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,25,"約沙法和他的百姓就來收取敵人的財物、在屍首中見了許多財物、珍寶、他們剝脫下來的多得不可攜帶、因為甚多、直收取了三日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,26,"第四日眾人聚集在比拉迦〔就是稱頌的意思〕谷、在那裡稱頌耶和華．因此那地方名叫比拉迦谷、直到今日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,27,"猶大人、和耶路撒冷人、都歡歡喜喜地回耶路撒冷、約沙法率領他們．因為耶和華使他們戰勝仇敵、就歡喜快樂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,28,"他們彈琴、鼓瑟、吹號來到耶路撒冷、進了耶和華的殿．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,29,"列邦諸國聽見耶和華戰敗以色列的仇敵、就甚懼怕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,30,"這樣、約沙法的國、得享太平．因為　神賜他四境平安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,31,"約沙法作猶大王．登基的時候年三十五歲．在耶路撒冷作王二十五年．他母親名叫阿蘇巴、乃示利希的女兒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,32,"約沙法效法他父亞撒所行的、不偏左右、行耶和華眼中看為正的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,33,"只是丘壇還沒有廢去．百姓也沒有立定心意歸向他們列祖的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,34,"約沙法其餘的事、自始至終、都寫在哈拿尼的兒子耶戶的書上、也載入以色列諸王記上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,35,"此後猶大王約沙法與以色列王亞哈謝交好．亞哈謝行惡太甚。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,36,"二王合夥造船要往他施去．遂在以旬迦別造船。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",20,37,"那時瑪利沙人、多大瓦的兒子以利以謝、向約沙法預言、說、因你與亞哈謝交好、耶和華必破壞你所造的。後來那船果然破壞、不能往他施去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,1,"約沙法與他列祖同睡、葬在大衛城、他列祖的墳地裡．他兒子約蘭接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,2,"約蘭有幾個兄弟、就是約沙法的兒子亞撒利雅、耶歇、撒迦利雅、亞撒利雅、米迦勒、示法提雅．這都是以色列王約沙法的兒子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,3,"他們的父親將許多金銀、財寶、和猶大地的堅固城、賜給他們．但將國賜給約蘭、因為他是長子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,4,"約蘭興起坐他父的位、奮勇自強、就用刀殺了他的眾兄弟、和以色列的幾個首領。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,5,"約蘭登基的時候年三十二歲．在耶路撒冷作王八年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,6,"他行以色列諸王的道、與亞哈家一樣．因他娶了亞哈的女兒為妻、行耶和華眼中看為惡的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,7,"耶和華卻因自己與大衛所立的約、不肯滅大衛的家、照他所應許的、永遠賜燈光與大衛和他的子孫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,8,"約蘭年間以東人背叛猶大、脫離他的權下、自己立王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,9,"約蘭就率領軍長、和所有的戰車、夜間起來、攻擊圍困他的以東人、和車兵長。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,10,"這樣、以東人背叛猶大、脫離他的權下、直到今日．那時立拿人也背叛了、因為約蘭離棄耶和華他列祖的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,11,"他又在猶大諸山建築丘壇、使耶路撒冷的居民行邪淫、誘惑猶大人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,12,"先知以利亞達信與約蘭、說、耶和華你祖大衛的　神如此說、因為你不行你父約沙法、和猶大王亞撒的道、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,13,"乃行以色列諸王的道、使猶大人和耶路撒冷的居民行邪淫、像亞哈家一樣、又殺了你父家比你好的諸兄弟．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,14,"故此耶和華降大災與你的百姓、和你的妻子、兒女、並你一切所有的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,15,"你的腸子必患病、日加沉重、以致你的腸子墜落下來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,16,"以後耶和華激動非利士人、和靠近古實的亞拉伯人、來攻擊約蘭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,17,"他們上來攻擊猶大、侵入境內、擄掠了王宮裡所有的財貨、和他的妻子、兒女．除了他小兒子約哈斯〔又名亞哈謝〕之外、沒有留下一個兒子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,18,"這些事以後、耶和華使約蘭的腸子、患不能醫治的病。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,19,"他患此病纏綿日久、過了二年、腸子墜落下來、病重而死。他的民沒有為他燒甚麼物件、像從前為他列祖所燒的一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",21,20,"約蘭登基的時候年三十二歲、在耶路撒冷作王八年．他去世無人思慕．眾人葬他在大衛城、只是不在列王的墳墓裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",22,1,"耶路撒冷的居民立約蘭的小兒子亞哈謝接續他作王．因為跟隨亞拉伯人來攻營的軍兵曾殺了亞哈謝的眾兄長．這樣、猶大王約蘭的兒子亞哈謝作了王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",22,2,"亞哈謝登基的時候年四十二歲．〔列王下八章二十六節作二十二歲〕在耶路撒冷作王一年．他母親名叫亞他利雅、是暗利的孫女。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",22,3,"亞哈謝也行亞哈家的道．因為他母親給他主謀．使他行惡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",22,4,"他行耶和華眼中看為惡的事、像亞哈家一樣．因他父親死後有亞哈家的人給他主謀、以致敗壞。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",22,5,"他聽從亞哈家的計謀、同以色列王亞哈的兒子約蘭往基列的拉末去、與亞蘭王哈薛爭戰．亞蘭人打傷了約蘭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",22,6,"約蘭回到耶斯列、醫治在拉末與亞蘭王哈薛打仗所受的傷、猶大王約蘭的兒子亞撒利雅、〔即亞哈謝〕因為亞哈的兒子約蘭病了、就下到耶斯列看望他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",22,7,"亞哈謝去見約蘭就被害了、這是出乎　神．因為他到了、就同約蘭出去攻擊寧示的孫子耶戶．這耶戶是耶和華所膏、使他剪除亞哈家的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",22,8,"耶戶討亞哈家罪的時候、遇見猶大的眾首領、和亞哈謝的眾姪子服事亞哈謝、就把他們都殺了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",22,9,"亞哈謝藏在撒瑪利亞、耶戶尋找他．眾人將他拿住、送到耶戶那裡、就殺了他、將他葬埋．因他們說、他是那盡心尋求耶和華之約沙法的兒子。這樣、亞哈謝的家無力保守國權。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",22,10,"亞哈謝的母親亞他利雅見他兒子死了、就起來剿滅猶大王室。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",22,11,"但王的女兒約示巴、將亞哈謝的兒子約阿施、從那被殺的王子中偷出來、把他和他的乳母都藏在臥房裡。約示巴是約蘭王的女兒、亞哈謝的妹子、祭司耶何耶大的妻．他收藏約阿施、躲避亞他利雅、免得被殺。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",22,12,"約阿施和他們一同藏在　神殿裡六年．亞他利雅篡了國位。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,1,"第七年耶何耶大奮勇自強、將百夫長耶羅罕的兒子亞撒利雅、約哈難的兒子以實瑪利、俄備得的兒子亞撒利雅、亞大雅的兒子瑪西雅、細基利的兒子以利沙法召來、與他們立約。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,2,"他們走遍猶大、從猶大各城裡招聚利未人、和以色列的眾族長、到耶路撒冷來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,3,"會眾在　神殿裡與王立約。耶何耶大對他們說、看哪、王的兒子必當作王、正如耶和華指著大衛子孫所應許的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,4,"又說、你們當這樣行．祭司和利未人、凡安息日進班的三分之一、要把守各門．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,5,"三分之一、要在王宮、三分之一、要在基址門．眾百姓要在耶和華殿的院內。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,6,"除了祭司、和供職的利未人之外、不准別人進耶和華的殿．惟獨他們可以進去、因為他們聖潔．眾百姓要遵守耶和華所吩咐的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,7,"利未人要手中各拿兵器、四圍護衛王．凡擅入殿宇的、必當治死．王出入的時候、你們當跟隨他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,8,"利未人、和猶大眾人、都照著祭司耶何耶大一切所吩咐的去行．各帶所管安息日進班出班的人來．因為祭司耶何耶大不許他們下班。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,9,"祭司耶何耶大便將　神殿裡所藏大衛王的槍、盾牌、擋牌、交給百夫長．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,10,"又分派眾民手中各拿兵器、在壇和殿那裡、從殿右直到殿左、站在王子的四圍。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,11,"於是領王子出來、給他戴上冠冕、將律法書交給他、立他作王．耶何耶大和眾子膏他．眾人說、願王萬歲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,12,"亞他利雅聽見民奔走、讚美王的聲音、就到民那裡、進耶和華的殿．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,13,"看見王站在殿門的柱旁、百夫長和吹號的人侍立在王左右．國民都歡樂吹號．又有歌唱的、用各樣的樂器、領人歌唱讚美、亞他利雅就撕裂衣服、喊叫說、反了、反了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,14,"祭司耶何耶大帶管轄軍兵的百夫長出來、吩咐他們說、將他趕到班外、凡跟隨他的必用刀殺死．因為祭司說、不可在耶和華殿裡殺他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,15,"眾兵就閃開、讓他去．他走到王宮的馬門、便在那裡把他殺了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,16,"耶何耶大、與眾民、和王立約、都要作耶和華的民。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,17,"於是眾民都到巴力廟、拆毀了廟、打碎壇和像、又在壇前將巴力的祭司瑪坦殺了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,18,"耶何耶大派官看守耶和華的殿、是在祭司利未人手下．這祭司利未人是大衛分派在耶和華殿中、照摩西律法上所寫的、給耶和華獻燔祭、又按大衛所定的例、歡樂歌唱．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,19,"且設立守門的把守耶和華殿的各門、無論為何事、不潔淨的人都不准進去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,20,"又率領百夫長、和貴冑、與民間的官長、並國中的眾民、請王從耶和華殿下來、由上門進入王宮、立王坐在國位上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",23,21,"國民都歡樂、合城都安靜。眾人已將亞他利雅用刀殺了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,1,"約阿施登基的時候年七歲、在耶路撒冷作王四十年．他母親名叫西比亞、是別是巴人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,2,"祭司耶何耶大在世的時候、約阿施行耶和華眼中看為正的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,3,"耶何耶大為他娶了兩個妻、並且生兒養女。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,4,"此後、約阿施有意重修耶和華的殿、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,5,"便召聚眾祭司和利未人、吩咐他們說、你們要往猶大各城去、使以色列眾人捐納銀子、每年可以修理你們　神的殿．你們要急速辦理這事。只是利未人不急速辦理。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,6,"王召了大祭司耶何耶大來、對他說、從前耶和華的僕人摩西、為法櫃的帳幕、與以色列會眾所定的捐項、你為何不叫利未人、照這例從猶大和耶路撒冷帶來、作殿的費用呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,7,"因為那惡婦亞他利雅的眾子曾拆毀　神的殿．又用耶和華殿中分別為聖的物供奉巴力。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,8,"於是王下令、眾人作了一櫃、放在耶和華殿的門外。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,9,"又通告猶大和耶路撒冷的百姓、要將　神僕人摩西、在曠野所吩咐以色列人的捐項給耶和華送來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,10,"眾首領和百姓都歡歡喜喜地將銀子送來、投入櫃中、直到捐完。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,11,"利未人見銀子多了、就把櫃抬到王所派的司事面前、王的書記和大祭司的屬員來、將櫃倒空、仍放在原處、日日都是這樣．積蓄的銀子甚多。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,12,"王與耶何耶大將銀子交給耶和華殿裡辦事的人．他們就雇了石匠、木匠、重修耶和華的殿．又雇了鐵匠、銅匠、修理耶和華的殿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,13,"工人操作、漸漸修成、將　神殿修造的、與從前一樣、而且甚是堅固。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,14,"工程完了、他們就把其餘的銀子、拿到王與耶何耶大面前、用以製造耶和華殿供奉所用的器皿、和調羹、並金銀的器皿。耶何耶大在世的時候、眾人常在耶和華殿裡獻燔祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,15,"耶何耶大年紀老邁、日子滿足而死．死的時候、年一百三十歲．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,16,"葬在大衛城列王的墳墓裡．因為他在以色列人中行善、又事奉　神、修理　神的殿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,17,"耶何耶大死後、猶大的眾首領來朝拜王．王就聽從他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,18,"他們離棄耶和華他們列祖　神的殿、去事奉亞舍拉和偶像．因他們這罪、就有忿怒臨到猶大和耶路撒冷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,19,"但　神仍遣先知到他們那裡、引導他們歸向耶和華．這先知警戒他們、他們卻不肯聽。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,20,"那時　神的靈感動祭司耶何耶大的兒子撒迦利亞．他就站在上面對民說、　神如此說、你們為何干犯耶和華的誡命、以致不得亨通呢．因為你們離棄耶和華、所以他也離棄你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,21,"眾民同心謀害撒迦利亞、就照王的吩咐、在耶和華殿的院內、用石頭打死他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,22,"這樣、約阿施王不想念撒迦利亞的父親耶何耶大向自己所施的恩、殺了他的兒子。撒迦利亞臨死的時候、說、願耶和華鑒察伸冤。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,23,"滿了一年、亞蘭的軍兵上來攻擊約阿施、來到猶大和耶路撒冷、殺了民中的眾首領、將所掠的財貨送到大馬色王那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,24,"亞蘭的軍兵、雖來了一小隊、耶和華卻將大隊的軍兵交在他們手裡、是因猶大人離棄耶和華他們列祖的　神．所以藉亞蘭人懲罰約阿施。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,25,"亞蘭人離開約阿施的時候、他患重病、臣僕背叛他、要報祭司耶何耶大兒子流血之仇、殺他在床上、葬他在大衛城．只是不葬在列王的墳墓裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,26,"背叛他的是亞捫婦人示米押的兒子撒拔、和摩押婦人示米利的兒子約薩拔。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",24,27,"至於他的眾子、和他所受的警戒、並他重修　神殿的事、都寫在列王的傳上。他兒子亞瑪謝接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,1,"亞瑪謝登基的時候、年二十五歲、在耶路撒冷作王二十九年．他母親名叫約耶但、是耶路撒冷人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,2,"亞瑪謝行耶和華眼中看為正的事、只是心不專誠。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,3,"國一堅定、就把殺他父王的臣僕殺了、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,4,"卻沒有治死他們的兒子、是照摩西律法書上耶和華所吩咐的、說、不可因子殺父、也不可因父殺子、各人要為本身的罪而死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,5,"亞瑪謝招聚猶大人、按著猶大和便雅憫的宗族、設立千夫長、百夫長．又數點人數、從二十歲以外、能拿槍拿盾牌、出去打仗的精兵、共有三十萬。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,6,"又用銀子一百他連得、從以色列招募了十萬大能的勇士。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,7,"有一個神人來見亞瑪謝、對他說、王阿、不要使以色列的軍兵與你同去、因為耶和華不與以色列人以法蓮的後裔同在。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,8,"你若一定要去、就奮勇爭戰罷．但　神必使你敗在敵人面前、因為　神能助人得勝、也能使人傾敗。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,9,"亞瑪謝問神人說、我給了以色列軍的那一百他連得銀子怎麼樣呢．神人回答說、耶和華能把更多的賜給你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,10,"於是亞瑪謝將那從以法蓮來的軍兵分別出來、叫他們回家去．故此他們甚惱怒猶大人、氣忿忿地回家去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,11,"亞瑪謝壯起膽來、率領他的民到鹽谷、殺了西珥人一萬。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,12,"猶大人又生擒了一萬帶到山崖上、從那裡把他們扔下去、以致他們都摔碎了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,13,"但亞瑪謝所打發回去、不許一同出征的那些軍兵、攻打猶大各城、從撒瑪利亞直到伯和崙、殺了三千人、搶了許多財物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,14,"亞瑪謝殺了以東人回來、就把西珥的神像帶回、立為自己的神、在他面前叩拜燒香。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,15,"因此、耶和華的怒氣向亞瑪謝發作、就差一個先知去見他、說、這些神不能救他的民脫離你的手、你為何尋求他呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,16,"先知與王說話的時候、王對他說、誰立你作王的謀士呢．你住口罷、為何找打呢．先知就止住了．又說、你行這事不聽從我的勸戒、我知道　神定意要滅你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,17,"猶大王亞瑪謝與群臣商議、就差遣使者去見耶戶的孫子約哈斯的兒子以色列王約阿施說、你來、我們二人相見於戰場。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,18,"以色列王約阿施差遣使者去見猶大王亞瑪謝說、利巴嫩的蒺藜、差遣使者去見利巴嫩的香柏樹、說、將你的女兒給我兒子為妻．後來利巴嫩有一個野獸經過、把蒺藜踐踏了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,19,"你說、看哪、我打敗了以東人、你就心高氣傲、以致矜誇．你在家裡安居就罷了、為何要惹禍使自己和猶大國一同敗亡呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,20,"亞瑪謝卻不肯聽從．這是出乎　神、好將他們交在敵人手裡、因為他們尋求以東的神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,21,"於是以色列王約阿施上來、在猶大的伯示麥與猶大王亞瑪謝相見於戰場。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,22,"猶大人敗在以色列人面前、各自逃回家裡去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,23,"以色列王約阿施、在伯示麥擒住約哈斯〔就是亞哈謝〕的孫子約阿施的兒子猶大王亞瑪謝、將他帶到耶路撒冷．又拆毀耶路撒冷的城牆、從以法蓮門、直到角門、共四百肘。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,24,"又將俄別以東所看守　神殿裡的一切金銀、和器皿、與王宮裡的財寶、都拿了去．並帶人去為質、就回撒瑪利亞去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,25,"以色列王約哈斯的兒子約阿施死後、猶大王約阿施的兒子亞瑪謝又活了十五年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,26,"亞瑪謝其餘的事、自始至終、不都寫在猶大和以色列諸王記上麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,27,"自從亞瑪謝離棄耶和華之後、在耶路撒冷有人背叛他、他就逃到拉吉．叛黨卻打發人到拉吉、將他殺了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",25,28,"人就用馬將他的屍首馱回、葬在猶大京城、他列祖的墳地裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,1,"猶大眾民立亞瑪謝的兒子烏西雅〔又名亞撒利雅〕接續他父作王．那時他年十六歲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,2,"亞瑪謝與他列祖同睡之後、烏西雅收回以祿仍歸猶大、又重新修理。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,3,"烏西雅登基的時候、年十六歲、在耶路撒冷作王五十二年．他母親名叫耶可利雅、是耶路撒冷人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,4,"烏西雅行耶和華眼中看為正的事、效法他父亞瑪謝一切所行的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,5,"通曉　神默示撒迦利亞在世的時候、烏西雅定意尋求　神、他尋求、耶和華、　神就使他亨通。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,6,"他出去攻擊非利士人、拆毀了迦特城、雅比尼城、和亞實突城．在非利士人中、在亞實突境內、又建築了些城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,7,"　神幫助他攻擊非利士人、和住在姑珥巴力的亞拉伯人、並米烏尼人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,8,"亞捫人給烏西雅進貢．他的名聲傳到埃及、因他甚是強盛。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,9,"烏西雅在耶路撒冷的角門和谷門、並城牆轉彎之處、建築城樓、且甚堅固。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,10,"又在曠野與高原和平原、建築望樓、挖了許多井、因他的牲畜甚多．又在山地和佳美之地、有農夫和修理葡萄園的人、因為他喜悅農事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,11,"烏西雅又有軍兵、照書記耶利和官長瑪西雅所數點的、在王的一個將軍哈拿尼雅手下、分隊出戰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,12,"族長大能勇士的總數共有二千六百人、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,13,"他們手下的軍兵、共有三十萬七千五百人、都有大能、善於爭戰、幫助王攻擊仇敵。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,14,"烏西雅為全軍預備盾牌、槍、盔、甲、弓、和甩石的機弦。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,15,"又在耶路撒冷使巧匠作機器、安在城樓和角樓上、用以射箭發石．烏西雅的名聲傳到遠方、因為他得了非常的幫助、甚是強盛。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,16,"他既強盛、就心高氣傲、以致行事邪僻、干犯耶和華他的　神、進耶和華的殿、要在香壇上燒香。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,17,"祭司亞撒利雅率領耶和華勇敢的祭司八十人、跟隨他進去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,18,"他們就阻擋烏西雅王、對他說、烏西雅阿、給耶和華燒香不是你的事、乃是亞倫子孫承接聖職祭司的事．你出聖殿罷、因為你犯了罪．你行這事耶和華　神必不使你得榮耀。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,19,"烏西雅就發怒、手拿香爐要燒香．他向祭司發怒的時候、在耶和華殿中香壇旁眾祭司面前、額上忽然發出大痲瘋。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,20,"大祭司亞撒利雅和眾祭司觀看、見他額上發出大痲瘋、就催他出殿、他自己也急速出去、因為耶和華降災與他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,21,"烏西雅王長大痲瘋直到死日、因此住在別的宮裡、與耶和華的殿隔絕．他兒子約坦管理家事、治理國民。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,22,"烏西雅其餘的事、自始至終、都是亞摩斯的兒子先知以賽亞所記的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",26,23,"烏西雅與他列祖同睡、葬在王陵的田間他列祖的墳地裡、因為人說、他是長大痲瘋的．他兒子約坦接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",27,1,"約坦登基的時候、年二十五歲、在耶路撒冷作王十六年．他母親名叫耶路沙、是撒督的女兒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",27,2,"約坦行耶和華眼中看為正的事、效法他父烏西雅一切所行的、只是不入耶和華的殿。百姓還行邪僻的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",27,3,"約坦建立耶和華殿的上門．在俄斐勒城上多有建造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",27,4,"又在猶大山地建造城邑、在樹林中建築營寨、和高樓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",27,5,"約坦與亞捫人的王打仗勝了他們．當年他們進貢銀一百他連得、小麥一萬歌珥、大麥一萬歌珥、第二年、第三年、也是這樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",27,6,"約坦在耶和華他　神面前行正道、以致日漸強盛。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",27,7,"約坦其餘的事、和一切爭戰、並他的行為、都寫在以色列和猶大列王記上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",27,8,"他登基的時候年二十五歲、在耶路撒冷作王十六年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",27,9,"約坦與他列祖同睡、葬在大衛城裡．他兒子亞哈斯接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,1,"亞哈斯登基的時候、年二十歲、在耶路撒冷作王十六年．不像他祖大衛行耶和華眼中看為正的事、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,2,"卻行以色列諸王的道、又鑄造巴力的像．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,3,"並且在欣嫩子谷燒香、用火焚燒他的兒女、行耶和華在以色列人面前所驅逐的外邦人那可憎的事．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,4,"並在丘壇上、山岡上、各青翠樹下獻祭燒香。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,5,"所以耶和華他的　神將他交在亞蘭王手裡、亞蘭王打敗他、擄了他許多的民、帶到大馬色去。　神又將他交在以色列王手裡、以色列王向他大行殺戮。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,6,"利瑪利的兒子比加、一日殺了猶大人十二萬、都是勇士、因為他們離棄了耶和華他們列祖的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,7,"有一個以法蓮中的勇士、名叫細基利、殺了王的兒子瑪西雅、和管理王宮的押斯利甘、並宰相以利加拿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,8,"以色列人擄了他們的弟兄、連婦人帶兒女共有二十萬．又掠了許多的財物、帶到撒瑪利亞去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,9,"但那裡有耶和華的一個先知、名叫俄德、出來迎接往撒瑪利亞去的軍兵、對他們說、因為耶和華你們列祖的　神惱怒猶大人、所以將他們交在你們手裡、你們竟怒氣沖天、大行殺戮。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,10,"如今你們又有意、強逼猶大人和耶路撒冷人作你們的奴婢．你們豈不也有得罪耶和華你們　神的事麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,11,"現在你們當聽我說、要將擄來的弟兄釋放回去、因為耶和華向你們已經大發烈怒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,12,"於是以法蓮人的幾個族長、就是約哈難的兒子亞撒利雅、米實利末的兒子比利家、沙龍的兒子耶希西家、哈得萊的兒子亞瑪撒、起來攔擋出兵回來的人、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,13,"對他們說、你們不可帶進這被擄的人來．你們想要使我們得罪耶和華、加增我們的罪惡過犯、因為我們的罪過甚大、已經有烈怒臨到以色列人了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,14,"於是帶兵器的人將擄來的人口、和掠來的財物、都留在眾首領和會眾的面前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,15,"以上提名的那些人就站起、使被擄的人前來、其中有赤身的、就從所掠的財物中拿出衣服和鞋來、給他們穿．又給他們喫喝、用膏抹他們．其中有軟弱的、就使他們騎驢、送到棕樹城耶利哥他們弟兄那裡．隨後就回撒瑪利亞去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,16,"那時、亞哈斯王差遣人去見亞述諸王、求他們幫助、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,17,"因為以東人又來攻擊猶大、擄掠子民。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,18,"非利士人也來侵佔高原、和猶大南方的城邑、取了伯示麥、亞雅崙、基低羅、梭哥、和屬梭哥的鄉村．亭納和屬亭納的鄉村．瑾鎖和屬瑾鎖的鄉村、就住在那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,19,"因為以色列王亞哈斯在猶大放肆、大大干犯耶和華、所以耶和華使猶大卑微。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,20,"亞述王提革拉毗尼色上來、卻沒有幫助他、反倒欺凌他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,21,"亞哈斯從耶和華殿裡和王宮中、並首領家內所取的財寶給了亞述王．這也無濟於事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,22,"這亞哈斯王在急難的時候、越發得罪耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,23,"他祭祀攻擊他的大馬色之神、說、因為亞蘭王的神幫助他們、我也獻祭與他、他好幫助我．但那些神使他和以色列眾人敗亡了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,24,"亞哈斯將　神殿裡的器皿都聚了來、毀壞了．且封鎖耶和華殿的門．在耶路撒冷各處的拐角建築祭壇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,25,"又在猶大各城建立丘壇、與別神燒香、惹動耶和華他列祖　神的怒氣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,26,"亞哈斯其餘的事、和他的行為、自始至終都寫在猶大和以色列諸王記上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",28,27,"亞哈斯與他列祖同睡、葬在耶路撒冷城裡、沒有送入以色列諸王的墳墓中．他兒子希西家接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,1,"希西家登基的時候、年二十五歲、在耶路撒冷作王二十九年．他母親名叫亞比雅、是撒迦利雅的女兒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,2,"希西家行耶和華眼中看為正的事、效法他祖大衛一切所行的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,3,"元年正月、開了耶和華殿的門、重新修理。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,4,"他召眾祭司和利未人來、聚集在東邊的寬闊處、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,5,"對他們說、利未人哪、當聽我說、現在你們要潔淨自己、又潔淨耶和華你們列祖　神的殿、從聖所中除去污穢之物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,6,"我們列祖犯了罪、行耶和華我們　神眼中看為惡的事、離棄他、轉臉背向他的居所、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,7,"封鎖廊門、吹滅燈火、不在聖所中向以色列　神燒香、或獻燔祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,8,"因此、耶和華的忿怒臨到猶大和耶路撒冷、將其中的人拋來拋去、令人驚駭、嗤笑、正如你們親眼所見的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,9,"所以我們的祖宗倒在刀下、我們的妻子兒女也被擄掠。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,10,"現在我心中有意與耶和華以色列的　神立約、好使他的烈怒轉離我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,11,"我的眾子阿、現在不要懈怠、因為耶和華揀選你們站在他面前事奉他、與他燒香。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,12,"於是利未人哥轄的子孫亞瑪賽的兒子瑪哈、亞撒利雅的兒子約珥、米拉利的子孫亞伯底的兒子基士、耶哈利勒的兒子亞撒利雅、革順的子孫薪瑪的兒子約亞、約亞的兒子伊甸、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,13,"以利撒反的子孫申利和耶利、亞薩的子孫撒迦利雅、和瑪探雅、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,14,"希幔的子孫耶歇和示每、耶杜頓的子孫示瑪雅和烏薛、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,15,"起來聚集他們的弟兄、潔淨自己、照著王的吩咐、耶和華的命令、進去潔淨耶和華的殿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,16,"祭司進入耶和華的殿要潔淨殿、將殿中所有污穢之物搬到耶和華殿的院內、利未人接去、搬到外頭汲淪溪邊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,17,"從正月初一日潔淨起、初八日到了耶和華的殿廊、用八日的工夫潔淨耶和華的殿、到正月十六日纔潔淨完了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,18,"於是他們晉見希西家王說、我們已將耶和華的全殿、和燔祭壇、並壇的一切器皿、陳設餅的桌子、與桌子的一切器皿都潔淨了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,19,"並且亞哈斯王在位犯罪的時候所廢棄的器皿、我們預備齊全、且潔淨了．現今都在耶和華的壇前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,20,"希西家王清早起來、聚集城裡的首領都上耶和華的殿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,21,"牽了七隻公牛、七隻公羊、七隻羊羔、七隻公山羊、要為國、為殿、為猶大人、作贖罪祭．王吩咐亞倫的子孫眾祭司、獻在耶和華的壇上、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,22,"就宰了公牛、祭司接血灑在壇上、宰了公羊、把血灑在壇上、又宰了羊羔、也把血灑在壇上．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,23,"把那作贖罪祭的公山羊、牽到王和會眾面前、他們就按手在其上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,24,"祭司宰了羊、將血獻在壇上作贖罪祭、為以色列眾人贖罪、因為王吩咐將燔祭、和贖罪祭、為以色列眾人獻上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,25,"王又派利未人在耶和華殿中敲鈸、鼓瑟、彈琴、乃照大衛和他先見迦得、並先知拿單所吩咐的、就是耶和華藉先知所吩咐的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,26,"利未人拿大衛的樂器、祭司拿號、一同站立。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,27,"希西家吩咐在壇上獻燔祭、燔祭一獻、就唱讚美耶和華的歌、用號、並用以色列王大衛的樂器相和。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,28,"會眾都敬拜、歌唱的歌唱、吹號的吹號、如此直到燔祭獻完了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,29,"獻完了祭、王和一切跟隨的人都俯伏敬拜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,30,"希西家王與眾首領、又吩咐利未人用大衛和先見亞薩的詩詞、頌讚耶和華．他們就歡歡喜喜地頌讚耶和華、低頭敬拜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,31,"希西家說、你們既然歸耶和華為聖、就要前來把祭物和感謝祭奉到耶和華殿裡．會眾就把祭物和感謝祭奉來、凡甘心樂意的也將燔祭奉來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,32,"會眾所奉的燔祭如下．公牛七十隻、公羊一百隻、羊羔二百隻．這都是作燔祭獻給耶和華的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,33,"又有分別為聖之物、公牛六百隻、綿羊三千隻。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,34,"但祭司太少、不能剝盡燔祭牲的皮、所以他們的弟兄利未人幫助他們、直等燔祭的事完了、又等別的祭司自潔了、因為利未人誠心自潔、勝過祭司。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,35,"燔祭和平安祭牲的脂油、並燔祭同獻的奠祭甚多．這樣、耶和華殿中的事務俱都齊備了。〔或作就整頓了〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",29,36,"這事辦的甚速、希西家和眾民都喜樂、是因　神為眾民所預備的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,1,"希西家差遣人去見以色列和猶大眾人、又寫信給以法蓮和瑪拿西人、叫他們到耶路撒冷耶和華的殿、向耶和華以色列的　神守逾越節．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,2,"因為王和眾首領、並耶路撒冷全會眾已經商議、要在二月內守逾越節。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,3,"正月〔原文作那時〕間他們不能守、因為自潔的祭司尚不敷用、百姓也沒有聚集在耶路撒冷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,4,"王與全會眾都以這事為善。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,5,"於是定了命令、傳遍以色列、從別是巴直到但、使他們都來、在耶路撒冷向耶和華以色列的　神守逾越節、因為照所寫的例、守這節的不多了。〔或作因為民許久沒有照所寫的例守節了〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,6,"驛卒就把王和眾首領的信、遵著王命傳遍以色列和猶大．信內說、以色列人哪、你們當轉向耶和華亞伯拉罕、以撒、以色列的　神、好叫他轉向你們這脫離亞述王手的餘民。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,7,"你們不要效法你們列祖、和你們的弟兄、他們干犯耶和華他們列祖的　神、以致耶和華丟棄他們、使他們敗亡、〔或作令人驚駭〕正如你們所見的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,8,"現在不要像你們列祖硬著頸項、只要歸順耶和華、進入他的聖所、就是永遠成聖的居所．又要事奉耶和華你們的　神、好使他的烈怒轉離你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,9,"你們若轉向耶和華、你們的弟兄和兒女、必在擄掠他們的人面前蒙憐恤、得以歸回這地、因為耶和華你們的　神有恩典、施憐憫．你們若轉向他、他必不轉臉不顧你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,10,"驛卒就由這城跑到那城、傳遍了以法蓮、瑪拿西、直到西布倫．那裡的人卻戲笑他們、譏誚他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,11,"然而亞設、瑪拿西、西布倫中、也有人自卑、來到耶路撒冷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,12,"　神也感動猶大人、使他們一心遵行王與眾首領憑耶和華之言所發的命令。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,13,"二月、有許多人在耶路撒冷聚集、成為大會、要守除酵節。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,14,"他們起來、把耶路撒冷的祭壇、和燒香的壇、盡都除去、拋在汲淪溪中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,15,"二月十四日、宰了逾越節的羊羔．祭司與利未人覺得慚愧、就潔淨自己、把燔祭奉到耶和華殿中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,16,"遵著神人摩西的律法、照例站在自己的地方．祭司從利未人手裡接過血來、灑在壇上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,17,"會中有許多人尚未自潔、所以利未人為一切不潔之人宰逾越節的羊羔、使他們在耶和華面前成為聖潔。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,18,"以法蓮、瑪拿西、以薩迦、西布倫、有許多人尚未自潔、他們卻也喫逾越節的羊羔、不合所記錄的定例．希西家為他們禱告、說、凡專心尋求　神、就是耶和華他列祖之　神的、雖不照著聖所潔淨之禮自潔、求至善的耶和華也饒恕他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,19,"見上節")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,20,"耶和華垂聽希西家的禱告、就饒恕〔原文作醫治〕百姓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,21,"在耶路撒冷的以色列人、大大喜樂、守除酵節七日．利未人和祭司、用響亮的樂器、日日頌讚耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,22,"希西家慰勞一切善於事奉耶和華的利未人．於是眾人喫節筵七日、又獻平安祭、且向耶和華他們列祖的　神認罪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,23,"全會眾商議、要再守節七日．於是歡歡喜喜地又守節七日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,24,"猶大王希西家賜給會眾公牛一千隻、羊七千隻為祭物．眾首領也賜給會眾公牛一千隻、羊一萬隻．並有許多的祭司潔淨自己。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,25,"猶大全會眾、祭司利未人、並那從以色列地來的會眾、和寄居的人、以及猶大寄居的人、盡都喜樂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,26,"這樣、在耶路撒冷大有喜樂、自從以色列王大衛兒子所羅門的時候、在耶路撒冷沒有這樣的喜樂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",30,27,"那時、祭司利未人起來、為民祝福．他們的聲音、蒙　神垂聽、他們的禱告、達到天上的聖所。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,1,"這事既都完畢、在那裡的以色列眾人、就到猶大的城邑、打碎柱像、砍斷木偶．又在猶大、便雅憫、以法蓮、瑪拿西遍地、將丘壇和祭壇拆毀淨盡．於是以色列眾人各回各城、各歸各地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,2,"希西家派定祭司利未人的班次、各按各職獻燔祭和平安祭．又在耶和華殿〔原文作營〕門內事奉、稱謝、頌讚耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,3,"王又從自己的產業中定出分來為燔祭、就是早晚的燔祭、和安息日、月朔、並節期的燔祭．都是按耶和華律法上所載的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,4,"又吩咐住耶路撒冷的百姓、將祭司利未人所應得的分給他們、使他們專心遵守耶和華的律法。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,5,"諭旨一出、以色列人就把初熟的五穀、新酒、油、蜜、和田地的出產、多多送來、又把各物的十分之一、送來的極多。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,6,"住猶大各城的以色列人、和猶大人、也將牛羊的十分之一、並分別為聖歸耶和華他們　神之物、就是十分取一之物、盡都送來、積成堆壘。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,7,"從三月積起、到七月纔完。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,8,"希西家和眾首領來看見堆壘、就稱頌耶和華。又為耶和華的民以色列人祝福。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,9,"希西家向祭司利未人查問這堆壘。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,10,"撒督家的大祭司亞撒利雅、回答說、自從民將供物送到耶和華殿以來、我們不但喫飽、且剩下的甚多、因為耶和華賜福給他的民．所剩下的纔這樣豐盛。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,11,"希西家吩咐在耶和華殿裡預備倉房、他們就預備了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,12,"他們誠心將供物和十分取一之物、並分別為聖之物、都搬入倉內．利未人歌楠雅掌管這事．他兄弟示每為副管。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,13,"耶歇、亞撒細雅、拿哈、亞撒黑、耶利末、約撒拔、以列、伊斯瑪基雅、瑪哈、比拿雅、都是督理、在歌楠雅和他兄弟示每的手下、是希西家王和管理　神殿的亞撒利雅所派的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,14,"守東門的利未人音拿的兒子可利、掌管樂意獻與　神的禮物、發放獻與耶和華的供物、和至聖的物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,15,"在他手下有伊甸、玟雅玟、耶書亞、示瑪雅、亞瑪利雅、示迦尼雅、在祭司的各城裡供緊要的職任、無論弟兄大小、都按著班次分給他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,16,"按家譜三歲以外的男丁、凡每日進耶和華殿、按班次供職的、也分給他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,17,"又按宗族家譜分給祭司、按班次職任分給二十歲以外的利未人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,18,"又按家譜計算、分給他們會中的妻子、兒女、因他們身供要職、自潔成聖。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,19,"按名派定的人、要把應得的、分給亞倫子孫、住在各城郊野祭司所有的男丁、和一切載入家譜的利未人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,20,"希西家在猶大遍地這樣辦理、行耶和華他　神眼中看為善為正為忠的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",31,21,"凡他所行的、無論是辦　神殿的事、是遵律法守誡命、是尋求他的　神、都是盡心去行、無不亨通。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,1,"這虔誠的事以後、亞述王西拿基立、來侵入猶大、圍困一切堅固城、想要攻破佔據。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,2,"希西家見西拿基立來、定意要攻打耶路撒冷、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,3,"就與首領和勇士商議、塞住城外的泉源、他們就都幫助他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,4,"於是有許多人聚集、塞了一切泉源、並通流國中的小河、說、亞述王來、為何讓他得著許多水呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,5,"希西家力圖自強、就修築所有拆毀的城牆、高與城樓相齊、在城外又築一城、堅固大衛城的米羅、製造了許多軍器、盾牌．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,6,"設立軍長管理百姓、將他們招聚在城門的寬闊處、用話勉勵他們、說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,7,"你們當剛強壯膽、不要因亞述王和跟隨他的大軍恐懼、驚慌、因為與我們同在的、比與他們同在的更大．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,8,"與他們同在的是肉臂、與我們同在的是耶和華我們的　神、他必幫助我們、為我們爭戰．百姓就靠猶大王希西家的話、安然無懼了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,9,"此後、亞述王西拿基立、和他的全軍攻打拉吉、就差遣臣僕到耶路撒冷見猶大王希西家、和一切在耶路撒冷的猶大人、說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,10,"亞述王西拿基立如此說、你們倚靠甚麼、還在耶路撒冷受困呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,11,"希西家對你們說、耶和華我們的　神必救我們脫離亞述王的手、這不是誘惑你們、使你們受飢渴而死麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,12,"這希西家豈不是廢去耶和華的丘壇和祭壇、吩咐猶大與耶路撒冷的人說你們當在一個壇前敬拜、在其上燒香麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,13,"我與我列祖向列邦所行的、你們豈不知道麼．列邦的神、何嘗能救自己的國脫離我手呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,14,"我列祖所滅的國、那些神中誰能救自己的民脫離我手呢、難道你們的神能救你們脫離我手麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,15,"所以你們不要叫希西家這樣欺哄誘惑你們、也不要信他、因為沒有一國一邦的神、能救自己的民脫離我手、和我列祖的手、何況你們的神、更不能救你們脫離我的手。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,16,"西拿基立的臣僕、還有別的話毀謗耶和華　神、和他僕人希西家。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,17,"西拿基立也寫信毀謗耶和華以色列的　神、說、列邦的神既不能救他的民脫離我手、希西家的神也不能救他的民脫離我手了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,18,"亞述王的臣僕用猶大言語、向耶路撒冷城上的民大聲呼叫、要驚嚇他們、擾亂他們、以便取城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,19,"他們論耶路撒冷的　神、如同論世上人手所造的神一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,20,"希西家王和亞摩斯的兒子先知以賽亞、因此禱告、向天呼求。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,21,"耶和華就差遣一個使者、進入亞述王營中、把所有大能的勇士、和官長、將帥、盡都滅了．亞述王滿面含羞地回到本國、進了他神的廟中、有他親生的兒子在那裡用刀殺了他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,22,"這樣、耶和華救希西家和耶路撒冷的居民、脫離亞述王西拿基立的手、也脫離一切仇敵的手、又賜他們四境平安。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,23,"有許多人到耶路撒冷、將供物獻與耶和華、又將寶物送給猶大王希西家．此後、希西家在列邦人的眼中看為尊大。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,24,"那時希西家病得要死、就禱告耶和華、耶和華應允他、賜他一個兆頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,25,"希西家卻沒有照他所蒙的恩、報答耶和華、因他心裡驕傲、所以忿怒要臨到他和猶大並耶路撒冷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,26,"但希西家和耶路撒冷的居民、覺得心裡驕傲、就一同自卑、以致耶和華的忿怒在希西家的日子、沒有臨到他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,27,"希西家大有尊榮資財．建造府庫、收藏金銀、寶石、香料、盾牌、和各樣的寶器．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,28,"又建造倉房、收藏五榖、新酒、和油．又為各類牲畜、蓋棚立圈。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,29,"並且建立城邑、還有許多的羊群牛群、因為　神賜他極多的財產。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,30,"這希西家也塞住基訓的上源、引水直下、流在大衛城的西邊．希西家所行的事、盡都亨通。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,31,"惟有一件事、就是巴比倫王差遣使者來見希西家、訪問國中所現的奇事、這件事　神離開他、要試驗他、好知道他心內如何。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,32,"希西家其餘的事、和他的善行、都寫在亞摩斯的兒子先知以賽亞的默示書上、和猶大、以色列的諸王記上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",32,33,"希西家與他列祖同睡、葬在大衛子孫的高陵上．他死的時候．猶大人和耶路撒冷的居民、都尊敬他．他兒子瑪拿西接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,1,"瑪拿西登基的時候、年十二歲、在耶路撒冷作王五十五年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,2,"他行耶和華眼中看為惡的事、效法耶和華在以色列人面前趕出的外邦人那可憎的事．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,3,"重新建築他父希西家所拆毀的丘壇、又為巴力築壇、作木偶、且敬拜事奉天上的萬像。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,4,"在耶和華的殿宇中築壇．耶和華曾指著這殿說、我的名必永遠在耶路撒冷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,5,"他在耶和華殿的兩院中、為天上的萬像築壇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,6,"並在欣嫩子谷、使他的兒女經火．又觀兆、用法術、行邪術、立交鬼的、和行巫術的、多行耶和華眼中看為惡的事、惹動他的怒氣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,7,"又在　神殿內立雕刻的偶像．　神曾對大衛和他兒子所羅門說、我在以色列各支派中、所選擇的耶路撒冷和這殿、必立我的名直到永遠．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,8,"以色列人若謹守遵行我藉摩西所吩咐他們的一切法度、律例、典章、我就不再使他們挪移離開我所賜給他們列祖之地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,9,"瑪拿西引誘猶大和耶路撒冷的居民、以致他們行惡比耶和華在以色列人面前所滅的列國更甚。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,10,"耶和華警戒瑪拿西和他的百姓、他們卻是不聽。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,11,"所以耶和華使亞述王的將帥來攻擊他們、用鐃鉤鉤住瑪拿西、用銅鍊鎖住他、帶到巴比倫去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,12,"他在急難的時候、就懇求耶和華他的　神、且在他列祖的　神面前極其自卑。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,13,"他祈禱耶和華、耶和華就允准他的祈求、垂聽他的禱告、使他歸回耶路撒冷、仍坐國位．瑪拿西這纔知道惟獨耶和華是　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,14,"此後瑪拿西在大衛城外、從谷內基訓西邊、直到魚門口、建築城牆、環繞俄斐勒、這牆築得甚高．又在猶大各堅固城內、設立勇敢的軍長。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,15,"並除掉外邦人的神像、與耶和華殿中的偶像、又將他在耶和華殿的山上、和耶路撒冷所築的各壇、都拆毀拋在城外。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,16,"重修耶和華的祭壇、在壇上獻平安祭、感謝祭、吩咐猶大人事奉耶和華以色列的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,17,"百姓卻仍在丘壇上獻祭、只獻給耶和華他們的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,18,"瑪拿西其餘的事、和禱告他　神的話、並先見奉耶和華以色列　神的名警戒他的言語、都寫在以色列諸王記上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,19,"他的禱告、與　神怎樣應允他、他未自卑以前的罪愆過犯、並在何處建築丘壇、設立亞舍拉和雕刻的偶像、都寫在何賽的書上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,20,"瑪拿西與他列祖同睡、葬在自己的宮院裡．他兒子亞們接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,21,"亞們登基的時候、年二十二歲、在耶路撒冷作王二年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,22,"他行耶和華眼中看為惡的事、效法他父瑪拿西所行的、祭祀事奉他父瑪拿西所雕刻的偶像。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,23,"不在耶和華面前像他父瑪拿西自卑．這亞們所犯的罪、越犯越大。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,24,"他的臣僕背叛、在宮裡殺了他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",33,25,"但國民殺了那些背叛亞們王的人、立他兒子約西亞接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,1,"約西亞登基的時候、年八歲、在耶路撒冷作王三十一年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,2,"他行耶和華眼中看為正的事、效法他祖大衛所行的、不偏左右。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,3,"他作王第八年、尚且年幼、就尋求他祖大衛的　神．到了十二年、纔潔淨猶大和耶路撒冷、除掉丘壇、木偶、雕刻的像、和鑄造的像。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,4,"眾人在他面前拆毀巴力的壇、砍斷壇上高高的日像、又把木偶和雕刻的像、並鑄造的像打碎成灰、撒在祭偶像人的墳上、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,5,"將他們祭司的骸骨燒在壇上、潔淨了猶大和耶路撒冷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,6,"又在瑪拿西、以法蓮、西緬、拿弗他利各城、和四圍破壞之處、都這樣行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,7,"又拆毀祭壇、把木偶和雕刻的像打碎成灰、砍斷以色列遍地所有的日像、就回耶路撒冷去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,8,"約西亞王十八年、淨地淨殿之後、就差遣亞薩利雅的兒子沙番、邑宰瑪西雅、約哈斯的兒子史官約亞、去修理耶和華他　神的殿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,9,"他們就去見大祭司希勒家、將奉到　神殿的銀子交給他．這銀子是看守殿門的利未人、從瑪拿西、以法蓮、和一切以色列剩下的人、以及猶大、便雅憫眾人、並耶路撒冷的居民收來的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,10,"又將這銀子交給耶和華殿裡督工的、轉交修理耶和華殿的工匠．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,11,"就是交給木匠、石匠、買鑿成的石頭、和架木、與棟梁、修猶大王所毀壞的殿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,12,"這些人辦事誠實．督工的是利未人米拉利的子孫雅哈、俄巴底．督催的是哥轄的子孫撒迦利亞、米書蘭、還有善於作樂的利未人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,13,"他們又監管扛抬的人、督催一切作工的．利未人中、也有作書記、作司事、作守門的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,14,"他們將奉到耶和華殿的銀子運出來的時候、祭司希勒家偶然得了摩西所傳耶和華的律法書。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,15,"希勒家對書記沙番說、我在耶和華殿裡得了律法書、遂將書遞給沙番。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,16,"沙番把書拿到王那裡、回覆王說、凡交給僕人們辦的都辦理了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,17,"耶和華殿裡的銀子倒出來、交給督工的、和匠人的手裡了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,18,"書記沙番又對王說、祭司希勒家遞給我一卷書．沙番就在王面前讀那書。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,19,"王聽見律法上的話、就撕裂衣服、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,20,"吩咐希勒家與沙番的兒子亞希甘、米迦的兒子亞比頓、書記沙番、和王的臣僕亞撒雅、說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,21,"你們去為我為以色列和猶大剩下的人、以這書上的話求問耶和華、因我們列祖沒有遵守耶和華的言語、沒有照這書上所記的去行、耶和華的烈怒就倒在我們身上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,22,"於是希勒家和王所派的眾人、都去見女先知戶勒大．戶勒大是掌管禮服沙龍的妻、沙龍是哈斯拉的孫子特瓦的兒子．戶勒大住在耶路撒冷第二區．他們請問於他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,23,"他對他們說、耶和華以色列的　神如此說、你們可以回覆那差遣你們來見我的人說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,24,"耶和華如此說、我必照著在猶大王面前所讀那書上的一切咒詛、降禍與這地、和其上的居民．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,25,"因為他們離棄我、向別神燒香、用他們手所作的惹我發怒、所以我的忿怒如火倒在這地上、總不息滅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,26,"然而差遣你們來求問耶和華的猶大王、你們要這樣回覆他說、耶和華以色列的　神如此說、至於你所聽見的話、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,27,"就是聽見我指著這地和其上居民所說的話、你便心裡敬服、在我面前自卑、撕裂衣服、向我哭泣、因此我應允了你．這是我耶和華說的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,28,"我必使你平平安安地歸到墳墓、到你列祖那裡、我要降與這地和其上居民的一切災禍、你也不至親眼看見．他們就回覆王去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,29,"王差遣人招聚猶大和耶路撒冷的眾長老來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,30,"王和猶大眾人、與耶路撒冷的居民、並祭司利未人、以及所有的百姓、無論大小、都一同上到耶和華的殿．王就把殿裡所得的約書念給他們聽。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,31,"王站在他的地位上、在耶和華面前立約、要盡心盡性的順從耶和華、遵守他的誡命、法度、律例、成就這書上所記的約言．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,32,"又使住耶路撒冷和便雅憫的人、都服從這約．於是耶路撒冷的居民、都遵行他們列祖之　神的約。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",34,33,"約西亞從以色列各處、將一切可憎之物盡都除掉、使以色列境內的人、都事奉耶和華他們的　神。約西亞在世的日子、就跟從耶和華他們列祖的　神、總不離開。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,1,"約西亞在耶路撒冷向耶和華守逾越節．正月十四日、就宰了逾越節的羊羔。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,2,"王分派祭司各盡其職、又勉勵他們辦耶和華殿中的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,3,"又對那歸耶和華為聖、教訓以色列人的利未人說、你們將聖約櫃安放在以色列王大衛兒子所羅門建造的殿裡、不必再用肩扛抬．現在要事奉耶和華你們的　神、服事他的民以色列。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,4,"你們應當按著宗族、照著班次、遵以色列王大衛和他兒子所羅門所寫的、自己預備。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,5,"要按著你們的弟兄這民宗族的班次、站在聖所、每班中要利未宗族的幾個人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,6,"要宰逾越節的羊羔、潔淨自己．為你們的弟兄預備了、好遵守耶和華藉摩西所吩咐的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,7,"約西亞從群畜中賜給在那裡所有的人民、綿羊羔和山羊羔三萬隻、牛三千隻、作逾越節的祭物．這都是出自王的產業中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,8,"約西亞的眾首領也樂意將犧牲給百姓、和祭司利未人．又有管理　神殿的希勒家、撒迦利亞、耶歇將羊羔二千六百隻、牛三百隻、給祭司作逾越節的祭物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,9,"利未人的族長歌楠雅、和他兩個兄弟示瑪雅、拿坦業、與哈沙比雅、耶利、約撒拔、將羊羔五千隻、牛五百隻、給利未人作逾越節的祭物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,10,"這樣、供獻的事齊備了．祭司站在自己的地方、利未人按著班次站立、都是照王所吩咐的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,11,"利未人宰了逾越節的羊羔、祭司從他們手裡接過血來灑在壇上、利未人剝皮、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,12,"將燔祭搬來、按著宗族的班次分給眾民、好照摩西書上所寫的、獻給耶和華．獻牛也是這樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,13,"他們按著常例、用火烤逾越節的羊羔．別的聖物用鍋、用釜、用罐煮了．速速的送給眾民。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,14,"然後為自己和祭司預備祭物、因為祭司亞倫的子孫獻燔祭和脂油、直到晚上．所以利未人為自己、和祭司亞倫的子孫、預備祭物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,15,"歌唱的亞薩之子孫、照著大衛、亞薩、希幔、和王的先見耶杜頓所吩咐的、站在自己的地位上．守門的看守各門、不用離開他們的職事、因為他們的弟兄利未人給他們預備祭物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,16,"當日供奉耶和華的事齊備了、就照約西亞王的吩咐守逾越節、獻燔祭在耶和華的壇上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,17,"當時在耶路撒冷的以色列人守逾越節、又守除酵節七日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,18,"自從先知撒母耳以來、在以色列中沒有守過這樣的逾越節、以色列諸王也沒有守過、像約西亞、祭司、利未人在那裡的猶大人和以色列人、以及耶路撒冷居民所守的逾越節。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,19,"這逾越節是約西亞作王十八年守的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,20,"這事以後、約西亞修完了殿、有埃及王尼哥上來、要攻擊靠近伯拉河的迦基米施．約西亞出去抵擋他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,21,"他差遣使者來見約西亞說、猶大王阿、我與你何干、我今日來不是要攻擊你、乃是要攻擊與我爭戰之家、並且　神吩咐我速行、你不要干預　神的事、免得他毀滅你、因為　神是與我同在。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,22,"約西亞卻不肯轉去離開他、改裝要與他打仗、不聽從　神藉尼哥之口所說的話、便來到米吉多平原爭戰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,23,"弓箭手射中約西亞王．王對他的臣僕說、我受了重傷、你拉我出陣罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,24,"他的臣僕扶他下了戰車、上了次車、送他到耶路撒冷．他就死了、葬在他列祖的墳墓裡．猶大人和耶路撒冷人、都為他悲哀。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,25,"耶利米為約西亞作哀歌、所有歌唱的男女也唱哀歌、追悼約西亞、直到今日、而且在以色列中成了定例．這歌載在哀歌書上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,26,"約西亞其餘的事、和他遵著耶和華律法上所記而行的善事、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",35,27,"並他自始至終所行的、都寫在以色列和猶大列王記上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,1,"國民立約西亞的兒子約哈斯、在耶路撒冷接續他父作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,2,"約哈斯登基的時候年二十三歲、在耶路撒冷作王三個月。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,3,"埃及王在耶路撒冷廢了他、又罰猶大國、銀子一百他連得、金子一他連得。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,4,"埃及王尼哥立約哈斯的哥哥以利雅敬作猶大和耶路撒冷的王、改名叫約雅敬、又將約哈斯帶到埃及去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,5,"約雅敬登基的時候年二十五歲、在耶路撒冷作王十一年．行耶和華他　神眼中看為惡的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,6,"巴比倫王尼布甲尼撒上來攻擊他、用銅鍊鎖著他、要將他帶到巴比倫去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,7,"尼布甲尼撒又將耶和華殿裡的器皿帶到巴比倫、放在他神的廟裡。〔或作自己的宮裡〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,8,"約雅敬其餘的事、和他所行可憎的事、並他一切的行為、都寫在以色列和猶大列王記上．他兒子約雅斤接續他作王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,9,"約雅斤登基的時候年八歲、〔列王下二十四章八節作十八歲〕在耶路撒冷作王三個月零十天．行耶和華眼中看為惡的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,10,"過了一年、尼布甲尼撒差遣人、將約雅斤和耶和華殿裡各樣寶貴的器皿帶到巴比倫．就立約雅斤的叔叔〔原文作兄〕西底家、作猶大和耶路撒冷的王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,11,"西底家登基的時候年二十一歲、在耶路撒冷作王十一年．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,12,"行耶和華他　神眼中看為惡的事．先知耶利米以耶和華的話勸他、他仍不在耶利米面前自卑。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,13,"尼布甲尼撒曾使他指著　神起誓、他卻背叛．強項硬心、不歸服耶和華以色列的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,14,"眾祭司長和百姓也大大犯罪、效法外邦人一切可憎的事、污穢耶和華在耶路撒冷分別為聖的殿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,15,"耶和華他們列祖的　神因為愛惜自己的民、和他的居所、從早起來差遣使者去警戒他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,16,"他們卻嘻笑　神的使者、藐視他的言語、譏誚他的先知、以致耶和華的忿怒向他的百姓發作、無法可救。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,17,"所以耶和華使迦勒底人的王來攻擊他們、在他們聖殿裡用刀殺了他們的壯丁、不憐恤他們的少男處女、老人白叟．耶和華將他們都交在迦勒底王手裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,18,"迦勒底王將　神殿裡的大小器皿、與耶和華殿裡的財寶、並王和眾首領的財寶、都帶到巴比倫去了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,19,"迦勒底人焚燒　神的殿、拆毀耶路撒冷的城牆、用火燒了城裡的宮殿、毀壞了城裡寶貴的器皿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,20,"凡脫離刀劍的、迦勒底王都擄到巴比倫去、作他和他子孫的僕婢、直到波斯國興起來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,21,"這就應驗耶和華藉耶利米口所說的話、地享受安息、因為地土荒涼便守安息、直滿了七十年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,22,"波斯王古列元年、耶和華為要應驗藉耶利米口所說的話、就激動波斯王古列的心、使他下詔通告全國、說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "2Ch",36,23,"波斯王古列如此說、耶和華天上的　神、已將天下萬國賜給我、又囑咐我在猶大的耶路撒冷為他建造殿宇．你們中間凡作他子民的、可以上去、願耶和華他的　神與他同在。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",1,1,"波斯王古列元年、耶和華為要應驗藉耶利米口所說的話、就激動波斯王古列的心、使他下詔通告全國說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",1,2,"波斯王古列如此說、耶和華天上的　神、已將天下萬國賜給我．又囑咐我在猶大的耶路撒冷、為他建造殿宇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",1,3,"在你們中間凡作他子民的、可以上猶大的耶路撒冷、在耶路撒冷重建耶和華以色列　神的殿．（只有他是　神）願　神與這人同在。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",1,4,"凡剩下的人、無論寄居何處、那地的人要用金銀財物牲畜幫助他．另外也要為耶路撒冷　神的殿、甘心獻上禮物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",1,5,"於是猶大和便雅憫的族長、祭司利未人、就是一切被　神激動他心的人、都起來要上耶路撒冷去建造耶和華的殿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",1,6,"他們四圍的人就拿銀器、金子、財物、牲畜、珍寶幫助他們．〔原文作堅固他們的手〕另外還有甘心獻的禮物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",1,7,"古列王也將耶和華殿的器皿拿出來．這器皿是尼布甲尼撒從耶路撒冷掠來、放在自己神之廟中的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",1,8,"波斯王古列派庫官米提利達、將這器皿拿出來、按數交給猶大的首領設巴薩。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",1,9,"器皿的數目記在下面．金盤三十個、銀盤一千個、刀二十九把、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",1,10,"金碗三十個、銀碗之次的四百一十個、別樣的器皿一千件．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",1,11,"金銀器皿、共有五千四百件．被擄的人從巴比倫上耶路撒冷的時候、設巴薩將這一切都帶上來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,1,"巴比倫王尼布甲尼撒、從前擄到巴比倫之猶大省的人、現在他們的子孫從被擄到之地、回耶路撒冷和猶大、各歸本城。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,2,"他們是同著所羅巴伯、耶書亞、尼希米、西萊雅、利來雅、末底改、必珊、米斯拔、比革瓦伊、利宏、巴拿、回來的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,3,"以色列人民的數目記在下面．巴錄的子孫、二千一百七十二名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,4,"示法提雅的子孫、三百七十二名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,5,"亞拉的子孫、七百七十五名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,6,"巴哈摩押的後裔、就是耶書亞和約押的子孫、二千八百一十二名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,7,"以攔的子孫、一千二百五十四名．")');
