tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,23,"亞拿突人、一百二十八名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,24,"亞斯瑪弗人、四十二名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,25,"基列耶琳人、基非拉人、比錄人、共七百四十三名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,26,"拉瑪人、迦巴人、共六百二十一名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,27,"默瑪人、一百二十二名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,28,"伯特利人、艾人、共二百二十三名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,29,"尼波人、五十二名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,30,"末必人、一百五十六名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,31,"別的以攔子孫、一千二百五十四名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,32,"哈琳的子孫、三百二十名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,33,"羅德人、哈第人、阿挪人、共七百二十五名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,34,"耶利哥人、三百四十五名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,35,"西拿人、三千六百三十名。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,36,"祭司、耶書亞家耶大雅的子孫、九百七十三名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,37,"音麥的子孫、一千零五十二名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,38,"巴施戶珥的子孫、一千二百四十七名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,39,"哈琳的子孫、一千零一十七名。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,40,"利未人、何達威雅的後裔、就是耶書亞和甲篾的子孫、七十四名。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,41,"歌唱的、亞薩的子孫、一百二十八名。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,42,"守門的、沙龍的子孫、亞特的子孫、達們的子孫、亞谷的子孫、哈底大的子孫、朔拜的子孫、共一百三十九名。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,43,"尼提寧、〔就是殿役〕西哈的子孫、哈蘇巴的子孫、答巴俄的子孫、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,44,"基綠的子孫、西亞的子孫、巴頓的子孫、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,45,"利巴拿的子孫、哈迦巴的子孫、亞谷的子孫、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,46,"哈甲的子孫、薩買的子孫、哈難的子孫、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,47,"吉德的子孫、迦哈的子孫、利亞雅的子孫、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,48,"利汛的子孫、尼哥大的子孫、迦散的子孫、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,49,"烏撒的子孫、巴西亞的子孫、比賽的子孫、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,50,"押拿的子孫、米烏寧的子孫、尼普心的子孫、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,51,"巴蔔的子孫、哈古巴的子孫、哈忽的子孫、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,52,"巴洗律的子孫、米希大的子孫、哈沙的子孫、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,53,"巴柯的子孫、西西拉的子孫、答瑪的子孫、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,54,"尼細亞的子孫、哈提法的子孫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,55,"所羅門僕人的後裔、就是瑣太的子孫、瑣斐列的子孫、比路大的子孫、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,56,"雅拉的子孫、達昆的子孫、吉德的子孫、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,57,"示法提雅的子孫、哈替的子孫、玻黑列哈斯巴音的子孫、亞米的子孫．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,58,"尼提寧和所羅門僕人的後裔、共三百九十二名。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,59,"從特米拉、特哈薩、基綠、押但、音麥上來的、不能指明他們的宗族譜系、是以色列人不是．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,60,"他們是第來雅的子孫、多比雅的子孫、尼哥大的子孫、共六百五十二名。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,61,"祭司中哈巴雅的子孫、哈哥斯的子孫、巴西萊的子孫、因為他們的先祖娶了基列人巴西萊的女兒為妻、所以起名叫巴西萊．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,62,"這三家的人、在族譜之中尋查自己的譜系、卻尋不著、因此算為不潔、不准供祭司的職任．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,63,"省長對他們說、不可喫至聖的物、直到有用烏陵和土明決疑的祭司興起來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,64,"會眾共有四萬二千三百六十名。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,65,"此外、還有他們的僕婢、七千三百三十七名．又有歌唱的男女二百名．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,66,"他們有馬七百三十六匹、騾子二百四十五匹、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,67,"駱駝四百三十五隻、驢六千七百二十匹。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,68,"有些族長、到了耶路撒冷耶和華殿的地方、便為　神的殿甘心獻上禮物、要重新建造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,69,"他們量力捐入工程庫的金子、六萬一千達利克．銀子、五千彌拿．並祭司的禮服一百件。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",2,70,"於是祭司利未人、民中的一些人、歌唱的、守門的、尼提寧、並以色列眾人、各住在自己的城裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",3,1,"到了七月、以色列人住在各城、那時他們如同一人、聚集在耶路撒冷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",3,2,"約薩達的兒子耶書亞、和他的弟兄眾祭司、並撒拉鐵的兒子所羅巴伯、與他的弟兄、都起來建築以色列　神的壇、要照神人摩西律法書上所寫的、在壇上獻燔祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",3,3,"他們在原有的根基上築壇、因懼怕鄰國的民．又在其上向耶和華早晚獻燔祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",3,4,"又照律法書上所寫的、守住棚節．按數照例、獻每日所當獻的燔祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",3,5,"其後獻常獻的燔祭、並在月朔與耶和華的一切聖節獻祭、又向耶和華獻各人的甘心祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",3,6,"從七月初一日起、他們就向耶和華獻燔祭．但耶和華殿的根基、尚未立定。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",3,7,"他們又將銀子給石匠、木匠、把糧食、酒、油、給西頓人、推羅人、使他們將香柏樹從利巴嫩運到海裡、浮海運到約帕、是照波斯王古列所允准的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",3,8,"百姓到了耶路撒冷　神殿的地方、第二年二月、撒拉鐵的兒子所羅巴伯、約薩達的兒子耶書亞、和其餘的弟兄、就是祭司、利未人、並一切被擄歸回耶路撒冷的人、都興工建造．又派利未人、從二十歲以外的、督理建造耶和華殿的工作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",3,9,"於是猶大〔在二章四十節作何達威雅〕的後裔、就是耶書亞、和他的子孫與弟兄、甲篾和他的子孫、利未人希拿達的子孫與弟兄、都一同起來、督理那在　神殿作工的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",3,10,"匠人立耶和華殿根基的時候、祭司皆穿禮服吹號、亞薩的子孫利未人敲鈸、照以色列王大衛所定的例、都站著讚美耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",3,11,"他們彼此唱和、讚美稱謝耶和華、說、他本為善、他向以色列人永發慈愛．他們讚美耶和華的時候、眾民大聲呼喊、因耶和華殿的根基已經立定．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",3,12,"然而有許多祭司、利未人、族長、就是見過舊殿的老年人、現在親眼看見立這殿的根基、便大聲哭號．也有許多人大聲歡呼．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",3,13,"甚至百姓不能分辨歡呼的聲音、和哭號的聲音、因為眾人大聲呼喊、聲音聽到遠處。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,1,"猶大和便雅憫的敵人聽說、被擄歸回的人為耶和華以色列的　神建造殿宇、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,2,"就去見所羅巴伯、和以色列的族長、對他們說、請容我們與你們一同建造、因為我們尋求你們的　神、與你們一樣．自從亞述王以撒哈頓帶我們上這地以來、我們常祭祀　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,3,"但所羅巴伯、耶書亞、和其餘以色列的族長、對他們說、我們建造　神的殿與你們無干、我們自己為耶和華以色列的　神協力建造、是照波斯王古列所吩咐的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,4,"那地的民、就在猶大人建造的時候、使他們的手發軟、擾亂他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,5,"從波斯王古列年間、直到波斯王大利烏登基的時候、賄買謀士、要敗壞他們的謀算。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,6,"在亞哈隨魯纔登基的時候、上本控告猶大和耶路撒冷的居民。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,7,"亞達薛西年間、比施蘭、米特利達、他別、和他們的同黨、上本奏告波斯王亞達薛西．本章是用亞蘭文字、亞蘭方言。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,8,"省長利宏、書記伸帥、要控告耶路撒冷人、也上本奏告亞達薛西王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,9,"省長利宏、書記伸帥、和同黨的底拿人、亞法薩提迦人、他毗拉人、亞法撒人、亞基衛人、巴比倫人、書珊迦人、底亥人、以攔人、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,10,"和尊大的亞斯那巴所遷移、安置在撒瑪利亞城、並大河西一帶地方的人等．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,11,"上奏亞達薛西王說、河西的臣民雲雲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,12,"王該知道、從王那裡上到我們這裡的猶大人、已經到耶路撒冷重建這反叛惡劣的城、築立根基、建造城牆。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,13,"如今王該知道、他們若建造這城、城牆完畢、就不再與王進貢、交課、納稅、終久王必受虧損．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,14,"我們既食御鹽、不忍見王喫虧、因此奏告於王、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,15,"請王考察先王的實錄、必在其上查知這城是反叛的城、與列王和各省有害、自古以來、其中常有悖逆的事、因此這城曾被拆毀。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,16,"我們謹奏王知、這城若再建造、城牆完畢、河西之地王就無分了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,17,"那時王諭覆省長利宏、書記伸帥、和他們的同黨、就是住撒瑪利亞、並河西一帶地方的人、說、願你們平安雲雲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,18,"你們所上的本、已經明讀在我面前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,19,"我已命人考查、得知此城古來果然背叛列王、其中常有反叛悖逆的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,20,"從前耶路撒冷也有大君王統管河西全地、人就給他們進貢、交課、納稅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,21,"現在你們要出告示命這些人停工、使這城不得建造、等我降旨．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,22,"你們當謹慎、不可遲延、為何容害加重、使王受虧損呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,23,"亞達薛西王的上諭讀在利宏、和書記伸帥、並他們的同黨面前、他們就急忙往耶路撒冷去見猶大人、用勢力強迫他們停工。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",4,24,"於是在耶路撒冷　神殿的工程就停止了、直停到波斯王大利烏第二年。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,1,"那時先知哈該、和易多的孫子撒迦利亞、奉以色列　神的名、向猶大和耶路撒冷的猶大人、說勸勉的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,2,"於是撒拉鐵的兒子所羅巴伯、和約薩達的兒子耶書亞、都起來動手建造耶路撒冷　神的殿、有　神的先知在那裡幫助他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,3,"當時河西的總督達乃、和示他波斯乃、並他們的同黨、來問說、誰降旨讓你們建造這殿、修成這牆呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,4,"我們便告訴他們建造這殿的人叫甚麼名字。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,5,"　神的眼目看顧猶大的長老、以致總督等、沒有叫他們停工、直到這事奏告大利烏、得著他的回諭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,6,"河西的總督達乃、和示他波斯乃、並他們的同黨、就是住河西的亞法薩迦人、上本奏告大利烏王．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,7,"本上寫著說、願大利烏王諸事平安．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,8,"王該知道、我們往猶大省去、到了至大　神的殿、這殿是用大石建造的、梁木插入牆內、工作甚速、他們手下亨通．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,9,"我們就問那些長老說、誰降旨讓你們建造這殿、修成這牆呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,10,"又問他們的名字、要記錄他們首領的名字、奏告於王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,11,"他們回答說、我們是天地之　神的僕人、重建前多年所建造的殿、就是以色列的一位大君王建造修成的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,12,"只因我們列祖惹天上的　神發怒、　神把他們交在迦勒底人巴比倫王尼布甲尼撒的手中、他就拆毀這殿、又將百姓擄到巴比倫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,13,"然而巴比倫王古列元年、他降旨允准建造　神的這殿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,14,"　神殿中的金銀、器皿、就是尼布甲尼撒從耶路撒冷的殿中掠去帶到巴比倫廟裡的、古列王從巴比倫廟裡取出來、交給派為省長的、名叫設巴薩．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,15,"對他說、可以將這些器皿帶去、放在耶路撒冷的殿中、在原處建造　神的殿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,16,"於是這設巴薩來建立耶路撒冷　神殿的根基．這殿、從那時直到如今、尚未造成。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",5,17,"現在王若以為美、請察巴比倫王的府庫、看古列王降旨、允准在耶路撒冷建造　神的殿沒有．王的心意如何、請降旨曉諭我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,1,"於是大利烏王降旨、要尋察典籍庫內、就是在巴比倫藏寶物之處．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,2,"在瑪代省、亞馬他城的宮內、尋得一卷、其中記著說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,3,"古列王元年、他降旨論到耶路撒冷　神的殿、要建造這殿為獻祭之處、堅立殿的根基、殿高六十肘、寬六十肘．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,4,"用三層大石頭、一層新木頭、經費要出於王庫．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,5,"並且　神殿的金銀器皿、就是尼布甲尼撒從耶路撒冷的殿中掠到巴比倫的、要歸還帶到耶路撒冷的殿中、各按原處放在　神的殿裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,6,"現在河西的總督達乃、和示他波斯乃、並你們的同黨、就是住河西的亞法薩迦人、你們當遠離他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,7,"不要攔阻　神殿的工作、任憑猶大人的省長、和猶大人的長老在原處建造　神的這殿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,8,"我又降旨、吩咐你們向猶大人的長老為建造　神的殿、當怎樣行、就是從河西的款項中、急速撥取貢銀作他們的經費、免得耽誤工作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,9,"他們與天上的　神獻燔祭所需用的公牛犢、公綿羊、綿羊羔、並所用的麥子、鹽、酒、油、都要照耶路撒冷祭司的話、每日供給他們、不得有誤．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,10,"好叫他們獻馨香的祭給天上的　神、又為王、和王眾子的壽命祈禱。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,11,"我再降旨、無論誰更改這命令、必從他房屋中拆出一根梁來、把他舉起、懸在其上．又使他的房屋成為糞堆。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,12,"若有王和民伸手更改這命令、拆毀這殿、願那使耶路撒冷的殿作為他名居所的　神、將他們滅絕．我大利烏降這旨意、當速速遵行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,13,"於是河西總督達乃、和示他波斯乃、並他們的同黨、因大利烏王所發的命令、就急速遵行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,14,"猶大長老因先知哈該、和易多的孫子撒迦利亞所說勸勉的話、就建造這殿、凡事亨通．他們遵著以色列　神的命令、和波斯王古列、大利烏、亞達薛西的旨意、建造完畢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,15,"大利烏王第六年、亞達月初三日、這殿修成了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,16,"以色列的祭司、和利未人、並其餘被擄歸回的人、都歡歡喜喜地行奉獻　神殿的禮。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,17,"行奉獻　神殿的禮、就獻公牛一百隻、公綿羊二百隻、綿羊羔四百隻．又照以色列支派的數目、獻公山羊十二隻、為以色列眾人作贖罪祭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,18,"且派祭司、和利未人、按著班次在耶路撒冷事奉　神、是照摩西律法書上所寫的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,19,"正月十四日、被擄歸回的人守逾越節。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,20,"原來祭司和利未人、一同自潔、無一人不潔淨．利未人為被擄歸回的眾人和他們的弟兄眾祭司、並為自己宰逾越節的羊羔。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,21,"從擄到之地歸回的以色列人、和一切除掉所染外邦人污穢歸附他們、要尋求耶和華以色列　神的人、都喫這羊羔。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",6,22,"歡歡喜喜地守除酵節七日、因為耶和華使他們歡喜、又使亞述王的心轉向他們、堅固他們的手、作以色列　神殿的工程。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,1,"這事以後、波斯王亞達薛西年間、有個以斯拉、他是西萊雅的兒子、西萊雅是亞撒利雅的兒子、亞撒利雅是希勒家的兒子、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,2,"希勒家是沙龍的兒子、沙龍是撒督的兒子、撒督是亞希突的兒子、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,3,"亞希突是亞瑪利雅的兒子、亞瑪利雅是亞撒利雅的兒子、亞撒利雅是米拉約的兒子、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,4,"米拉約是西拉希雅的兒子、西拉希雅是烏西的兒子、烏西是布基的兒子、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,5,"布基是亞比書的兒子、亞比書是非尼哈的兒子、非尼哈是以利亞撒的兒子、以利亞撒是大祭司亞倫的兒子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,6,"這以斯拉從巴比倫上來、他是敏捷的文士、通達耶和華以色列　神所賜摩西的律法書．王允准他一切所求的、是因耶和華他　神的手幫助他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,7,"亞達薛西王第七年、以色列人、祭司、利未人、歌唱的、守門的、尼提寧、有上耶路撒冷的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,8,"王第七年五月、以斯拉到了耶路撒冷．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,9,"正月初一日、他從巴比倫起程．因他　神施恩的手幫助他、五月初一日就到了耶路撒冷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,10,"以斯拉定志考究遵行耶和華的律法、又將律例典章教訓以色列人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,11,"祭司以斯拉是通達耶和華誡命、和賜以色列之律例的文士．亞達薛西王賜給他諭旨、上面寫著說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,12,"諸王之王亞達薛西、達於祭司以斯拉通達天上　神律法大德的文士雲雲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,13,"住在我國中的以色列人、祭司、利未人、凡甘心上耶路撒冷去的、我降旨准他們與你同去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,14,"王與七個謀士既然差你去、照你手中　神的律法書、察問猶大和耶路撒冷的景況．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,15,"又帶金銀、就是王和謀士甘心獻給住耶路撒冷、以色列　神的、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,16,"並帶你在巴比倫全省所得的金銀、和百姓、祭司樂意獻給耶路撒冷他們　神殿的禮物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,17,"所以你當用這金銀、急速買公牛、公綿羊、綿羊羔、和同獻的素祭奠祭之物、獻在耶路撒冷你們　神殿的壇上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,18,"剩下的金銀、你和你的弟兄看著怎樣好、就怎樣用、總要遵著你們　神的旨意。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,19,"所交給你　神殿中使用的器皿、你要交在耶路撒冷　神面前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,20,"你　神殿裡、若再有需用的經費、你可以從王的府庫裡支取。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,21,"我亞達薛西王、又降旨與河西的一切庫官、說、通達天上　神律法的文士祭司以斯拉、無論向你們要甚麼、你們要速速的備辦．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,22,"就是銀子直到一百他連得、麥子一百柯珥、酒一百罷特、油一百罷特、鹽不計其數、也要給他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,23,"凡天上之　神所吩咐的、當為天上　神的殿詳細辦理．為何使忿怒臨到王、和王眾子的國呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,24,"我又曉諭你們、至於祭司、利未人、歌唱的、守門的、和尼提寧、並在　神殿當差的人、不可叫他們進貢、交課、納稅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,25,"以斯拉阿、要照著你　神賜你的智慧、將所有明白你　神律法的人立為士師、審判官、治理河西的百姓、使他們教訓一切不明白　神律法的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,26,"凡不遵行你　神律法、和王命令的人、就當速速定他的罪、或治死、或充軍、或抄家、或囚禁。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,27,"以斯拉說、耶和華我們列祖的　神是應當稱頌的、因他使王起這心意、修飾耶路撒冷耶和華的殿．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",7,28,"又在王和謀士、並大能的軍長面前施恩於我。因耶和華我　神的手幫助我、我就得以堅強、從以色列中招聚首領、與我一同上來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,1,"當亞達薛西王年間、同我從巴比倫上來的人、他們的族長、和他們的家譜、記在下面．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,2,"屬非尼哈的子孫有革順、屬以他瑪的子孫有但以理、屬大衛的子孫有哈突、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,3,"屬巴錄的後裔、就是示迦尼的子孫、有撒迦利亞．同著他、按家譜計算、男丁一百五十人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,4,"屬巴哈摩押的子孫有西拉希雅的兒子以利約乃．同著他有男丁二百。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,5,"屬示迦尼的子孫有雅哈悉的兒子．同著他有男丁三百。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,6,"屬亞丁的子孫有約拿單的兒子以別．同著他有男丁五十。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,7,"屬以攔的子孫有亞他利雅的兒子耶篩亞．同著他有男丁七十。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,8,"屬示法提雅的子孫有米迦勒的兒子西巴第雅．同著他有男丁八十。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,9,"屬約押的子孫有耶歇的兒子俄巴底亞．同著他有男丁二百一十八。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,10,"屬示羅密的子孫有約細斐的兒子．同著他有男丁一百六十。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,11,"屬比拜的子孫有比拜的兒子撒迦利亞．同著他有男丁二十八。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,12,"屬押甲的子孫有哈加坦的兒子約哈難．同著他有男丁一百一十。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,13,"屬亞多尼干的子孫、就是末尾的、他們的名字是以利法列、耶利、示瑪雅．同著他們有男丁六十。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,14,"屬比革瓦伊的子孫有烏太和撒布．同著他們有男丁七十。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,15,"我招聚這些人在流入亞哈瓦的河邊、我們在那裡住了三日、我查看百姓和祭司、見沒有利未人在那裡．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,16,"就召首領以利以謝、亞列、示瑪雅、以利拿單、雅立、以利拿單、拿單、撒迦利亞、米書蘭．又召教習約雅立、和以利拿單。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,17,"我打發他們往迦西斐雅地方去、見那裡的首領易多、又告訴他們當向易多和他的弟兄尼提寧說甚麼話、叫他們為我們　神的殿帶使用的人來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,18,"蒙我們　神施恩的手幫助我們、他們在以色列的曾孫、利未的孫子、抹利的後裔中、帶一個通達人來．還有示利比、和他的眾子、與弟兄、共一十八人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,19,"又有哈沙比雅．同著他有米拉利的子孫耶篩亞、並他的眾子、和弟兄、共二十人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,20,"從前大衛和眾首領、派尼提寧服事利未人、現在從這尼提寧中也帶了二百二十人來、都是按名指定的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,21,"那時我在亞哈瓦河邊宣告禁食、為要在我們　神面前克苦己心、求他使我們和婦人孩子、並一切所有的、都得平坦的道路。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,22,"我求王撥步兵馬兵、幫助我們抵擋路上的仇敵、本以為羞恥、因我曾對王說、我們　神施恩的手、必幫助一切尋求他的．但他的能力和忿怒、必攻擊一切離棄他的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,23,"所以我們禁食祈求我們的　神、他就應允了我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,24,"我分派祭司長十二人、就是示利比、哈沙比雅、和他們的弟兄十人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,25,"將王、和謀士、軍長、並在那裡的以色列眾人、為我們　神殿所獻的金銀、和器皿、都秤了交給他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,26,"我秤了交在他們手中的銀子、有六百五十他連得、銀器重一百他連得、金子一百他連得、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,27,"金碗二十個、重一千達利克、上等光銅的器皿兩個、寶貴如金。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,28,"我對他們說、你們歸耶和華為聖、器皿也為聖、金銀是甘心獻給耶和華你們列祖之　神的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,29,"你們當警醒看守、直到你們在耶路撒冷耶和華殿的庫內、在祭司長和利未族長、並以色列的各族長面前過了秤。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,30,"於是祭司、利未人、按著分量接受金銀、和器皿、要帶到耶路撒冷我們　神的殿裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,31,"正月十二日、我們從亞哈瓦河邊起行、要往耶路撒冷去．我們　神的手保佑我們、救我們脫離仇敵、和路上埋伏之人的手。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,32,"我們到了耶路撒冷、在那裡住了三日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,33,"第四日、在我們　神的殿裡、把金銀和器皿都秤了、交在祭司烏利亞的兒子米利末的手中．同著他有非尼哈的兒子以利亞撒、還有利未人耶書亞的兒子約撒拔、和賓內的兒子挪亞底。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,34,"當時都點了數目、按著分量寫在冊上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,35,"從擄到之地歸回的人、向以色列的　神獻燔祭、就是為以色列眾人獻公牛十二隻、公綿羊九十六隻、綿羊羔七十七隻．又獻公山羊十二隻作贖罪祭．這都是向耶和華焚獻的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",8,36,"他們將王的諭旨交給王所派的總督、與河西的省長、他們就幫助百姓、又供給　神殿裡所需用的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",9,1,"這事作完了、眾首領來見我、說、以色列民和祭司、並利未人、沒有離絕迦南人、赫人、比利洗人、耶布斯人、亞捫人、摩押人、埃及人、亞摩利人、仍效法這些國的民、行可憎的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",9,2,"因他們為自己和兒子、娶了這些外邦女子為妻、以致聖潔的種類和這些國的民混雜．而且首領和官長、在這事上為罪魁。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",9,3,"我一聽見這事、就撕裂衣服和外袍、拔了頭髮和鬍鬚、驚懼憂悶而坐。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",9,4,"凡為以色列　神言語戰兢的、都因這被擄歸回之人所犯的罪、聚集到我這裡來．我就驚懼憂悶而坐、直到獻晚祭的時候。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",9,5,"獻晚祭的時候我起來、心中愁苦、穿著撕裂的衣袍、雙膝跪下向耶和華我的　神舉手、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",9,6,"說、我的　神阿、我抱愧蒙羞、不敢向我　神仰面、因為我們的罪孽滅頂、我們的罪惡滔天。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",9,7,"從我們列祖直到今日、我們的罪惡甚重、因我們的罪孽、我們和君王、祭司、都交在外邦列王的手中、殺害、擄掠、搶奪、臉上蒙羞、正如今日的光景。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",9,8,"現在耶和華我們的　神暫且施恩與我們、給我們留些逃脫的人、使我們安穩如釘子釘在他的聖所、我們的　神好光照我們的眼目、使我們在受轄制之中稍微復興。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",9,9,"我們是奴僕、然而在受轄制之中、我們的　神仍沒有丟棄我們、在波斯王眼前向我們施恩、叫我們復興、能重建我們　神的殿、修其毀壞之處、使我們在猶大和耶路撒冷有牆垣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",9,10,"我們的　神阿、既是如此、我們還有甚麼話可說呢．因為我們已經離棄你的命令、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",9,11,"就是你藉你僕人眾先知所吩咐的、說、你們要去得為業之地是污穢之地、因列國之民的污穢、和可憎的事、叫全地從這邊直到那邊、滿了污穢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",9,12,"所以不可將你們的女兒嫁他們的兒子、也不可為你們的兒子娶他們的女兒、永不可求他們的平安、和他們的利益、這樣你們就可以強盛、喫這地的美物、並遺留這地給你們的子孫永遠為業。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",9,13,"　神阿、我們因自己的惡行和大罪、遭遇了這一切的事、並且你刑罰我們輕於我們罪所當得的、又給我們留下這些人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",9,14,"我們豈可再違背你的命令、與這行可憎之事的民結親呢、若這樣行、你豈不向我們發怒、將我們滅絕、以致沒有一個剩下逃脫的人麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",9,15,"耶和華以色列的　神阿、因你是公義的、我們這剩下的人纔得逃脫、正如今日的光景．看哪、我們在你面前有罪惡、因此無人在你面前站立得住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,1,"以斯拉禱告、認罪、哭泣、俯伏在　神殿前的時候、有以色列中的男女孩童、聚集到以斯拉那裡、成了大會．眾民無不痛哭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,2,"屬以攔的子孫、耶歇的兒子示迦尼、對以斯拉說、我們在此地娶了外邦女子為妻、干犯了我們的　神、然而以色列人還有指望。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,3,"現在當與我們的　神立約、休這一切的妻、離絕他們所生的、照著我主和那因　神命令戰兢之人所議定的、按律法而行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,4,"你起來、這是你當辦的事、我們必幫助你、你當奮勉而行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,5,"以斯拉便起來、使祭司長、和利未人、並以色列眾人起誓、說、必照這話去行．他們就起了誓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,6,"以斯拉從　神殿前起來、進入以利亞實的兒子約哈難的屋裡、到了那裡不喫飯、也不喝水．因為被擄歸回之人所犯的罪、心裡悲傷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,7,"他們通告猶大和耶路撒冷被擄歸回的人、叫他們在耶路撒冷聚集．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,8,"凡不遵首領和長老所議定三日之內不來的、就必抄他的家、使他離開被擄歸回之人的會。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,9,"於是猶大和便雅憫眾人、三日之內都聚集在耶路撒冷．那日正是九月二十日、眾人都坐在　神殿前的寬闊處．因這事、又因下大雨、就都戰兢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,10,"祭司以斯拉站起來、對他們說、你們有罪了．因你們娶了外邦的女子為妻、增添以色列人的罪惡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,11,"現在當向耶和華你們列祖的　神認罪、遵行他的旨意、離絕這些國的民、和外邦的女子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,12,"會眾都大聲回答說、我們必照著你的話行．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,13,"只是百姓眾多、又逢大雨的時令、我們不能站在外頭、這也不是一兩天辦完的事、因我們在這事上犯了大罪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,14,"不如為全會眾派首領辦理．凡我們城邑中娶外邦女子為妻的、當按所定的日期、同著本城的長老和士師而來、直到辦完這事．　神的烈怒就轉離我們了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,15,"惟有亞撒黑的兒子約拿單、特瓦的兒子雅哈謝、阻擋〔或作總辦〕這事、並有米書蘭、和利未人沙比太幫助他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,16,"被擄歸回的人如此而行。祭司以斯拉和些族長、按著宗族、都指名見派、在十月初一日、一同在座查辦這事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,17,"到正月初一日、纔查清娶外邦女子的人數。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,18,"在祭司中查出娶外邦女子為妻的、就是耶書亞的子孫約薩達的兒子、和他弟兄瑪西雅、以利以謝、雅立、基大利、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,19,"他們便應許必休他們的妻．他們因有罪、就獻群中的一隻公綿羊贖罪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,20,"音麥的子孫中、有哈拿尼、西巴第雅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,21,"哈琳的子孫中、有瑪西雅、以利雅、示瑪雅、耶歇、烏西雅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,22,"巴施戶珥的子孫中、有以利約乃、瑪西雅、以實瑪利、拿坦業、約撒拔、以利亞撒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,23,"利未人中、有約撒拔、示每、基拉雅、基拉雅就是基利他．還有毗他希雅、猶大、以利以謝。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,24,"歌唱的人中、有以利亞實。守門的人中、有沙龍、提聯、烏利。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,25,"以色列人巴錄的子孫中、有拉米、耶西雅、瑪基雅、米雅民、以利亞撒、瑪基雅、比拿雅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,26,"以攔的子孫中、有瑪他尼、撒迦利亞、耶歇、押底、耶利末、以利雅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,27,"薩土的子孫中、有以利約乃、以利亞實、瑪他尼、耶利末、撒拔、亞西撒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,28,"比拜的子孫中、有約哈難、哈拿尼雅、薩拜、亞勒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,29,"巴尼的子孫中、有米書蘭、瑪鹿、亞大雅、雅述、示押、耶利末。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,30,"巴哈摩押的子孫中、有阿底拿、基拉、比拿雅、瑪西雅、瑪他尼、比撒列、賓內、瑪拿西。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,31,"哈琳的子孫中、有以利以謝、伊示雅、瑪基雅、示瑪雅、西緬、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,32,"便雅憫、瑪鹿、示瑪利雅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,33,"哈順的子孫中、有瑪特乃、瑪達他、撒拔、以利法列、耶利買、瑪拿西、示每。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,34,"巴尼的子孫中、有瑪玳、暗蘭、烏益、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,35,"比拿雅、比底雅、基祿、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,36,"瓦尼雅、米利末、以利亞實、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,37,"瑪他尼、瑪特乃、雅掃、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,38,"巴尼、賓內、示每、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,39,"示利米雅、拿單、亞大雅、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,40,"瑪拿底拜、沙賽、沙賴、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,41,"亞薩利、示利米雅、示瑪利雅、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,42,"沙龍、亞瑪利雅、約瑟。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,43,"尼波的子孫中、有耶利、瑪他提雅、撒拔、西比拿、雅玳、約珥、比拿雅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Ezr",10,44,"這些人都娶了外邦女子為妻、其中也有生了兒女的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",1,1,"哈迦利亞的兒子尼希米的言語如下。亞達薛西王二十年、基斯流月、我在書珊城的宮中。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",1,2,"那時、有我一個弟兄哈拿尼、同著幾個人從猶大來．我問他們那些被擄歸回剩下逃脫的猶大人、和耶路撒冷的光景。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",1,3,"他們對我說、那些被擄歸回剩下的人、在猶大省遭大難、受凌辱．並且耶路撒冷的城牆拆毀、城門被火焚燒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",1,4,"我聽見這話、就坐下哭泣、悲哀幾日．在天上的　神面前禁食祈禱說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",1,5,"耶和華天上的　神、大而可畏的　神阿、你向愛你、守你誡命的人、守約施慈愛．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",1,6,"願你睜眼看、側耳聽、你僕人晝夜在你面前為你眾僕人以色列民的祈禱、承認我們以色列人向你所犯的罪．我與我父家都有罪了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",1,7,"我們向你所行的甚是邪惡、沒有遵守你藉著僕人摩西所吩咐的誡命、律例、典章。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",1,8,"求你記念所吩咐你僕人摩西的話、說、你們若犯罪、我就把你們分散在萬民中．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",1,9,"但你們若歸向我、謹守遵行我的誡命、你們被趕散的人、雖在天涯、我也必從那裡將他們招聚回來、帶到我所選擇立為我名的居所。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",1,10,"這都是你的僕人、你的百姓、就是你用大力、和大能的手、所救贖的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",1,11,"主阿、求你側耳聽你僕人的祈禱、和喜愛敬畏你名眾僕人的祈禱、使你僕人現今亨通、在王面前蒙恩。我是作王酒政的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,1,"亞達薛西王二十年、尼散月、在王面前擺酒、我拿起酒來奉給王。我素來在王面前沒有愁容。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,2,"王對我說、你既沒有病、為甚麼面帶愁容呢．這不是別的、必是你心中愁煩。於是我甚懼怕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,3,"我對王說、願王萬歲．我列祖墳墓所在的那城荒涼、城門被火焚燒、我豈能面無愁容麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,4,"王問我說、你要求甚麼。於是我默禱天上的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,5,"我對王說、僕人若在王眼前蒙恩、王若喜歡、求王差遣我往猶大、到我列祖墳墓所在的那城去．我好重新建造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,6,"那時王後坐在王的旁邊。王問我說、你去要多少日子、幾時回來．我就定了日期．於是王喜歡差遣我去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,7,"我又對王說、王若喜歡、求王賜我詔書、通知大河西的省長、准我經過、直到猶大．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,8,"又賜詔書、通知管理王園林的亞薩、使他給我木料、作屬殿營樓之門的橫梁、和城牆、與我自己房屋使用的．王就允准我、因我　神施恩的手幫助我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,9,"王派了軍長、和馬兵護送我。我到了河西的省長那裡、將王的詔書交給他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,10,"和倫人參巴拉、並為奴的亞捫人多比雅、聽見有人來為以色列人求好處、就甚惱怒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,11,"我到了耶路撒冷、在那裡住了三日。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,12,"我夜間起來、有幾個人也一同起來．但　神使我心裡要為耶路撒冷作甚麼事、我並沒有告訴人．除了我騎的牲扣以外、也沒有別的牲扣在我那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,13,"當夜我出了谷門、往野狗井去〔野狗或作龍〕、到了糞廠門、察看耶路撒冷的城牆、見城牆拆毀、城門被火焚燒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,14,"我又往前、到了泉門、和王池．但所騎的牲口沒有地方過去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,15,"於是夜間沿溪而上、察看城牆．又轉身、進入谷門、就回來了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,16,"我往那裡去、我作甚麼事、官長都不知道．我還沒有告訴猶大平民、祭司、貴冑、官長、和其餘作工的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,17,"以後、我對他們說、我們所遭的難、耶路撒冷怎樣荒涼、城門被火焚燒、你們都看見了．來罷、我們重建耶路撒冷的城牆、免得再受凌辱。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,18,"我告訴他們我　神施恩的手怎樣幫助我、並王對我所說的話．他們就說、我們起來建造罷。於是他們奮勇作這善工。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,19,"但和倫人參巴拉、並為奴的亞捫人多比雅、和亞拉伯人基善、聽見就嗤笑我們、藐視我們、說、你們作甚麼呢．要背叛王麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",2,20,"我回答他們說、天上的　神必使我們亨通．我們作他僕人的、要起來建造．你們卻在耶路撒冷無分、無權、無紀念。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,1,"那時、大祭司以利亞實、和他的弟兄眾祭司、起來建立羊門、分別為聖、安立門扇、又築城牆到哈米亞樓、直到哈楠業樓、分別為聖。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,2,"其次是耶利哥人建造。其次是音利的兒子撒刻建造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,3,"哈西拿的子孫建立魚門、架橫梁、安門扇、和閂鎖。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,4,"其次是哈哥斯的孫子烏利亞的兒子米利末修造。其次是米示薩別的孫子比利迦的兒子米書蘭修造。其次是巴拿的兒子撒督修造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,5,"其次是提哥亞人修造．但是他們的貴冑、不用肩擔他們主的工作。〔肩原文作頸項〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,6,"巴西亞的兒子耶何耶大、與比所玳的兒子米書蘭、修造古門、架橫梁、安門扇和閂鎖。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,7,"其次是基遍人米拉提、米倫人雅頓、與基遍人、並屬河西總督所管的米斯巴人修造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,8,"其次是銀匠哈海雅的兒子烏薛修造。其次是作香的哈拿尼雅修造．這些人修堅耶路撒冷、直到寬牆。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,9,"其次是管理耶路撒冷一半、戶珥的兒子利法雅修造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,10,"其次是哈路抹的兒子耶大雅、對著自己的房屋修造。其次是哈沙尼的兒子哈突修造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,11,"哈琳的兒子瑪基雅、和巴哈摩押的兒子哈述修造一段、並修造爐樓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,12,"其次是管理耶路撒冷那一半、哈羅黑的兒子沙龍、和他的女兒們修造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,13,"哈嫩和撒挪亞的居民、修造谷門．立門、安門扇、和閂鎖、又建築城牆一千肘、直到糞廠門。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,14,"管理伯哈基琳、利甲的兒子瑪基雅、修造糞廠門．立門、安門扇、和閂鎖。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,15,"管理米斯巴、各荷西的兒子沙崙、修造泉門．立門、蓋門頂、安門扇、和閂鎖、又修造靠近王園西羅亞池的牆垣、直到那從大衛城下來的臺階。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,16,"其次是管理伯夙一半、押蔔的兒子尼希米修造、直到大衛墳地的對面、又到挖成的池子、並勇士的房屋。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,17,"其次是利未人、巴尼的兒子利宏修造。其次是管理基伊拉一半、哈沙比雅為他所管的本境修造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,18,"其次是利未人弟兄中、管理基伊拉那一半、希拿達的兒子巴瓦伊修造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,19,"其次是管理米斯巴、耶書亞的兒子以謝修造一段、對著武庫的上坡、城牆轉彎之處。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,20,"其次是薩拜的兒子巴錄、竭力修造一段、從城牆轉彎、直到大祭司以利亞實的府門。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,21,"其次是哈哥斯的孫子、烏利亞的兒子米利末修造一段、從以利亞實的府門、直到以利亞實府的盡頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,22,"其次是住平原的祭司修造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,23,"其次是便雅憫與哈述、對著自己的房屋修造。其次是亞難尼的孫子瑪西雅的兒子亞撒利雅、在靠近自己的房屋修造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,24,"其次是希拿達的兒子賓內修造一段、從亞撒利雅的房屋、直到城牆轉彎、又到城角。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,25,"烏賽的兒子巴拉修造對著城牆的轉彎、和王上宮凸出來的城樓、靠近護衛院的那一段。其次是巴錄的兒子毗大雅修造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,26,"（尼提寧住在俄斐勒、直到朝東水門的對面、和凸出來的城樓。）")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,27,"其次是提哥亞人又修一段、對著那凸出來的大樓、直到俄斐勒的牆。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,28,"從馬門往上、眾祭司各對自己的房屋修造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,29,"其次是音麥的兒子撒督、對著自己的房屋修造。其次是守東門示迦尼的兒子示瑪雅修造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,30,"其次是示利米雅的兒子哈拿尼雅、和薩拉的第六子哈嫩、又修一段。其次是比利迦的兒子米書蘭、對著自己的房屋修造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,31,"其次是銀匠瑪基雅修造到尼提寧、和商人的房屋、對著哈米弗甲門、直到城的角樓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",3,32,"銀匠與商人在城的角樓、和羊門中間修造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,1,"參巴拉聽見我們修造城牆就發怒、大大惱恨、嗤笑猶大人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,2,"對他弟兄和撒瑪利亞的軍兵說、這些軟弱的猶大人作甚麼呢．要保護自己麼．要獻祭麼．要一日成功麼．要從土堆裡拿出火燒的石頭再立牆麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,3,"亞捫人多比雅站在旁邊、說、他們所修造的石牆、就是狐狸上去也必跐倒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,4,"我們的　神阿、求你垂聽、因為我們被藐視．求你使他們的毀謗歸於他們的頭上、使他們在擄到之地作為掠物．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,5,"不要遮掩他們的罪孽、不要使他們的罪惡從你面前塗抹．因為他們在修造的人眼前惹動你的怒氣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,6,"這樣、我們修造城牆、城牆就都連絡、高至一半、因為百姓專心作工。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,7,"參巴拉、多比雅、亞拉伯人、亞捫人、亞實突人、聽見修造耶路撒冷城牆、著手進行、堵塞破裂的地方、就甚發怒．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,8,"大家同謀要來攻擊耶路撒冷、使城內擾亂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,9,"然而我們禱告我們的　神、又因他們的緣故、就派人看守、晝夜防備。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,10,"猶大人說、灰土尚多、扛抬的人力氣已經衰敗、所以我們不能建造城牆。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,11,"我們的敵人且說、趁他們不知、不見、我們進入他們中間、殺他們、使工作止住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,12,"那靠近敵人居住的猶大人、十次從各處來見我們說、你們必要回到我們那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,13,"所以我使百姓各按宗族、拿刀、拿槍、拿弓站在城牆後邊低窪的空處。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,14,"我察看了、就起來對貴冑、官長、和其餘的人說、不要怕他們、當記念主是大而可畏的．你們要為弟兄、兒女、妻子、家產爭戰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,15,"仇敵聽見我們知道他們的心意、見　神也破壞他們的計謀、就不來了。我們都回到城牆那裡、各作各的工。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,16,"從那日起、我的僕人一半作工、一半拿槍、拿盾牌、拿弓、穿鎧甲．〔穿或作拿〕官長都站在猶大眾人的後邊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,17,"修造城牆的、扛抬材料的、都一手作工、一手拿兵器。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,18,"修造的人、都腰間佩刀修造、吹角的人在我旁邊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,19,"我對貴冑、官長、和其餘的人說、這工程浩大、我們在城牆上相離甚遠．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,20,"你們聽見角聲在那裡、就聚集到我們那裡去．我們的　神必為我們爭戰。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,21,"於是我們作工、一半拿兵器、從天亮直到星宿出現的時候。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,22,"那時、我又對百姓說、各人和他的僕人當在耶路撒冷住宿、好在夜間保守我們、白晝作工。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",4,23,"這樣、我和弟兄僕人、並跟從我的護兵、都不脫衣服、出去打水也帶兵器。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,1,"百姓和他們的妻大大呼號、埋怨他們的弟兄猶大人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,2,"有的說、我們和兒女人口眾多．要去得糧食度命。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,3,"有的說、我們典了田地、葡萄園、房屋．要得糧食充飢．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,4,"有的說、我們已經指著田地、葡萄園、借了錢、給王納稅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,5,"我們的身體、與我們弟兄的身體一樣、我們的兒女與他們的兒女一般．現在我們將要使兒女作人的僕婢、我們的女兒已有為婢的．我們並無力拯救．因為我們的田地、葡萄園、已經歸了別人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,6,"我聽見他們呼號、說這些話、便甚發怒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,7,"我心裡籌畫、就斥責貴冑、和官長、說、你們各人向弟兄取利。於是我招聚大會攻擊他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,8,"我對他們說、我們盡力贖回我們弟兄、就是賣與外邦的猶大人、你們還要賣弟兄、使我們贖回來麼。他們就靜默不語、無話可答。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,9,"我又說、你們所行的不善．你們行事不當敬畏我們的　神麼．不然、難免我們的仇敵外邦人毀謗我們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,10,"我和我的弟兄、與僕人、也將銀錢糧食借給百姓．我們大家都當免去利息。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,11,"如今我勸你們將他們的田地、葡萄園、橄欖園、房屋、並向他們所取的銀錢、糧食、新酒、和油、百分之一的利息都歸還他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,12,"眾人說、我們必歸還、不再向他們索要、必照你的話行．我就召了祭司來、叫眾人起誓、必照著所應許的而行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,13,"我也抖著胸前的衣襟說、凡不成就這應許的、願　神照樣抖他離開家產和他勞碌得來的、直到抖空了。會眾都說、阿們、又讚美耶和華。百姓就照著所應許的去行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,14,"自從我奉派作猶大地的省長、就是從亞達薛西王二十年、直到三十二年、共十二年之久、我與我弟兄都沒有喫省長的俸祿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,15,"在我以前的省長、加重百姓的擔子、每日索要糧食和酒、並銀子四十舍客勒、就是他們的僕人也轄制百姓．但我因敬畏　神、不這樣行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,16,"並且我恆心修造城牆、並沒有置買田地．我的僕人也都聚集在那裡作工。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,17,"除了從四圍外邦中來的猶大人以外、有猶大平民和官長、一百五十人在我席上喫飯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,18,"每日預備一隻公牛、六隻肥羊．又預備些飛禽、每十日一次、多預備各樣的酒．雖然如此、我並不要省長的俸祿．因為百姓服役甚重。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",5,19,"我的　神阿、求你記念我為這百姓所行的一切事、施恩與我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,1,"參巴拉、多比雅、亞拉伯人基善、和我們其餘的仇敵、聽見我已經修完了城牆、其中沒有破裂之處（那時我還沒有安門扇）")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,2,"參巴拉和基善就打發人來見我、說、請你來、我們在阿挪平原的一個莊村相會。他們卻想害我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,3,"於是我差遣人去見他們說、我現在辦理大工、不能下去．焉能停工、下去見你們呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,4,"他們這樣四次打發人來見我、我都如此回答他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,5,"參巴拉第五次打發僕人來見我、手裡拿著未封的信、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,6,"信上寫著說、外邦人中有風聲、迦施慕〔就是基善見二章十九節〕也說、你和猶大人謀反修造城牆、你要作他們的王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,7,"你又派先知在耶路撒冷指著你宣講、說、在猶大有王．現在這話必傳與王知．所以請你來、與我們彼此商議。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,8,"我就差遣人去見他說、你所說的這事、一概沒有、是你心裡捏造的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,9,"他們都要使我們懼怕、意思說、他們的手必軟弱、以致工作不能成就。　神阿、求你堅固我的手。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,10,"我到了米希大別的孫子第來雅的兒子示瑪雅家裡．那時、他閉門不出．他說、我們不如在　神的殿裡會面、將殿門關鎖．因為他們要來殺你、就是夜裡來殺你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,11,"我說、像我這樣的人、豈要逃跑呢．像我這樣的人、豈能進入殿裡保全生命呢．我不進去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,12,"我看明　神沒有差遣他、是他自己說這話攻擊我．是多比雅和參巴拉賄買了他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,13,"賄買他的緣故、是要叫我懼怕、依從他犯罪、他們好傳揚惡言毀謗我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,14,"我的　神阿、多比雅、參巴拉、女先知挪亞底、和其餘的先知、要叫我懼怕、求你記念他們所行的這些事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,15,"以祿月二十五日城牆修完了、共修了五十二天。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,16,"我們一切仇敵、四圍的外邦人、聽見了便懼怕、愁眉不展．因為見這工作完成、是出乎我們的　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,17,"在那些日子猶大的貴冑屢次寄信與多比雅、多比雅也來信與他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,18,"在猶大有許多人與多比雅結盟、因他是亞拉的兒子、示迦尼的女婿．並且他的兒子約哈難娶了比利迦兒子米書蘭的女兒為妻。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",6,19,"他們常在我面前說多比雅的善行、也將我的話傳與他。多比雅又常寄信來、要叫我懼怕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",7,1,"城牆修完、我安了門扇、守門的、歌唱的、和利未人、都已派定。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",7,2,"我就派我的弟兄哈拿尼、和營樓的宰官哈拿尼雅、管理耶路撒冷．因為哈拿尼雅是忠信的、又敬畏　神過於眾人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",7,3,"我吩咐他們說、等到太陽上升、纔可開耶路撒冷的城門．人尚看守的時候、就要關門、上閂、也當派耶路撒冷的居民、各按班次、看守自己房屋對面之處。")');
