tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",10,9,"又有利未人．就是亞散尼的兒子耶書亞、希拿達的子孫賓內、甲篾．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Neh",10,10,"還有他們的弟兄示巴尼、荷第雅、基利他、毗萊雅、哈難、")');
