tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",11,15,"那時、你必仰起臉來、毫無斑點．你也必堅固、無所懼怕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",11,16,"你必忘記你的苦楚、就是想起也如流過去的水一樣。")');
