tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",13,8,"你們要為　神徇情麼、要為他爭論麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",13,9,"他查出你們來、這豈是好麼．人欺哄人、你們也要照樣欺哄他麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",13,10,"你們若暗中徇情、他必要責備你們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",13,11,"他的尊榮、豈不叫你們懼怕麼．他的驚嚇、豈不臨到你們麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",13,12,"你們以為可記念的箴言、是爐灰的箴言．你們以為可靠的堅壘是淤泥的堅壘。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",13,13,"你們不要作聲、任憑我罷、讓我說話．無論如何我都承當。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",13,14,"我何必把我的肉掛在牙上、將我的命放在手中。")');
