tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",13,18,"我已陳明我的案、知道自己有義。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",13,19,"有誰與我爭論、我就情願緘默不言、氣絕而亡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",13,20,"惟有兩件、不要向我施行、我就不躲開你的面．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",13,21,"就是把你的手縮回、遠離我身．又不使你的驚惶威嚇我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",13,22,"這樣、你呼叫、我就回答．或是讓我說話、你回答我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",13,23,"我的罪孽和罪過有多少呢．求你叫我知道我的過犯與罪愆。")');
