tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",30,17,"夜間我裡面的骨頭刺我、疼痛不止、好像齦我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",30,18,"因　神的大力、我的外衣污穢不堪．又如裡衣的領子將我纏住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",30,19,"　神把我扔在淤泥中、我就像塵土和爐灰一般。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",30,20,"主阿、我呼求你、你不應允我．我站起來、你就定睛看我。")');
