tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",31,10,"就願我的妻子給別人推磨、別人也與他同室。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",31,11,"因為這是大罪、是審判官當罰的罪孽。")');
