tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",31,13,"我的僕婢與我爭辯的時候、我若藐視不聽他們的情節．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",31,14,"　神興起、我怎樣行呢．他察問、我怎樣回答呢。")');
