tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",32,6,"布西人巴拉迦的兒子以利戶回答說、我年輕、你們老邁、因此我退讓、不敢向你們陳說我的意見。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",32,7,"我說、年老的當先說話、壽高的當以智慧教訓人。")');
