tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",32,12,"留心聽你們．誰知你們中間無一人折服約伯、駁倒他的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",32,13,"你們切不可說、我們尋得智慧．　神能勝他、人卻不能。")');
