tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",32,17,"我也要回答我的一分話、陳說我的意見。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",32,18,"因為我的言語滿懷、我裡面的靈激動我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",32,19,"我的胸懷如盛酒之囊、沒有出氣之縫、又如新皮袋快要破裂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",32,20,"我要說話、使我舒暢．我要開口回答。")');
