tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,1,"約伯阿、請聽我的話、留心聽我一切的言語。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,2,"我現在開口、用舌發言。")');
