tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,8,"你所說的我聽見了、也聽見你的言語、說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,9,"我是清潔無過的．我是無辜的、在我裡面也沒有罪孽。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,10,"　神找機會攻擊我、以我為仇敵、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,11,"把我的腳上了木狗、窺察我一切的道路。")');
