tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,13,"你為何與他爭論呢、因他的事都不對人解說。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,14,"　神說、一次、兩次、世人卻不理會。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,15,"人躺在床上沉睡的時候　神就用夢、和夜間的異像、")');
