tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,17,"好叫人不從自己的謀算、不行驕傲的事．〔原文作將驕傲向人隱藏〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,18,"攔阻人不陷於坑裡、不死在刀下。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,19,"人在床上被懲治、骨頭中不住的疼痛．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,20,"以致他的口厭棄食物、心厭惡美味。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,21,"他的肉消瘦、不得再見、先前不見的骨頭、都凸出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,22,"他的靈魂臨近深坑、他的生命近於滅命的。")');
