tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,26,"他禱告　神、　神就喜悅他、使他歡呼朝見　神的面．　神又看他為義。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,27,"他在人前歌唱說、我犯了罪、顛倒是非、這竟與我無益。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,28,"　神救贖我的靈魂免入深坑．我的生命也必見光。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,29,"　神兩次、三次、向人行這一切的事．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,30,"為要從深坑救回人的靈魂、使他被光照耀與活人一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",33,31,"約伯阿、你當側耳聽我的話．不要作聲、等我講說。")');
