tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,3,"因為耳朵試驗話語、好像上膛嘗食物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,4,"我們當選擇何為是、彼此知道何為善。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,5,"約伯曾說、我是公義、　神奪去我的理．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,6,"我雖有理、還算為說謊言的．我雖無過、受的傷還不能醫治。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,7,"誰像約伯、喝譏誚如同喝水呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,8,"他與作孽的結伴、和惡人同行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,9,"他說、人以　神為樂、總是無益。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,10,"所以你們明理的人要聽我的話．　神斷不至行惡、全能者斷不至作孽。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,11,"他必按人所作的報應人、使各人照所行的得報。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,12,"　神必不作惡、全能者也不偏離公平。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,13,"誰派他治理地、安定全世界呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,14,"他若專心為己、將靈和氣收歸自己．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,15,"凡有血氣的就必一同死亡、世人必仍歸塵土。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,16,"你若明理、就當聽我的話、留心聽我言語的聲音。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,17,"難道恨惡公平的、可以掌權麼．那有公義的、有大能的、豈可定他有罪麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,18,"他對君王說、你是鄙陋的、對貴臣說、你是邪惡的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,19,"他待王子不徇情面、也不看重富足的過於貧窮的．因為都是他手所造。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,20,"在轉眼之間、半夜之中、他們就死亡．百姓被震動而去世、有權力的被奪去非借人手。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,21,"　神注目觀看人的道路、看明人的腳步。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,22,"沒有黑暗、陰翳、能給作孽的藏身。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,23,"　神審判人、不必使人到他面前、再三鑒察。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,24,"他用難測之法打破有能力的人、設立別人代替他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,25,"他原知道他們的行為、使他們在夜間傾倒滅亡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,26,"他在眾人眼前擊打他們、如同擊打惡人一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,27,"因為他們偏行不跟從他、也不留心他的道．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,28,"甚至使貧窮人的哀聲達到他那裡、他也聽了困苦人的哀聲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,29,"他使人安靜、誰能擾亂〔或作定罪〕呢、他掩面誰能見他呢．無論待一國、或一人都是如此．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,30,"使不虔敬的人不得作王、免得有人牢籠百姓。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,31,"有誰對　神說、我受了責罰、不再犯罪．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,32,"我所看不明的、求你指教我．我若作了孽、必不再作。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,33,"他施行報應、豈要隨你的心願、叫你推辭不受麼。選定的、是你、不是我．你所知道的只管說罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,34,"明理的人、和聽我話的智慧人、必對我說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,35,"約伯說話沒有知識、言語中毫無智慧。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,36,"願約伯被試驗到底、因他回答像惡人一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",34,37,"他在罪上又加悖逆．在我們中間拍手、用許多言語輕慢　神。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",35,1,"以利戶又說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",35,2,"你以為有理、或以為你的公義勝於　神的公義、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",35,3,"纔說、這與我有甚麼益處．我不犯罪比犯罪有甚麼好處呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",35,4,"我要回答你、和在你這裡的朋友。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",35,5,"你要向天觀看、瞻望那高於你的穹蒼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",35,6,"你若犯罪、能使　神受何害呢．你的過犯加增、能使　神受何損呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",35,7,"你若是公義、還能加增他甚麼呢．他從你手裡還接受甚麼呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",35,8,"你的過惡或能害你這類的人．你的公義或能叫世人得益處。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",35,9,"人因多受欺壓就哀求．因受能者的轄制〔轄制原文作膀臂〕便求救、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",35,10,"卻無人說、造我的　神在那裡．他使人夜間歌唱。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",35,11,"教訓我們勝於地上的走獸、使我們有聰明勝於空中的飛鳥。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",35,12,"他們在那裡、因惡人的驕傲呼求、卻無人答應。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",35,13,"虛妄的呼求、　神必不垂聽、全能者也必不眷顧。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",35,14,"何況你說、你不得見他、你的案件在他面前、你等候他罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",35,15,"但如今因他未曾發怒降罰、也不甚理會狂傲．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",35,16,"所以約伯開口說虛妄的話、多發無知識的言語。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,1,"以利戶又接著說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,2,"你再容我片時、我就指示你．因我還有話為　神說。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,3,"我要將所知道的從遠處引來、將公義歸給造我的主。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,4,"我的言語真不虛謊．有知識全備的與你同在。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,5,"　神有大能、並不藐視人．他的智慧甚廣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,6,"他不保護惡人的性命、卻為困苦人伸冤。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,7,"他時常看顧義人、使他們和君王同坐寶座、永遠要被高舉。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,8,"他們若被鎖鍊捆住、被苦難的繩索纏住．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,9,"他就把他們的作為、和過犯指示他們、叫他們知道有驕傲的行動。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,10,"他也開通他們的耳朵得受教訓、吩咐他們離開罪孽轉回。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,11,"他們若聽從事奉他、就必度日亨通、歷年福樂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,12,"若不聽從、就要被刀殺滅、無知無識而死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,13,"那心中不敬虔的人、積蓄怒氣．　神捆綁他們、他們竟不求救．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,14,"必在青年時死亡、與污穢人一樣喪命。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,15,"　神藉著困苦、救拔困苦人、趁他們受欺壓、開通他們的耳朵。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,16,"　神也必引你出離患難、進入寬闊不狹窄之地．擺在你席上的、必滿有肥甘。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,17,"但你滿口有惡人批評的言語．判斷和刑罰抓住你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,18,"不可容忿怒觸動你、使你不服責罰．也不可因贖價大就偏行。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,19,"你的呼求、〔呼求或作資財〕或是你一切的勢力、果有靈驗、叫你不受患難麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,20,"不要切慕黑夜、就是眾民在本處被除滅的時候。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,21,"你要謹慎、不可重看罪孽、因你選擇罪孽、過於選擇苦難。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,22,"　神行事有高大的能力．教訓人的、有誰像他呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,23,"誰派定他的道路．誰能說、你所行的不義。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,24,"你不可忘記稱讚他所行的為大、就是人所歌頌的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,25,"他所行的萬人都看見、世人也從遠處觀看。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,26,"　神為大、我們不能全知．他的年數不能測度。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,27,"他吸取水點、這水點從雲霧中就變成雨．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,28,"雲彩將雨落下、沛然降與世人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,29,"誰能明白雲彩如何鋪張、和　神行宮的雷聲呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,30,"他將亮光普照在自己的四圍．他又遮覆海底。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,31,"他用這些審判眾民、且賜豐富的糧食。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,32,"他以電光遮手、命閃電擊中敵人。〔或作中了靶子〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",36,33,"所發的雷聲顯明他的作為、又向牲畜指明要起暴風。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,1,"因此我心戰兢、從原處移動。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,2,"聽阿、　神轟轟的聲音、是他口中所發的響聲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,3,"他發響聲震遍天下、發電光閃到地極。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,4,"隨後人聽見有雷聲轟轟、大發威嚴．雷電接連不斷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,5,"　神發出奇妙的雷聲．他行大事、我們不能測透。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,6,"他對雪說、要降在地上．對大雨和暴雨、也是這樣說。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,7,"他封住各人的手、叫所造的萬人、都曉得他的作為。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,8,"百獸進入穴中、臥在洞內。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,9,"暴風出於南宮、寒冷出於北方。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,10,"　神噓氣成冰、寬闊之水也都凝結。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,11,"他使密雲盛滿水氣．布散電光之雲．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,12,"這雲、是藉他的指引、游行旋轉、得以在全地面上行他一切所吩咐的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,13,"或為責罰、或為潤地、或為施行慈愛。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,14,"約伯阿、你要留心聽．要站立思想　神奇妙的作為。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,15,"　神如何吩咐這些、如何使雲中的電光照耀、你知道麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,16,"雲彩如何浮於空中、那知識全備者奇妙的作為、你知道麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,17,"南風使地寂靜、你的衣服就如火熱、你知道麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,18,"你豈能與　神同鋪穹蒼麼．這穹蒼堅硬、如同鑄成的鏡子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,19,"我們愚昧不能陳說、請你指教我們該對他說甚麼話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,20,"人豈可說、我願與他說話、豈有人自願滅亡麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,21,"現在有雲遮蔽、人不得見穹蒼的光亮．但風吹過、天又發晴。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,22,"金光出於北方．在　神那裡有可怕的威嚴。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,23,"論到全能者、我們不能測度．他大有能力、有公平和大義、必不苦待人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",37,24,"所以人敬畏他．凡自以為心中有智慧的人、他都不顧念。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,1,"那時耶和華從旋風中回答約伯說、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,2,"誰用無知的言語、使我的旨意暗昧不明、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,3,"你要如勇士束腰．我問你、你可以指示我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,4,"我立大地根基的時候、你在哪裡呢．你若有聰明只管說罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,5,"你若曉得就說、是誰定地的尺度．是誰把準繩拉在其上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,6,"地的根基安置在何處．地的角石是誰安放的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,7,"那時晨星一同歌唱、　神的眾子也都歡呼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,8,"海水衝出、如出胎胞．那時誰將他關閉呢．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,9,"是我用雲彩當海的衣服、用幽暗當包裹他的布、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,10,"為他定界限、又安門和閂、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,11,"說、你只可到這裡、不可越過、你狂傲的浪要到此止住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,12,"你自生以來、曾命定晨光、使清晨的日光知道本位．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,13,"叫這光普照地的四極、將惡人從其中驅逐出來麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,14,"因這光地面改變如泥上印印．萬物出現如衣服一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,15,"亮光不照惡人、強橫的膀臂也必折斷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,16,"你曾進到海源、或在深淵的隱密處行走麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,17,"死亡的門、曾向你顯露麼．死蔭的門、你曾見過麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,18,"地的廣大、你能明透麼．你若全知道、只管說罷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,19,"光明的居所從何而至、黑暗的本位在於何處。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Job",38,20,"你能帶到本境、能看明其室之路麼。")');
