tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,8,"但願人因耶和華的慈愛、和他向人所行的奇事、都稱讚他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,9,"因他使心裡渴慕的人、得以知足、使心裡飢餓的人、得飽美物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,10,"那些坐在黑暗中死蔭裡的人、被困苦和鐵鍊捆鎖、")');
