tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,14,"他從黑暗中、和死蔭裡、領他們出來、折斷他們的綁索。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,15,"但願人因耶和華的慈愛、和他向人所行的奇事、都稱讚他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,16,"因為他打破了銅門、砍斷了鐵閂。")');
