tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,20,"他發命醫治他們、救他們脫離死亡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,21,"但願人因耶和華的慈愛、和他向人所行的奇事、都稱讚他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,22,"願他們以感謝為祭獻給他、歡呼述說他的作為。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,23,"在海上坐船、在大水中經理事務的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,24,"他們看見耶和華的作為、並他在深水中的奇事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,25,"因他一吩咐、狂風就起來、海中的波浪也揚起．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,26,"他們上到天空、下到海底、他們的心因患難便消化。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,27,"他們搖搖幌幌、東倒西歪、好像醉酒的人．他們的智慧無法可施。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,28,"於是他們在苦難中哀求耶和華、他從他們的禍患中領出他們來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,29,"他使狂風止息、波浪就平靜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,30,"風息浪靜．他們便歡喜．他就引他們到所願去的海口。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,31,"但願人因耶和華的慈愛、和他向人所行的奇事、都稱讚他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,32,"願他們在民的會中尊崇他、在長老的位上讚美他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,33,"他使江河變為曠野、叫水泉變為乾渴之地．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,34,"使肥地變為鹼地．這都因其間居民的罪惡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,35,"他使曠野變為水潭、叫旱地變為水泉。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,36,"他使飢餓的人住在那裡、好建造可住的城邑．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,37,"又種田地、栽葡萄園、得享所出的土產。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,38,"他又賜福給他們、叫他們生養眾多．也不叫他們的牲畜減少。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,39,"他們又因暴虐、患難、愁苦、就減少且卑下。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,40,"他使君王蒙羞被辱、使他們在荒廢無路之地漂流。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,41,"他卻將窮乏人安置在高處、脫離苦難、使他的家屬多如羊群。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,42,"正直人看見、就歡喜．罪孽之輩、必塞口無言。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",107,43,"凡有智慧的、必在這些事上留心．也必思想耶和華的慈愛。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",108,1,"〔大衛的詩歌。〕　神阿、我心堅定．我口〔原文作榮耀〕要唱詩歌頌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",108,2,"琴瑟阿、你們當醒起．我自己要極早醒起。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",108,3,"耶和華阿、我要在萬民中稱謝你、在列邦中歌頌你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",108,4,"因為你的慈愛大過諸天．你的誠實達到穹蒼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",108,5,"　神阿、願你崇高、過於諸天．願你的榮耀、高過全地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",108,6,"求你應允我們、用右手拯救我們、好叫你所親愛的人得救。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",108,7,"　神已經指著他的聖潔說、〔說或作應許我〕我要歡樂．我要分開示劍、丈量疏割谷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",108,8,"基列是我的．瑪拿西是我的．以法蓮是護衛我頭的．猶大是我的杖．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",108,9,"摩押是我的沐浴盆．我要向以東拋鞋．我必因勝非利士呼喊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",108,10,"誰能領我進堅固城．誰能引我到以東地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",108,11,"　神阿、你不是丟棄了我們麼．　神阿、你不和我們的軍兵同去麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",108,12,"求你幫助我們攻擊敵人、因為人的幫助是枉然的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",108,13,"我們倚靠　神、纔得施展大能、因為踐踏我們敵人的就是他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,1,"〔大衛的詩、交與伶長。〕我所讚美的　神阿、求你不要閉口不言．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,2,"因為惡人的嘴、和詭詐人的口、已經張開攻擊我．他們用撒謊的舌頭對我說話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,3,"他們圍繞我、說怨恨的話、又無故地攻打我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,4,"他們與我為敵以報我愛．但我專心祈禱。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,5,"他們向我以惡報善、以恨報愛。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,6,"願你派一個惡人轄制他、派一個對頭站在他右邊。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,7,"他受審判的時候、願他出來擔當罪名．願他的祈禱、反成為罪。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,8,"願他的年日短少．願別人得他的職分。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,9,"願他的兒女為孤兒、他的妻子為寡婦。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,10,"願他的兒女漂流討飯、從他們荒涼之處出來求食。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,11,"願強暴的債主牢籠他一切所有的．願外人搶他勞碌得來的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,12,"願無人向他延綿施恩．願無人可憐他的孤兒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,13,"願他的後人斷絕、名字被塗抹、不傳於下代。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,14,"願他祖宗的罪孽被耶和華記念．願他母親的罪過不被塗抹。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,15,"願這些罪常在耶和華面前、使他的名號斷絕於世．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,16,"因為他不想施恩、卻逼迫困苦窮乏的、和傷心的人、要把他們治死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,17,"他愛咒罵、咒罵就臨到他．他不喜愛福樂、福樂就與他遠離。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,18,"他拿咒罵當衣服穿上．這咒罵就如水進他裡面、像油入他的骨頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,19,"願這咒罵當他遮身的衣服、當他常束的腰帶。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,20,"這就是我對頭、和用惡言議論我的人、從耶和華那裡所受的報應。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,21,"主耶和華阿、求你為你的名恩待我．因你的慈愛美好、求你搭救我．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,22,"因為我困苦窮乏、內心受傷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,23,"我如日影漸漸偏斜而去．我如蝗蟲被抖出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,24,"我因禁食、膝骨軟弱．我身上的肉、也漸漸瘦了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,25,"我受他們的羞辱．他們看見我、便搖頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,26,"耶和華我的　神阿、求你幫助我、照你的慈愛拯救我．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,27,"使他們知道這是你的手、是你耶和華所行的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,28,"任憑他們咒罵．惟願你賜福．他們幾時起來、就必蒙羞．你的僕人卻要歡喜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,29,"願我的對頭、披戴羞辱．願他們以自己的羞愧為外袍遮身。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,30,"我要用口極力稱謝耶和華．我要在眾人中間讚美他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",109,31,"因為他必站在窮乏人的右邊、要救他脫離審判他靈魂的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",110,1,"〔大衛的詩。〕耶和華對我主說、你坐在我的右邊、等我使你仇敵作你的腳凳。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",110,2,"耶和華必使你從錫安伸出能力的杖來．你要在你仇敵中掌權。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",110,3,"當你掌權的日子、〔或作行軍的日子〕你的民要以聖潔的妝飾為衣、〔或作以聖潔為妝飾〕甘心犧牲自己．你的民多如清晨的甘露。〔或作你少年時光耀如清晨的甘露〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",110,4,"耶和華起了誓、決不後悔、說、你是照著麥基洗德的等次、永遠為祭司。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",110,5,"在你右邊的主、當他發怒的日子、必打傷列王。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",110,6,"他要在列邦中刑罰惡人、屍首就遍滿各處．他要在許多國中打破仇敵的頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",110,7,"他要喝路旁的河水．因此必抬起頭來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",111,1,"你們要讚美耶和華。我要在正直人的大會中、並公會中、一心稱謝耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",111,2,"耶和華的作為本為大．凡喜愛的都必考察。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",111,3,"他所行的是尊榮、和威嚴。他的公義存到永遠。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",111,4,"他行了奇事、使人記念．耶和華有恩惠、有憐憫。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",111,5,"他賜糧食給敬畏他的人．他必永遠記念他的約。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",111,6,"他向百姓顯出大能的作為、把外邦的地賜給他們為業。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",111,7,"他手所行的、是誠實公平．他的訓詞都是確實的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",111,8,"是永永遠遠堅定的．是按誠實正直設立的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",111,9,"他向百姓施行救贖、命定他的約、直到永遠．他的名聖而可畏。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",111,10,"敬畏耶和華是智慧的開端．凡遵行他命令的、是聰明人．耶和華是永遠當讚美的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",112,1,"你們要讚美耶和華。敬畏耶和華、甚喜愛他命令的、這人便為有福。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",112,2,"他的後裔在世必強盛．正直人的後代、必要蒙福。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",112,3,"他家中有貨物、有錢財．他的公義、存到永遠。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",112,4,"正直人在黑暗中、有光向他發現．他有恩惠、有憐憫、有公義。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",112,5,"施恩與人、借貸與人的、這人事情順利．他被審判的時候、要訴明自己的冤。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",112,6,"他永不動搖．義人被記念直到永遠。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",112,7,"他必不怕兇惡的信息．他心堅定、倚靠耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",112,8,"他心確定、總不懼怕、直到他看見敵人遭報。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",112,9,"他施捨錢財、賙濟貧窮．他的仁義、存到永遠。他的角必被高舉、大有榮耀。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",112,10,"惡人看見便惱恨、必咬牙而消化．惡人的心願、要歸滅絕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",113,1,"你們要讚美耶和華。耶和華的僕人哪、你們要讚美、讚美耶和華的名。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",113,2,"耶和華的名、是應當稱頌的、從今時直到永遠。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",113,3,"從日出之地、到日落之處、耶和華的名是應當讚美的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",113,4,"耶和華超乎萬民之上．他的榮耀高過諸天。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",113,5,"誰像耶和華我們的　神呢．他坐在至高之處、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",113,6,"自己謙卑、觀看天上地下的事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",113,7,"他從灰塵裡抬舉貧寒人、從糞堆中提拔窮乏人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",113,8,"使他們與王子同坐、就是與本國的王子同坐。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",113,9,"他使不能生育的婦人安居家中、為多子的樂母。你們要讚美耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",114,1,"以色列出了埃及、雅各家離開說異言之民．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",114,2,"那時猶大為主的聖所、以色列為他所治理的國度。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",114,3,"滄海看見就奔逃、約但河也倒流。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",114,4,"大山踴躍如公羊、小山跳舞如羊羔。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",114,5,"滄海阿、你為何奔逃．約但哪你為何倒流．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",114,6,"大山哪、你為何踴躍如公羊．小山哪、你為何跳舞如羊羔。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",114,7,"大地阿、你因見主的面、就是雅各　神的面、便要震動。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",114,8,"他叫磐石變為水池、叫堅石變為泉源。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,1,"耶和華阿、榮耀不要歸與我們、不要歸與我們．要因你的慈愛和誠實歸在你的名下。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,2,"為何容外邦人說、他們的　神在哪裡呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,3,"然而我們的　神在天上．都隨自己的意旨行事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,4,"他們的偶像、是金的銀的、是人手所造的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,5,"有口卻不能言．有眼卻不能看．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,6,"有耳卻不能聽．有鼻卻不能聞．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,7,"有手卻不能摸．有腳卻不能走．有喉嚨也不能出聲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,8,"造他的要和他一樣．凡靠他的也要如此。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,9,"以色列阿、你要倚靠耶和華．他是你的幫助、和你的盾牌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,10,"亞倫家阿、你們要倚靠耶和華．他是你們的幫助、和你們的盾牌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,11,"你們敬畏耶和華的、要倚靠耶和華．他是你們的幫助、和你們的盾牌。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,12,"耶和華向來眷念我們．他還要賜福給我們、要賜福給以色列的家、賜福給亞倫的家。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,13,"凡敬畏耶和華的、無論大小、主必賜福給他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,14,"願耶和華叫你們和你們的子孫、日見加增。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,15,"你們蒙了造天地之耶和華的福。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,16,"天是耶和華的天．地、他卻給了世人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,17,"死人不能讚美耶和華．下到寂靜中的、也都不能．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",115,18,"但我們要稱頌耶和華、從今時直到永遠。你們要讚美耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,1,"我愛耶和華、因為他聽了我的聲音、和我的懇求。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,2,"他既向我側耳、我一生要求告他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,3,"死亡的繩索纏繞我、陰間的痛苦抓住我．我遭遇患難愁苦。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,4,"那時、我便求告耶和華的名、說、耶和華阿、求你救我的靈魂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,5,"耶和華有恩惠、有公義．我們的　神以憐憫為懷。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,6,"耶和華保護愚人．我落到卑微的地步、他救了我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,7,"我的心哪、你要仍歸安樂、因為耶和華用厚恩待你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,8,"主阿、你救我的命、免了死亡、救我的眼、免了流淚、救我的腳、免了跌倒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,9,"我要在耶和華面前、行活人之路。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,10,"我因信、所以如此說話．我受了極大的困苦。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,11,"我曾急促地說、人都是說謊的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,12,"我拿甚麼報答耶和華向我所賜的一切厚恩。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,13,"我要舉起救恩的杯、稱揚耶和華的名。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,14,"我要在他眾民面前向耶和華還我的願。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,15,"在耶和華眼中看聖民之死、極為寶貴。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,16,"耶和華阿、我真是你的僕人．我是你的僕人、是你婢女的兒子．你已經解開我的綁索。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,17,"我要以感謝為祭獻給你、又要求告耶和華的名。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,18,"我要在他眾民面前、在耶和華殿的院內、在耶路撒冷當中、向耶和華還我的願。你們要讚美耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",116,19,"見上節")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",117,1,"萬國阿、你們都當讚美耶和華．萬民哪、你們都當頌讚他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",117,2,"因為他向我們大施慈愛．耶和華的誠實、存到永遠。你們要讚美耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,1,"你們要稱謝耶和華、因他本為善．他的慈愛永遠長存。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,2,"願以色列說、他的慈愛永遠長存。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,3,"願亞倫的家說、他的慈愛永遠長存。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,4,"願敬畏耶和華的說、他的慈愛永遠長存。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,5,"我在急難中求告耶和華、他就應允我、把我安置在寬闊之地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,6,"有耶和華幫助我．我必不懼怕．人能把我怎麼樣呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,7,"在那幫助我的人中、有耶和華幫助我．所以我要看見那恨我的人遭報。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,8,"投靠耶和華、強似倚賴人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,9,"投靠耶和華、強似倚賴王子。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,10,"萬民圍繞我．我靠耶和華的名、必剿滅他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,11,"他們環繞我、圍困我．我靠耶和華的名、必剿滅他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,12,"他們如同蜂子圍繞我、好像燒荊棘的火、必被熄滅．我靠耶和華的名、必剿滅他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,13,"你推我要叫我跌倒、但耶和華幫助了我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,14,"耶和華是我的力量、是我的詩歌．他也成了我的拯救。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,15,"在義人的帳棚裡、有歡呼拯救的聲音．耶和華的右手施展大能。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,16,"耶和華的右手高舉．耶和華的右手施展大能。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,17,"我必不至死、仍要存活、並要傳揚耶和華的作為。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,18,"耶和華雖嚴嚴的懲治我、卻未曾將我交於死亡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,19,"給我敞開義門．我要進去、稱謝耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,20,"這是耶和華的門．義人要進去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,21,"我要稱謝你、因為你已經應允我、又成了我的拯救。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,22,"匠人所棄的石頭、已成了房角的頭塊石頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,23,"這是耶和華所作的、在我們眼中看為希奇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,24,"這是耶和華所定的日子．我們在其中要高興歡喜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,25,"耶和華阿、求你拯救．耶和華阿、求你使我們亨通。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,26,"奉耶和華名來的、是應當稱頌的．我們從耶和華的殿中、為你們祝福。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,27,"耶和華是　神．他光照了我們．理當用繩索把祭牲拴住、牽到壇角那裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,28,"你是我的　神、我要稱謝你．你是我的　神、我要尊崇你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",118,29,"你們要稱謝耶和華、因他本為善．他的慈愛永遠長存。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,1,"行為完全、遵行耶和華律法的、這人便為有福。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,2,"遵守他的法度、一心尋求他的、這人便為有福。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,3,"這人不作非義的事．但遵行他的道。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,4,"耶和華阿、你曾將你的訓詞吩咐我們、為要我們殷勤遵守。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,5,"但願我行事堅定、得以遵守你的律例。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,6,"我看重你的一切命令、就不至於羞愧。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,7,"我學了你公義的判語、就要以正直的心稱謝你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,8,"我必守你的律例．求你總不要丟棄我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,9,"少年人用甚麼潔淨他的行為呢．是要遵行你的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,10,"我一心尋求了你、求你不要叫我偏離你的命令。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,11,"我將你的話藏在心裡、免得我得罪你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,12,"耶和華阿、你是應當稱頌的．求你將你的律例教訓我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,13,"我用嘴唇傳揚你口中的一切典章。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,14,"我喜悅你的法度、如同喜悅一切的財物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,15,"我要默想你的訓詞、看重你的道路。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,16,"我要在你的律例中自樂．我不忘記你的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,17,"求你用厚恩待你的僕人、使我存活．我就遵守你的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,18,"求你開我的眼睛、使我看出你律法中的奇妙。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,19,"我是在地上作寄居的．求你不要向我隱瞞你的命令。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,20,"我時常切慕你的典章、甚至心碎。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,21,"受咒詛偏離你命令的驕傲人、你已經責備他們。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,22,"求你除掉我所受的羞辱和藐視．因我遵守你的法度。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,23,"雖有首領坐著妄論我、你僕人卻思想你的律例。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,24,"你的法度、是我所喜樂的、是我的謀士。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,25,"我的性命幾乎歸於塵土．求你照你的話、將我救活。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,26,"我述說我所行的、你應允了我．求你將你的律例教訓我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,27,"求你使我明白你的訓詞、我就思想你的奇事。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,28,"我的心因愁苦而消化．求你照你的話使我堅立。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,29,"求你使我離開奸詐的道、開恩將你的律法賜給我。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,30,"我揀選了忠信的道、將你的典章擺在我面前。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,31,"我持守你的法度．耶和華阿、求你不要叫我羞愧。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,32,"你開廣我心的時候、我就往你命令的道上直奔。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,33,"耶和華阿、求你將你的律例指教我、我必遵守到底。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,34,"求你賜我悟性、我便遵守你的律法．且要一心遵守。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,35,"求你叫我遵行你的命令．因為這是我所喜樂的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,36,"求你使我的心、趨向你的法度、不趨向非義之財。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,37,"求你叫我轉眼不看虛假、又叫我在你的道中生活。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,38,"你向敬畏你的人所應許的話、求你向僕人堅定。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,39,"求你使我所怕的羞辱遠離我．因你的典章本為美。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,40,"我羨慕你的訓詞．求你使我在你的公義上生活。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,41,"耶和華阿、願你照你的話、使你的慈愛、就是你的救恩、臨到我身上。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,42,"我就有話回答那羞辱我的．因我倚靠你的話。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,43,"求你叫真理的話、總不離開我口．因我仰望你的典章。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,44,"我要常守你的律法、直到永永遠遠。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Psm",119,45,"我要自由而行．〔或作我要行在寬闊之地〕因我素來考究你的訓詞。")');
