tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,4,"不要勞碌求富、休仗自己的聰明。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,5,"你豈要定睛在虛無的錢財上麼．因錢財必長翅膀、如鷹向天飛去。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,6,"不要喫惡眼人的飯、也不要貪他的美味．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,7,"因為他心怎樣思量、他為人就是怎樣．他雖對你說、請喫、請喝、他的心卻與你相背。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,8,"你所喫的那點食物、必吐出來．你所說的甘美言語、也必落空。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,9,"你不要說話給愚昧人聽．因他必藐視你智慧的言語。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,10,"不可挪移古時的地界．也不可侵入孤兒的田地。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,11,"因他們的救贖主、大有能力．他必向你為他們辨屈。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,12,"你要留心領受訓誨。側耳聽從知識的言語。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,13,"不可不管教孩童、你用杖打他、他必不至於死。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,14,"你要用杖打他、就可以救他的靈魂免下陰間。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,15,"我兒、你心若存智慧、我的心也甚歡喜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,16,"你的嘴若說正直話、我的心腸也必快樂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,17,"你心中不要嫉妒罪人．只要終日敬畏耶和華。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,18,"因為至終必有善報．你的指望也不至斷絕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,19,"我兒、你當聽、當存智慧、好在正道上引導你的心。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,20,"好飲酒的、好喫肉的、不要與他們來往。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,21,"因為好酒貪食的、必致貧窮．好睡覺的、必穿破爛衣服。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,22,"你要聽從生你的父親．你母親老了、也不可藐視他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,23,"你當買真理．就是智慧、訓誨、和聰明、也都不可賣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,24,"義人的父親、必大得快樂．人生智慧的兒子、必因他歡喜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,25,"你要使父母歡喜．使生你的快樂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,26,"我兒、要將你的心歸我．你的眼目、也要喜悅我的道路。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,27,"妓女是深坑．外女是窄阱．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,28,"他埋伏好像強盜、他使人中多有奸詐的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,29,"誰有禍患、誰有憂愁、誰有爭鬥、誰有哀歎、〔或作怨言〕誰無故受傷、誰眼目紅赤、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,30,"就是那流連飲酒、常去尋找調和酒的人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,31,"酒發紅、在杯中閃爍、你不可觀看、雖然下咽舒暢、終久是咬你如蛇、刺你如毒蛇。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,32,"見上節")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,33,"你眼必看見異怪的事。〔異怪的事或作淫婦〕你心必發出乖謬的話．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,34,"你必像躺在海中、或像臥在桅杆上．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",23,35,"你必說、人打我我卻未受傷、人鞭打我我竟不覺得、我幾時清醒、我仍去尋酒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,1,"你不要嫉妒惡人、也不要起意與他們相處．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,2,"因為他們的心、圖謀強暴．他們的口、談論奸惡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,3,"房屋因智慧建造、又因聰明立穩．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,4,"其中因知識充滿各樣美好寶貴的財物。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,5,"智慧人大有能力．有知識的人、力上加力。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,6,"你去打仗、要憑智謀．謀士眾多、人便得勝。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,7,"智慧極高、非愚昧人所能及、所以在城門內、不敢開口。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,8,"設計作惡的、必稱為奸人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,9,"愚妄人的思念、乃是罪惡．褻慢者為人所憎惡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,10,"你在患難之日若膽怯、你的力量就微小。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,11,"人被拉到死地、你要解救．人將被殺、你須攔阻。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,12,"你若說、這事我未曾知道．那衡量人心的、豈不明白麼．保守你命的豈不知道麼．他豈不按各人所行、的報應各人麼。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,13,"我兒、你要喫蜜、因為是好的．喫蜂房下滴的蜜、便覺甘甜。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,14,"你心得了智慧、也必覺得如此．你若找著、至終必有善報．你的指望、也不至斷絕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,15,"你這惡人、不要埋伏攻擊義人的家．不要毀壞他安居之所。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,16,"因為義人雖七次跌倒、仍必興起．惡人卻被禍患傾倒。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,17,"你仇敵跌倒、你不要歡喜．他傾倒、你心不要快樂。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,18,"恐怕耶和華看見就不喜悅、將怒氣從仇敵身上轉過來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,19,"不要為作惡的心懷不平．也不要嫉妒惡人．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,20,"因為惡人終不得善報．惡人的燈、也必熄滅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,21,"我兒、你要敬畏耶和華與君王．不要與反覆無常的人結交．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,22,"因為他們的災難、必忽然而起．耶和華與君王所施行的毀滅、誰能知道呢。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,23,"以下也是智慧人的箴言。審判時看人情面、是不好的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,24,"對惡人說、你是義人的、這人萬民必咒詛、列邦必憎惡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,25,"責備惡人的、必得喜悅．美好的福、也必臨到他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,26,"應對正直的、猶如與人親嘴。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,27,"你要在外頭預備工料、在田間辦理整齊、然後建造房屋。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,28,"不可無故作見證、陷害鄰舍．也不可用嘴欺騙人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,29,"不可說、人怎樣待我、我也怎樣待他、我必照他所行的報復他。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,30,"我經過懶惰人的田地、無知人的葡萄園．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,31,"荊棘長滿了地皮、刺草遮蓋了田面、石牆也坍塌了．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,32,"我看見就留心思想、我看著就領了訓誨。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,33,"再睡片時、打盹片時、抱著手躺臥片時、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",24,34,"你的貧窮、就必如強盜速來、你的缺乏、彷彿拿兵器的人來到。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,1,"以下也是所羅門的箴言．是猶大王希西家的人所謄錄的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,2,"將事隱祕、乃　神的榮耀．將事察清、乃君王的榮耀。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,3,"天之高、地之厚、君王之心也測不透。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,4,"除去銀子的渣滓、就有銀子出來、銀匠能以作器皿。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,5,"除去王面前的惡人、國位就靠公義堅立。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,6,"不要在王面前妄自尊大．不要在大人的位上站立。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,7,"寧可有人說請你上來、強如在你覲見的王子面前、叫你退下。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,8,"不要冒失出去與人爭競、免得至終被他羞辱、你就不知道怎樣行了。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,9,"你與鄰舍爭訟、要與他一人辯論．不可洩漏人的密事．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,10,"恐怕聽見的人罵你、你的臭名就難以脫離。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,11,"一句話說得合宜、就如金蘋果在銀網子裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,12,"智慧人的勸戒、在順從的人耳中、好像金耳環、和精金的妝飾。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,13,"忠信的使者、叫差他的人心裡舒暢、就如在收割時、有冰雪的涼氣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,14,"空誇贈送禮物的、好像無雨的風雲。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,15,"恆常忍耐、可以勸動君王．柔和的舌頭、能折斷骨頭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,16,"你得了蜜麼、只可喫夠而已．恐怕你過飽就嘔吐出來。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,17,"你的腳要少進鄰舍的家、恐怕他厭煩你、恨惡你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,18,"作假見證陷害鄰舍的、就是大槌、是利刀、是快箭。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,19,"患難時倚靠不忠誠的人、好像破壞的牙、錯骨縫的腳。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,20,"對傷心的人唱歌、就如冷天脫衣服、又如鹼上倒醋。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,21,"你的仇敵、若餓了就給他飯喫．若渴了就給他水喝。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,22,"因為你這樣行、就是把炭火堆在他的頭上．耶和華也必賞賜你。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,23,"北風生雨、讒謗人的舌頭也生怒容。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,24,"寧可住在房頂的角上、不在寬闊的房屋與爭吵的婦人同住。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,25,"有好消息從遠方來、就如拿涼水給口渴的人喝。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,26,"義人在惡人面前退縮、好像渾之泉、弄濁之井。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,27,"喫蜜過多是不好的．考究自己的榮耀、也是可厭的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",25,28,"人不制伏自己的心、好像毀壞的城邑沒有牆垣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,1,"夏天落雪、收割時下雨、都不相宜．愚昧人得尊榮、也是如此。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,2,"麻雀往來、燕子翻飛、這樣、無故的咒詛、也必不臨到。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,3,"鞭子是為打馬．轡頭是為勒驢．刑杖是為打愚昧人的背。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,4,"不要照愚昧人的愚妄話回答他、恐怕你與他一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,5,"要照愚昧人的愚妄話回答他、免得他自以為有智慧。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,6,"藉愚昧人手寄信的、是砍斷自己的腳、自受損害。〔自受原文作喝〕")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,7,"瘸子的腳、空存無用．箴言在愚昧人的口中也是如此。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,8,"將尊榮給愚昧人的、好像人把石子包在機弦裡。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,9,"箴言在愚昧人的口中、好像荊棘刺入醉漢的手。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,10,"雇愚昧人的、與雇過路人的、就像射傷眾人的弓箭手。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,11,"愚昧人行愚妄事、行了又行、就如狗轉過來喫牠所吐的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,12,"你見自以為有智慧的人麼．愚昧人比他更有指望。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,13,"懶惰人說、道上有猛獅、街上有壯獅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,14,"門在樞紐轉動、懶惰人在床上也是如此。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,15,"懶惰人放手在盤子裡、就是向口撤回、也以為勞乏。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,16,"懶惰人看自己、比七個善於應對的人更有智慧。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,17,"過路被事激動、管理不干己的爭競、好像人揪住狗耳。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,18,"人欺凌鄰舍、卻說、我豈不是戲耍嗎。他就像瘋狂的人拋擲火把、利箭、與殺人的兵器〔殺人的兵器原文作死亡〕。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,19,"見上節")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,20,"火缺了柴、就必熄滅．無人傳舌、爭競便止息。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,21,"好爭競的人煽惑爭端、就如餘火加炭、火上加柴一樣。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,22,"傳舌人的言語、如同美食、深入人的心腹。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,23,"火熱的嘴、奸惡的心、好像銀渣包的瓦器。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,24,"怨恨人的用嘴粉飾、心裡卻藏著詭詐．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,25,"他用甜言蜜語、你不可信他、因為他心中有七樣可憎惡的．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,26,"他雖用詭詐遮掩自己的怨恨、他的邪惡必在會中顯露。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",26,27,"挖陷坑的、自己必掉在其中．滾石頭的、石頭必反滾在他身上。")');
