tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,8,"你當為啞吧〔或作不能自辨的〕開口．為一切孤獨的伸冤。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,9,"你當開口按公義判斷、為困苦和窮乏的辨屈。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,10,"才德的婦人誰能得著呢．他的價值遠勝過珍珠．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,11,"他丈夫心裡倚靠他、必不缺少利益．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,12,"他一生使丈夫有益無損．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,13,"他尋找羊羢和麻、甘心用手作工．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,14,"他好像商船從遠方運糧來．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,15,"未到黎明他就起來、把食物分給家中的人．將當作的工分派婢女。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,16,"他想得田地、就買來．用手所得之利、栽種葡萄園．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,17,"他以能力束腰、使膀臂有力．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,18,"他覺得所經營的有利他的燈終夜不滅。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,19,"他手拿撚線竿．手把紡線車．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,20,"他張手賙濟困苦人．伸手幫補窮乏人。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,21,"他不因下雪為家裡的人擔心、因為全家都穿著朱紅衣服．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,22,"他為自己製作繡花毯子．他的衣服、是細麻和紫色布作的。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,23,"他丈夫在城門口與本地的長老同坐、為眾人所認識。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,24,"他作細麻布衣裳出賣．又將腰帶賣與商家。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,25,"能力和威儀、是他的衣服．他想到日後的景況就喜笑。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,26,"他開口就發智慧．他舌上有仁慈的法則．")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,27,"他觀察家務、並不喫閒飯。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,28,"他的兒女起來稱他有福．他的丈夫也稱讚他、")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,29,"說、才德的女子很多、惟獨你超過一切。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,30,"豔麗是虛假的．美容是虛浮的．惟敬畏耶和華的婦女、必得稱讚。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Pro",31,31,"願他享受操作所得的．願他的工作、在城門口榮耀他。")');
