tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Luk",3,13,"約翰說、除了例定的數目、不要多取。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Luk",3,14,"又有兵丁問他說、我們當作甚麼呢。約翰說、不要以強暴待人、也不要訛詐人、自己有錢糧就當知足。")');
tx.executeSql('INSERT INTO bible ( writer , ch , node, text) VALUES ( "Luk",3,15,"百姓指望基督來的時候、人都心裡猜疑、或者約翰是基督。")');
